mapper.insert:
/* setApproveDocOpt */ INSERT INTO DJ_CAMTIC.A_DOC_OPT ( DOC_ID, PUBLIC_TYPE, PUBLIC_REASON_TEXT, PUBLIC_REASON, PRIVATE_REASON, URGENT_TYPE, SECURITY_TYPE, DOC_GBN, REG_EMP_SEQ ) VALUES (
#{DOC_ID}, #{docId},
#{publicType}, #{publicReasonText}, #{publicReason}, #{privateReason}, #{urgentType}, #{securityType}, #{docGbn}, #{empSeq} )

mapper.select:
/* getDocInfo */ SELECT DI.DOC_ID, DI.AIKEYCODE, DI.DOC_MENU_CD, F.FORM_ID, F.FORM_NAME, DI.APPRO_KEY, (NULL) AS AITITLE, DI.DOC_NO, DI.DOC_TITLE, DI.DOC_CONTENT, DI.DRAFT_EMP_SEQ, DI.LAST_APPROVE_EMP_SEQ, DI.LAST_APPROVE_TYPE, DI.DRAFT_DT, DATE_FORMAT(DI.DRAFT_DT, '%Y-%m-%d') AS DRAFT_DATE, DI.APPROVE_STAT_CODE, DO.DOC_OPT_ID, IFNULL(DO.PUBLIC_TYPE, '001') AS PUBLIC_TYPE, (SELECT CM_CODE_NM FROM DJ_CAMTIC.V_COM_CODE WHERE CM_GROUP_CODE_ID = 27 AND CM_CODE = DO.PUBLIC_TYPE) AS PUBLIC_TYPE_KR, REPLACE(REPLACE(DO.PUBLIC_REASON_TEXT, '\n', ''), '\r', '') AS PUBLIC_REASON_TEXT, DO.PUBLIC_REASON, DO.PRIVATE_REASON, IFNULL(DO.URGENT_TYPE, '000') AS URGENT_TYPE, (SELECT CM_CODE_NM FROM DJ_CAMTIC.V_COM_CODE WHERE CM_GROUP_CODE_ID = 28 AND CM_CODE = DO.URGENT_TYPE) AS URGENT_TYPE_KR, IFNULL(DO.SECURITY_TYPE, '000') AS SECURITY_TYPE, (SELECT CM_CODE_NM FROM DJ_CAMTIC.V_COM_CODE WHERE CM_GROUP_CODE_ID = 29 AND CM_CODE = DO.SECURITY_TYPE) AS SECURITY_TYPE_KR, IFNULL(DO.DOC_GBN, '000') AS DOC_GBN, (SELECT CM_CODE_NM FROM DJ_CAMTIC.V_COM_CODE WHERE CM_GROUP_CODE_ID = 30 AND CM_CODE = DO.DOC_GBN) AS DOC_GBN_KR, DI.ATFILE_SN, (SELECT FILE_NO FROM CAM_COMMON.dj_FILE_INFO WHERE DOC_ID = DI.DOC_ID AND FILE_EXT = 'html') AS HTMLAT_FILE_SN, FI.FILE_CD, FI.FILE_PATH, FI.FILE_UUID, FI.FILE_ORG_NAME, FI.FILE_EXT, CONCAT(FI.FILE_PATH, FI.FILE_UUID) AS FILE_DOWN_PATH, (SELECT COUNT(*) FROM CAM_COMMON.dj_FILE_INFO WHERE DOC_ID = DI.DOC_ID AND DI.ATFILE_SN != FILE_NO AND FILE_EXT != 'html') as FILE_CONT, (SELECT COUNT(*) FROM DJ_CAMTIC.A_DOC_REFERENCES WHERE DOC_ID = DI.DOC_ID) as REFERENCES_CONT FROM DJ_CAMTIC.A_DOC_INFO DI JOIN DJ_CAMTIC.A_FORM F ON DI.FORM_ID = F.FORM_ID LEFT JOIN CAM_COMMON.dj_FILE_INFO FI ON DI.ATFILE_SN = FI.FILE_NO LEFT JOIN DJ_CAMTIC.A_DOC_OPT DO ON DI.DOC_ID = DO.DOC_ID WHERE DI.ACTIVE = 'Y'
AND DI.DOC_ID = #{DOC_ID}
AND DI.DOC_ID = #{docId}

/* getApproveRouteId */
SELECT
APPROVE_ROUTE_ID
FROM
DJ_CAMTIC.A_DOC_APPROVE_ROUTE
WHERE
APPROVE_EMP_SEQ = #{empSeq}


        AND DOC_ID = #{DOC_ID}


        AND DOC_ID = #{docId}

/* getDocAttachmentList */ SELECT CONCAT(DI.DOC_NO, '_', DI.DOC_TITLE) AS ZIP_NAME, FI.FILE_NO, CONCAT(FI.FILE_ORG_NAME, '.', FI.FILE_EXT) AS filename, FI.FILE_EXT, FI.FILE_SIZE, FI.FILE_UUID AS fileUUID, FI.FILE_PATH AS FILE_DOWN_PATH -- CONCAT('/upload/', FI.FILE_CD, '/', DATE_FORMAT(FI.REG_DT, '%Y/%m/%d'), '/') AS FILE_DOWN_PATH FROM CAM_COMMON.dj_FILE_INFO FI LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON FI.DOC_ID = DI.DOC_ID AND DI.ATFILE_SN != FI.FILE_NO AND FI.FILE_EXT != 'html' WHERE DI.DOC_ID = #{docId}
OR (CONTENT_ID = #{payAppSn} AND file_cd = #{type}) OR (FILE_NO IN (SELECT FILE_NO FROM CAM_MNG.DJ_PAY_APP_DET WHERE PAY_APP_SN = #{payAppSn}))
OR CONTENT_ID = #{payAppSn} AND file_cd = 'payApp' OR (FILE_NO IN (SELECT FILE_NO FROM CAM_MNG.DJ_PAY_APP_DET WHERE PAY_APP_SN = #{payAppSn}))
OR (CONTENT_ID = #{payAppSn} AND FILE_CD = 'useCard' AND FILE_PATH = #{filePath})

/* getDocApproveAllRoute */ SELECT DI.DRAFT_EMP_SEQ, DI.LAST_APPROVE_EMP_SEQ, DI.LAST_APPROVE_EMP_NAME, DAR.APPROVE_ROUTE_ID, DAR.APPROVE_EMP_SEQ, DAR.APPROVE_EMP_NAME, DAR.APPROVE_DEPT_SEQ, DAR.APPROVE_DEPT_NAME, DAR.APPROVE_POSITION_NAME, DAR.APPROVE_DUTY_NAME, DAR.APPROVE_STAT_CODE, DAR.APPROVE_STAT_CODE_DESC, DAR.APPROVE_TYPE, DAR.PROXY_APPROVE_EMP_SEQ, CASE WHEN DAR.PROXY_APPROVE_EMP_SEQ IS NOT NULL THEN CONCAT('원결재 : [', DAR.APPROVE_POSITION_NAME, '] ', DAR.APPROVE_EMP_NAME) ELSE '' END ORIGIN_APPROVE_EMP_NAME, DATE_FORMAT(DAR.DOC_READ_DT, '%Y-%m-%d %H:%s') AS DOC_READ_DT, DATE_FORMAT(DAR.APPROVE_DT, '%Y. %m. %d') AS APPROVE_DT, DATE_FORMAT(DAR.APPROVE_DT, '%Y-%m-%d %H:%s') AS APPROVE_DT2, DAR.APPROVE_ORDER FROM DJ_CAMTIC.A_DOC_INFO DI JOIN DJ_CAMTIC.A_DOC_APPROVE_ROUTE DAR ON DAR.DOC_ID = DI.DOC_ID
AND (DAR.APPROVE_STAT_CODE != 111 OR DAR.APPROVE_STAT_CODE IS NULL)
WHERE DI.DOC_ID = #{docId} ORDER BY DAR.APPROVE_ORDER ASC

