PROXY_APPROVE_DEPT_SEQ = #{proxyApproveDeptSeq}, PROXY_APPROVE_EMP_SEQ = #{proxyApproveEmpSeq}, DOC_READ_DT = CASE WHEN DOC_READ_DT IS NULL THEN NOW() ELSE DOC_READ_DT END,
APPROVE_STAT_CODE_DESC = #{approveStatCodeDesc}, APPROVE_STAT_CODE = #{approveStatCode}, APPROVE_OPIN = #{approveOpin}, APPROVE_DT = NOW(), APPROVE_CANCEL_DT = NULL,
MOD_EMP_SEQ = #{proxyApproveEmpSeq},
MOD_EMP_SEQ = #{approveEmpSeq},
MOD_DATE = NOW() WHERE DOC_ID = #{docId} AND APPROVE_ROUTE_ID = #{approveRouteId}
