mapper.select:
        /* getAssetList */
        SELECT
            AI.*,
            AP.AST_PLACE_NAME,
            IC.INSIDE_DT_CODE_NM,
            IFNULL((SELECT CRM_NM FROM CAM_CRM.DJ_CRM_INFO WHERE AI.PURC_COMPANY_ID = CRM_SN), '-') AS PURC_COMPANY_NAME
        FROM
            CAM_INSIDE.DJ_ASSET_INFO AI
        LEFT JOIN
            CAM_INSIDE.DJ_ASSET_PLACE AP ON AI.AST_PLACE_SN = AP.AST_PLACE_SN
        LEFT JOIN
            CAM_INSIDE.DJ_INSIDE_CODE IC ON AI.AST_STS_CODE = IC.INSIDE_DT_CODE AND IC.INSIDE_MD_CODE = '03'
        WHERE
            AI.ACTIVE = 'Y'



		AND
		    DATE_FORMAT(#{startDate}, '%Y%m%d') <= DATE_FORMAT(AI.PURC_DATE, '%Y%m%d')
		AND
		    DATE_FORMAT(#{endDate}, '%Y%m%d') >= DATE_FORMAT(AI.PURC_DATE, '%Y%m%d')




            AND AI.AST_CODE_COMPANY_ID = #{assetPosition}


            AND AI.AST_TYPE_CODE = #{assetType}


            AND AI.AST_CODE_ID_1 = #{categoryA}


            AND AI.AST_CODE_ID_2 = #{categoryB}


            AND AI.AST_CODE_ID_3 = #{categoryC}


            AND AI.AST_STS_CODE = #{assetStatus}


            AND AI.AST_PLACE_SN = #{assetPlace}


            AND AI.REG_STATUS = #{regStatus}


            AND AI.BARCODE_TYPE = #{barcodeType}




                AND AI.AST_NAME LIKE CONCAT ('%', #{searchContent},'%')  
                AND AI.AST_NO LIKE CONCAT ('%', #{searchContent},'%')
                AND AI.MODEL_SIZE LIKE CONCAT ('%', #{searchContent},'%')
                AND AI.MODEL_NAME LIKE CONCAT ('%', #{searchContent},'%')
                AND AI.EMP_NAME LIKE CONCAT ('%', #{searchContent},'%')


        ORDER BY PURC_DATE DESC, AST_INFO_SN DESC

