AND RPR_CLASS = #{rprClassNotStatus}
AND RES_YN = #{resYn}
AND III.REG_EMP_NAME like CONCAT('%', #{searchText}, '%')
AND III.SHARE_NAME like CONCAT('%', #{searchText}, '%')
AND III.TITLE like CONCAT('%', #{searchText}, '%')
AND III.RPR_NAME = #{rprClass}
AND III.IPR_CLASS = #{iprClass}
AND DATE_FORMAT(III.APPLICANT_DT, '%Y-%m-%d') >= DATE_FORMAT(#{stDt}, '%Y-%m-%d') AND DATE_FORMAT(III.APPLICANT_DT, '%Y-%m-%d') <= DATE_FORMAT(#{enDt}, '%Y-%m-%d')
AND DATE_FORMAT(III.EXPIRATION_DT, '%Y-%m-%d') >= DATE_FORMAT(#{stDt}, '%Y-%m-%d') AND DATE_FORMAT(III.EXPIRATION_DT, '%Y-%m-%d') <= DATE_FORMAT(#{enDt}, '%Y-%m-%d')
