/* getEquipApprovalData */ SELECT B.EQIPMN_GBN_NAME, B.EQIPMN_NAME, B.REGTR_NAME, (SELECT IFNULL(SUM(SA.USE_TIME), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = (SELECT DATE_FORMAT(DATE_SUB(CONCAT(#{searchDe}, '-01'), INTERVAL 1 MONTH), '%Y-%m')) AND SA.ACTVT_YN = 'Y') AS BEF_USE_TIME, (SELECT IFNULL(SUM(SA.USE_TIME), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS USE_TIME, (SELECT IFNULL(COUNT(SA.EQIPMN_USE_SN), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS EP_COUNT, (SELECT IFNULL(COUNT(CASE WHEN SA.PRTPCO_GBN_NAME IS NOT NULL THEN 1 END), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS PRTPCO_GBN_NAME, (SELECT IFNULL(COUNT(CASE WHEN SA.PRTPCO_GBN_SN = 4 THEN 1 END), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS PRTPCO_GBN_SN_A, (SELECT IFNULL(COUNT(CASE WHEN SA.PRTPCO_GBN_SN = 5 THEN 1 END), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS PRTPCO_GBN_SN_B, (SELECT IFNULL(COUNT(CASE WHEN SA.PRTPCO_GBN_SN = 6 THEN 1 END), 0) FROM CAM_INSIDE.DJ_EQIPMN_USE SA WHERE SA.EQIPMN_MST_SN = B.EQIPMN_MST_SN AND DATE_FORMAT(SA.USE_PD_STR_DE, '%Y-%m') = #{searchDe} AND SA.ACTVT_YN = 'Y') AS PRTPCO_GBN_SN_C, ROW_NUMBER()OVER(ORDER BY B.EQIPMN_GBN_NAME) AS ROW_NUM, COUNT(*)OVER(PARTITION BY B.EQIPMN_GBN_NAME DESC) AS PART_CNT FROM CAM_INSIDE.DJ_EQIPMN_MST B WHERE 1=1
AND DATE_FORMAT(B.REG_DE, '%Y-%m') < #{searchDe}
AND B.ACTVT_YN = 'Y' GROUP BY B.EQIPMN_GBN_NAME, B.EQIPMN_NAME ORDER BY B.EQIPMN_GBN_CMMN_CD_SN, B.EQIPMN_NAME

