)

mapper.select:
/*getEqipmnRegList*/ SELECT eqipmn_mst_sn as EQIPMN_MST_SN, eqipmn_gbn_name AS EQIPMN_GBN_NAME, eqipmn_name AS EQIPMN_NAME, regtr_name AS REGTR_NAME, reg_de AS REG_DE, actvt_yn as ACTVT_YN, sort_sn as SORT_SN FROM CAM_INSIDE.DJ_EQIPMN_MST WHERE 1=1
AND eqipmn_gbn_cmmn_cd_sn = #{eqipmnGbnCmmnCdSn}
AND EQIPMN_NAME LIKE CONCAT('%', #{eqipmnName}, '%')
AND ACTVT_YN = 'Y' ORDER BY SORT_SN ASC

