mapper.update:
/* setCategoryCodeUpd */ UPDATE CAM_INSIDE.DJ_ASSET_CODE SET
AST_CODE = #{astCode},
AST_CODE_NM = #{astCodeNm}, AST_DEPRE_TYPE = #{astCodeNm}, AST_DEPRE_PERCENT = #{astDeprePercent}, AST_USE_YEAR = #{astUseYear}, MOD_EMP_SEQ = #{empSeq}, MOD_DATE = NOW() WHERE AST_CODE_ID = #{astCodeId}

mapper.select:
/*getEqipmnUseList*/ SELECT A.eqipmn_use_sn as EQIPMN_USE_SN, A.sort_sn as SORT_SN, B.eqipmn_gbn_name as EQIPMN_GBN_NAME, B.eqipmn_gbn_cmmn_cd_sn as EQIPMN_GBN_CMMN_CD_SN, B.eqipmn_name as EQIPMN_NAME, A.use_pd_str_de as USE_PD_STR_DE, A.use_pd_end_de as USE_PD_END_DE, A.user_name as USER_NAME, A.oper_cn as OPER_CN, A.use_time as USE_TIME, A.use_amt as USE_AMT, A.client_prtpco_name as CLIENT_PRTPCO_NAME, A.prtpco_gbn_name as PRTPCO_GBN_NAME, A.prtpco_gbn_sn as PRTPCO_GBN_SN, A.actvt_yn AS ACTVT_YN FROM CAM_INSIDE.DJ_EQIPMN_USE A LEFT JOIN CAM_INSIDE.DJ_EQIPMN_MST B ON A.eqipmn_mst_sn = B.eqipmn_mst_sn WHERE 1=1
AND ( USE_PD_STR_DE >= #{usePdStrDe} AND USE_PD_END_DE <= #{usePdEndDe} )
AND eqipmn_gbn_cmmn_cd_sn = #{eqipmnGbnCmmnCdSn}
AND prtpco_gbn_sn = #{prtpcoGbnSn}
AND A.ACTVT_YN = 'Y'
AND ${searchText} LIKE CONCAT ('%', #{searchVal}, '%')
ORDER BY SORT_SN ASC

