mapper.select:
/*getRndSaleAmt*/ SELECT IFNULL(SUM(B.TOT_COST), 0) AS TOT_COST FROM CAM_MNG.DJ_EXNP A LEFT JOIN CAM_MNG.DJ_EXNP_DET B ON A.EXNP_SN = B.EXNP_SN LEFT JOIN CAM_PJT_MNG.DJ_PROJECT P ON A.PJT_CD = P.PJT_CD WHERE (substr(A.PJT_CD, 1, 1) = 'R' OR substr(A.PJT_CD, 1, 1) = 'S') AND substr(P.STR_DT, 1, 4) = #{year}
AND P.DEPT_SEQ = #{deptSeq}
AND DOC_STATUS IN (100, 101) AND B.BUDGET_SN IS NOT NULL

/*getPurcRndAmt*/
SELECT IFNULL(SUM(SUBSTRING_INDEX(PURC_ITEM_AMT_SUM, '.', 1)), 0) AS PURC_EXNP_AMT
FROM
    (SELECT
         IFNULL((SELECT SUM(ITEM_AMT) FROM CAM_MNG.DJ_PURC_CLAIM_ITEM WHERE CLAIM_SN = A.CLAIM_SN), 0) AS PURC_ITEM_AMT_SUM
    FROM
        CAM_MNG.DJ_PURC_CLAIM A
WHERE
    (SELECT LEFT(PJT_CD, 1) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) in ('R', 'S')
AND
    (SELECT LEFT(STR_DT, 4) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{year}

        AND (SELECT DEPT_SEQ FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{deptSeq}

) RE

/*getPurcEngnAmt*/
SELECT IFNULL(SUM(SUBSTRING_INDEX(PURC_ITEM_AMT_SUM, '.', 1)), 0) AS PURC_EXNP_AMT
FROM
    (SELECT
         IFNULL((SELECT SUM(ITEM_AMT) FROM CAM_MNG.DJ_PURC_CLAIM_ITEM WHERE CLAIM_SN = A.CLAIM_SN), 0) AS PURC_ITEM_AMT_SUM
     FROM
         CAM_MNG.DJ_PURC_CLAIM A
     WHERE
         (SELECT LEFT(PJT_CD, 1) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) in ('D', 'V')
    AND
        (SELECT LEFT(STR_DT, 4) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{year}

        AND (SELECT DEPT_SEQ FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{deptSeq}

    ) RE

/*getBustripRndAmt*/ SELECT IFNULL(SUM(SUBSTRING_INDEX(RES_EXNP_SUM, '.', 1)), 0) AS BUSTRIP_EXNP_AMT FROM (SELECT IFNULL(( SELECT SUM(REPLACE(TOT_COST, ',', '')) AS BUSTRIP_EXNP_SUM FROM CAM_INSIDE.DJ_HR_BIZ_EXNP BE WHERE BE.HR_BIZ_REQ_RESULT_ID = B.HR_BIZ_REQ_RESULT_ID ), 0) AS RES_EXNP_SUM FROM CAM_INSIDE.DJ_HR_BIZ_REQ A LEFT JOIN CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT B ON A.HR_BIZ_REQ_ID = B.HR_BIZ_REQ_ID WHERE (SELECT LEFT(PJT_CD, 1) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) in ('R', 'S') AND (SELECT LEFT(STR_DT, 4) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{year}
AND (SELECT DEPT_SEQ FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{deptSeq}
) RE

/*getBustripEngnAmt*/ SELECT IFNULL(SUM(SUBSTRING_INDEX(RES_EXNP_SUM, '.', 1)), 0) AS BUSTRIP_EXNP_AMT FROM (SELECT IFNULL(( SELECT SUM(REPLACE(TOT_COST, ',', '')) AS BUSTRIP_EXNP_SUM FROM CAM_INSIDE.DJ_HR_BIZ_EXNP BE WHERE BE.HR_BIZ_REQ_RESULT_ID = B.HR_BIZ_REQ_RESULT_ID ), 0) AS RES_EXNP_SUM FROM CAM_INSIDE.DJ_HR_BIZ_REQ A LEFT JOIN CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT B ON A.HR_BIZ_REQ_ID = B.HR_BIZ_REQ_ID WHERE (SELECT LEFT(PJT_CD, 1) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) in ('D', 'V') AND (SELECT LEFT(STR_DT, 4) FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{year}
AND (SELECT DEPT_SEQ FROM CAM_PJT_MNG.DJ_PROJECT WHERE PJT_SN = A.PJT_SN) = #{deptSeq}
) RE

/*getExnpRndAmt*/ SELECT IFNULL(SUM(TOT_COST), 0) AS EXNP_AMT FROM (SELECT IFNULL(TOT_COST, 0) AS TOT_COST FROM CAM_MNG.DJ_PAY_APP_DET PAD LEFT JOIN CAM_MNG.DJ_PAY_APP PA ON PAD.PAY_APP_SN = PA.PAY_APP_SN LEFT JOIN CAM_PJT_MNG.DJ_PROJECT D ON PA.PJT_CD = D.PJT_CD WHERE LEFT(PA.PJT_CD, 1) IN ('R', 'S') AND LEFT(D.STR_DT, 4) = #{year} AND PAD.COST_STAT = 'Y'
AND D.DEPT_SEQ = #{deptSeq}
) RE

mapper.update:
/*updProjectPaySet*/
UPDATE CAM_PJT_MNG.DJ_PJT_PAY_SET
SET
    BEF_EXP_SALE_AMT = #{befExpSaleAmt},
    BEF_EXP_PROFIT_AMT = #{befExpProfitAmt},
    AFT_SALE_AMT = #{aftSaleAmt},
    AFT_PROFIT_AMT = #{aftProfitAmt},
    MOD_EMP_SEQ = #{regEmpSeq}
WHERE
    PJT_SN = #{pjtSn}

mapper.select:
/*getDeptObjAmt*/
SELECT
    IFNULL(SUM(DELV_OBJ),0) AS DELV_OBJ,
    IFNULL(SUM(SALE_OBJ),0) AS SALE_OBJ,
    IFNULL(SUM(INCP_OBJ),0) AS INCP_OBJ
FROM
    CAM_PJT_MNG.DJ_DEPT_OBJ_SETTING
WHERE BASE_YEAR = #{year}
  AND DEPT_LEVEL = #{deptLevel}

    AND DEPT_SEQ = #{deptSeq}
