mapper.insert:
/* insGroupCode */
INSERT INTO CAM_ITEM.DJ_ITEM_CD
(
    GRP_SN,
    GRP_NM
)
VALUES
    (
        #{grpSn},
        #{grpNm}
    )

mapper.select:
/* selLgCode */ SELECT * FROM CAM_ITEM.DJ_ITEM_CD WHERE GRP_SN = #{grpSn}
AND LG_CD in(${lgCdArr})
AND LG_CD is not null
AND ITEM_CD is null

/* selSmCode */ SELECT * FROM CAM_ITEM.DJ_ITEM_CD WHERE GRP_SN = #{grpSn} AND LG_CD is not null
AND LG_CD in(${lgCdArr})
AND LG_CD = #{lgCd}
AND ITEM_CD is not null

/* getItemMasterList */ SELECT IM.*, WHC.ITEM_CD_NM AS WH_CD_NM, IUC.ITEM_CD_NM AS ITEM_UNIT_NM FROM CAM_ITEM.DJ_ITEM_MASTER IM JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND IM.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD IUC ON IUC.LG_CD = 'UNIT' AND IM.ITEM_UNIT_CD = IUC.ITEM_CD WHERE 1=1
AND IM.ACTIVE = #{active}
AND IM.ITEM_UNIT_CD = #{itemUnitCd}
AND IM.WH_CD = #{whCd}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR IM.SAFETY_INVEN LIKE CONCAT('%', #{searchValue}, '%') )
