/* getItemStandardUnitPriceList */ SELECT IM.*, MSU.MASTER_SD_UP_SN, MSU.UNIT_PRICE AS MSU_UNIT_PRICE, MSU.B2B_PRICE AS MSU_B2B_PRICE, MSU.START_DT, MSU.END_DT, MAX(MSU.CHANGE_NUM) AS CHANGE_NUM, ITC.ITEM_CD_NM AS ITEM_TYPE_NM, WHC.ITEM_CD_NM AS WH_CD_NM, IUC.ITEM_CD_NM AS ITEM_UNIT_NM, (SELECT REG_EMP_NAME FROM CAM_ITEM.DJ_ITEM_MASTER_SD_UP WHERE MASTER_SN = IM.MASTER_SN AND CHANGE_NUM = 1) AS REG_EMP_NAME, (SELECT REG_EMP_NAME FROM CAM_ITEM.DJ_ITEM_MASTER_SD_UP WHERE MASTER_SN = IM.MASTER_SN AND CHANGE_NUM != 1 ORDER BY CHANGE_NUM DESC LIMIT 1) AS MOD_EMP_NAME FROM CAM_ITEM.DJ_ITEM_MASTER IM JOIN CAM_ITEM.DJ_ITEM_CD ITC ON ITC.LG_CD = 'IT' AND IM.ITEM_TYPE = ITC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND IM.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD IUC ON IUC.LG_CD = 'UNIT' AND IM.ITEM_UNIT_CD = IUC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_MASTER_SD_UP MSU ON MSU.MASTER_SN = IM.MASTER_SN
WHERE 1=1
AND IM.MASTER_SN = #{masterSn}
AND IM.ITEM_TYPE = #{itemType}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR IM.STANDARD LIKE CONCAT('%', #{searchValue}, '%') OR IM.SAFETY_INVEN LIKE CONCAT('%', #{searchValue}, '%') )
AND IM.ACTIVE = 'Y'
GROUP BY MASTER_SN ORDER BY MASTER_SD_UP_SN DESC

/* getObtainOrderMaster */ SELECT OO.*, EI.EMP_NAME_KR, IM.ITEM_NO, IM.ITEM_NAME, IUC.ITEM_CD_NM AS UNIT, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS OVERALL_INVEN, CI.CRM_SN, CI.CRM_NM, PD.APPR_STAT, IFNULL(PD.PAY_INCP_SN, 0) AS PAY_INCP_SN, CASE WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) = PI.TOT_COST THEN '입금완료' WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != PI.TOT_COST THEN '부분입금' ELSE '미입금' END AS DOC_STATUS_TEXT, IFNULL( (SELECT SUM(SS.AMT) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) AS TOT_AMT, IFNULL(PI.RE_TOT_COST, 0) AS COM_AMT, IFNULL( (SELECT SUM(SS.ORDER_REMAIN) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) AS TOTAL_ODER_REMAIN, IFNULL( (SELECT COUNT(SS.OBTAIN_ORDER_TYPE) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) AS TOTAL_OBTAIN_ORDER_TYPE_Y, IFNULL( (SELECT COUNT(SS.OBTAIN_ORDER_TYPE) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'N' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) AS TOTAL_OBTAIN_ORDER_TYPE_N, (SELECT MAX(SS.DUE_DT) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ) AS MAX_DUE_DT FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO JOIN CAM_CRM.DJ_CRM_INFO CI ON OO.CRM_SN = CI.CRM_SN JOIN CAM_HR.DJ_EMP_INFO EI ON OO.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = OO.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD IUC ON IUC.LG_CD = 'UNIT' AND IM.ITEM_UNIT_CD = IUC.ITEM_CD LEFT JOIN CAM_MNG.DJ_PAY_DEPO PD ON PD.PAY_DEPO_SN = OO.PAY_DEPO_SN LEFT JOIN ( SELECT PI.PAY_INCP_SN, PI.DOC_STATUS, E.RE_CNT, E.RE_TOT_COST, C.TOT_COST FROM CAM_MNG.DJ_PAY_INCP PI LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS TOT_COST FROM CAM_MNG.DJ_PAY_INCP_DET GROUP BY PAY_INCP_SN )C ON PI.PAY_INCP_SN = C.PAY_INCP_SN LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS RE_TOT_COST, COUNT(PAY_INCP_RE_SN) AS RE_CNT FROM CAM_MNG.DJ_PAY_INCP_RE WHERE RE_STAT = 'Y' GROUP BY PAY_INCP_SN ) E ON PI.PAY_INCP_SN = E.PAY_INCP_SN )PI ON PD.PAY_INCP_SN = PI.PAY_INCP_SN WHERE 1=1
AND OO.OBTAIN_ORDER_TYPE = #{obtainOrderType}
AND OO.DEADLINE = #{deadLine}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d')
AND OO.CRM_SN = #{crmSn}
AND IM.MASTER_SN = #{masterSn}
AND OO.OBTAIN_ORDER_SN IN(${ooArr})
AND OO.DEADLINE = #{deadLine}
AND OO.UNPAID_TYPE = #{unpaidType}
AND IFNULL( (SELECT SUM(SS.ORDER_REMAIN) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) = '0'
AND IFNULL( (SELECT SUM(SS.ORDER_REMAIN) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) != '0'
AND CI.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( CI.CRM_NM LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND OO.REG_EMP_SEQ = #{regEmpSeq}
AND IM.ITEM_NO LIKE CONCAT(#{category}, '%')
GROUP BY OO.REG_EMP_SEQ, DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i') ORDER BY CASE WHEN OO.MOD_DT IS NULL THEN OO.REG_DT ELSE OO.MOD_DT END DESC

/* getObtainOrderList */ SELECT OO.*, EI.EMP_NAME_KR, IM.ITEM_NO, IM.ITEM_NAME, IM.STANDARD, IUC.ITEM_CD_NM AS UNIT, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS OVERALL_INVEN, CI.CRM_SN, CI.CRM_NM, PD.APPR_STAT, IFNULL(PD.PAY_INCP_SN, 0) AS PAY_INCP_SN, CASE WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) = PI.TOT_COST THEN '입금완료' WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != PI.TOT_COST THEN '부분입금' ELSE '미입금' END AS DOC_STATUS_TEXT FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO JOIN CAM_CRM.DJ_CRM_INFO CI ON OO.CRM_SN = CI.CRM_SN JOIN CAM_HR.DJ_EMP_INFO EI ON OO.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = OO.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD IUC ON IUC.LG_CD = 'UNIT' AND IM.ITEM_UNIT_CD = IUC.ITEM_CD LEFT JOIN CAM_MNG.DJ_PAY_DEPO PD ON PD.PAY_DEPO_SN = OO.PAY_DEPO_SN LEFT JOIN ( SELECT PI.PAY_INCP_SN, PI.DOC_STATUS, E.RE_CNT, E.RE_TOT_COST, C.TOT_COST FROM CAM_MNG.DJ_PAY_INCP PI LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS TOT_COST FROM CAM_MNG.DJ_PAY_INCP_DET GROUP BY PAY_INCP_SN )C ON PI.PAY_INCP_SN = C.PAY_INCP_SN LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS RE_TOT_COST, COUNT(PAY_INCP_RE_SN) AS RE_CNT FROM CAM_MNG.DJ_PAY_INCP_RE WHERE RE_STAT = 'Y' GROUP BY PAY_INCP_SN ) E ON PI.PAY_INCP_SN = E.PAY_INCP_SN )PI ON PD.PAY_INCP_SN = PI.PAY_INCP_SN WHERE 1=1
AND OO.OBTAIN_ORDER_TYPE = #{obtainOrderType}
AND OO.DEADLINE = #{deadLine}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d')
AND OO.CRM_SN = #{crmSn}
AND IM.MASTER_SN = #{masterSn}
AND OO.OBTAIN_ORDER_SN IN(${ooArr})
AND OO.DEADLINE = #{deadLine}
AND OO.UNPAID_TYPE = #{unpaidType}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND OO.REG_EMP_SEQ = #{regEmpSeq}
AND IM.ITEM_NO LIKE CONCAT(#{category}, '%')
AND (OO.REG_EMP_SEQ, DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = #{obtainOrderSn})
ORDER BY CASE WHEN OO.MOD_DT IS NULL THEN OO.REG_DT ELSE OO.MOD_DT END DESC

/* getObtainOrderExcelList */ SELECT ROW_NUMBER() OVER (ORDER BY DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i') DESC) AS ROW_NUM, CASE WHEN OO.DEADLINE = 'Y' THEN '견적마감' WHEN OO.DEADLINE = 'N' THEN '견적진행중' ELSE '' END AS DEADLINE, CASE WHEN OO.ORDER_REMAIN = '0' THEN '납품완료' ELSE '주문완료' END AS ORDERAMT, CASE WHEN OO.FORE_GB = 'D' THEN '국내' WHEN OO.FORE_GB = 'F' THEN '국외' ELSE '' END AS FORE_GB, (SELECT A.CATEGORY_CODE_NM FROM CAM_ITEM.DJ_ITEM_CATEGORY A WHERE A.ITEM_CATEGORY_SN = IM.CATEGORY_A_SN) AS CATEGORY_A_SN, (SELECT A.CATEGORY_CODE_NM FROM CAM_ITEM.DJ_ITEM_CATEGORY A WHERE A.ITEM_CATEGORY_SN = IM.CATEGORY_B_SN) AS CATEGORY_B_SN, (SELECT A.CATEGORY_CODE_NM FROM CAM_ITEM.DJ_ITEM_CATEGORY A WHERE A.ITEM_CATEGORY_SN = IM.CATEGORY_C_SN) AS CATEGORY_C_SN, IM.ITEM_NO, IM.ITEM_NAME, IM.STANDARD, FORMAT(OO.ORDER_VOLUME, '#,#') AS ORDER_VOLUME, FORMAT(OO.UNIT_PRICE, '#,#') AS UNIT_PRICE, FORMAT(OO.AMT, '#,#') AS AMT, CI.CRM_SN, CI.CRM_NM, CI.CRM_LOC, FORMAT((SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN), '#,#') AS OVERALL_INVEN, CASE WHEN OO.PAY_DEPO_SN IS NOT NULL AND PD.APPR_STAT = 'Y' THEN '요청완료' WHEN OO.PAY_DEPO_SN IS NOT NULL AND PD.APPR_STAT != 'Y' THEN '작성' ELSE '미작성' END AS OVERALL_KR, FORMAT(OO.DELIVERY_AMT, '#,#') AS DELIVERY_AMT, FORMAT(OO.ORDER_REMAIN, '#,#') AS ORDER_REMAIN, OO.DUE_DT, FORMAT(IFNULL(PI.RE_TOT_COST, 0), '#,#') AS COM_AMT, FORMAT(IFNULL(PI.COM_NOT_AMT, 0), '#,#') AS COM_NOT_AMT, OO.PAY_DEPO_SN, OO.RMK, EI.EMP_NAME_KR, CASE WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) = PI.TOT_COST THEN '입금완료' WHEN PI.DOC_STATUS = '100' AND IFNULL(PI.RE_CNT, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != 0 AND IFNULL(PI.RE_TOT_COST, 0) != PI.TOT_COST THEN '부분입금' ELSE '미입금' END AS DOC_STATUS_TEXT FROM (SELECT @ROWNUM := 0) R, CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO JOIN CAM_CRM.DJ_CRM_INFO CI ON OO.CRM_SN = CI.CRM_SN JOIN CAM_HR.DJ_EMP_INFO EI ON OO.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = OO.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD IUC ON IUC.LG_CD = 'UNIT' AND IM.ITEM_UNIT_CD = IUC.ITEM_CD LEFT JOIN CAM_MNG.DJ_PAY_DEPO PD ON PD.PAY_DEPO_SN = OO.PAY_DEPO_SN LEFT JOIN ( SELECT PI.PAY_INCP_SN, PI.DOC_STATUS, E.RE_CNT, E.RE_TOT_COST, C.TOT_COST, (IFNULL(C.TOT_COST,0) - IFNULL(E.RE_TOT_COST,0)) AS COM_NOT_AMT FROM CAM_MNG.DJ_PAY_INCP PI LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS TOT_COST FROM CAM_MNG.DJ_PAY_INCP_DET GROUP BY PAY_INCP_SN )C ON PI.PAY_INCP_SN = C.PAY_INCP_SN LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS RE_TOT_COST, COUNT(PAY_INCP_RE_SN) AS RE_CNT FROM CAM_MNG.DJ_PAY_INCP_RE WHERE RE_STAT = 'Y' GROUP BY PAY_INCP_SN ) E ON PI.PAY_INCP_SN = E.PAY_INCP_SN )PI ON PD.PAY_INCP_SN = PI.PAY_INCP_SN WHERE 1=1 AND OO.OBTAIN_ORDER_TYPE = 'Y'
AND OO.DEADLINE = #{deadLine}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(OO.ORDER_DT, '%Y%m%d')
AND OO.CRM_SN = #{crmSn}
AND IM.MASTER_SN = #{masterSn}
AND OO.OBTAIN_ORDER_SN IN(${ooArr})
AND OO.DEADLINE = #{deadLine}
AND OO.UNPAID_TYPE = #{unpaidType}
AND IFNULL( (SELECT SUM(SS.ORDER_REMAIN) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) = '0'
AND IFNULL( (SELECT SUM(SS.ORDER_REMAIN) FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SS WHERE SS.OBTAIN_ORDER_TYPE = 'Y' AND (SS.REG_EMP_SEQ, DATE_FORMAT(SS.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = OO.OBTAIN_ORDER_SN) ), 0) != '0'
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( CI.CRM_NM LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND OO.REG_EMP_SEQ = #{regEmpSeq}
AND IM.ITEM_NO LIKE CONCAT(#{category}, '%')
ORDER BY DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i') DESC

/* getOrderDeliveryAmtChk */ SELECT
CASE WHEN CAST(IFNULL(SR.DELIVERY_AMT, 0) AS UNSIGNED) > CAST(#{orderVolume} AS UNSIGNED) THEN 1 ELSE 0 END
FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO LEFT JOIN CAM_ITEM.DJ_ITEM_SM_RECORD SR ON OO.OBTAIN_ORDER_SN = SR.OBTAIN_ORDER_SN WHERE OO.OBTAIN_ORDER_SN = #{obtainOrderSn}

/* getShipmentRecordMaster */ SELECT SR.*, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS OVERALL_INVEN, EI.EMP_NAME_KR, IM.MASTER_SN, IM.ITEM_NO, IM.ITEM_NAME, CI.CRM_NM FROM CAM_ITEM.DJ_ITEM_SM_RECORD SR JOIN CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO ON OO.OBTAIN_ORDER_SN = SR.OBTAIN_ORDER_SN JOIN CAM_CRM.DJ_CRM_INFO CI ON SR.CRM_SN = CI.CRM_SN JOIN CAM_HR.DJ_EMP_INFO EI ON SR.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = OO.MASTER_SN WHERE 1=1
AND SR.DEADLINE = #{deadline}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SR.DELIVERY_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SR.DELIVERY_DT, '%Y%m%d')
AND SR.CRM_SN = #{crmSn}
AND IM.MASTER_SN = #{masterSn}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND SR.REG_EMP_SEQ = #{regEmpSeq}
AND SR.SM_RECORD_SN IN (${smRecordSnArr})
GROUP BY OO.REG_EMP_SEQ, DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i') ORDER BY SR.SM_RECORD_SN DESC

/* getShipmentRecordList */ SELECT SR.*, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS OVERALL_INVEN, EI.EMP_NAME_KR, IM.MASTER_SN, IM.ITEM_NO, IM.ITEM_NAME, CI.CRM_NM FROM CAM_ITEM.DJ_ITEM_SM_RECORD SR JOIN CAM_ITEM.DJ_ITEM_OBTAIN_ORDER OO ON OO.OBTAIN_ORDER_SN = SR.OBTAIN_ORDER_SN JOIN CAM_CRM.DJ_CRM_INFO CI ON SR.CRM_SN = CI.CRM_SN JOIN CAM_HR.DJ_EMP_INFO EI ON SR.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = OO.MASTER_SN WHERE 1=1
AND SR.DEADLINE = #{deadline}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SR.DELIVERY_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SR.DELIVERY_DT, '%Y%m%d')
AND SR.CRM_SN = #{crmSn}
AND IM.MASTER_SN = #{masterSn}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND SR.REG_EMP_SEQ = #{regEmpSeq}
AND SR.SM_RECORD_SN IN (${smRecordSnArr})
AND (OO.REG_EMP_SEQ, DATE_FORMAT(OO.REG_DT, '%Y-%m-%d %p %h:%i')) IN (SELECT SOO.REG_EMP_SEQ, DATE_FORMAT(SOO.REG_DT, '%Y-%m-%d %p %h:%i') FROM CAM_ITEM.DJ_ITEM_OBTAIN_ORDER SOO WHERE SOO.OBTAIN_ORDER_SN = #{obtainOrderSn})
ORDER BY SR.DELIVERY_DT DESC

/* getShipmentTrendList */
SELECT A.MONTH_DATA, SUM(IFNULL(B.CONFIRM_AMT, 0)) AS CONFIRM_AMT FROM (
SELECT DATE_FORMAT(CONCAT(#{year}, '-12-01') + INTERVAL -SEQ MONTH, '%Y-%m') MONTH_DATA FROM SEQ_0_TO_99 A WHERE SEQ < 60
)A LEFT JOIN CAM_ITEM.DJ_CRM_SALES_CONFIRM B ON A.MONTH_DATA = DATE_FORMAT(B.CONFIRM_DATE, '%Y-%m')
AND B.CRM_SN = #{crmSn}
GROUP BY MONTH_DATA

/* getReturnRecordRegList */ SELECT RR.*, EI.EMP_NAME_KR, IM.ITEM_NO, IM.ITEM_NAME, CI.CRM_NM, WHC.ITEM_CD_NM AS WH_CD_NM FROM CAM_ITEM.DJ_ITEM_RETURN_RECORD RR JOIN CAM_CRM.DJ_CRM_INFO CI ON RR.CRM_SN = CI.CRM_SN JOIN CAM_ITEM.DJ_ITEM_INVENTORY II ON II.INVEN_SN = RR.INVEN_SN JOIN CAM_HR.DJ_EMP_INFO EI ON RR.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = II.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND II.WH_CD = WHC.ITEM_CD WHERE 1=1
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(RR.DELIVERY_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(RR.DELIVERY_DT, '%Y%m%d')
AND RR.CRM_SN = #{crmSn}
AND II.WH_CD = #{whCd}
AND IM.MASTER_SN = #{masterSn}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND RR.REG_EMP_SEQ = #{regEmpSeq}
ORDER BY RR.DELIVERY_DT DESC

/* getBomList */ SELECT B.*, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = B.MASTER_SN) AS CURRENT_INVEN, IM.*, WHC.ITEM_CD_NM AS WH_CD_NM, ITC.ITEM_CD_NM AS ITEM_TYPE_NM FROM CAM_ITEM.DJ_BOM B JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = B.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND IM.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD ITC ON ITC.LG_CD = 'IT' AND IM.ITEM_TYPE = ITC.ITEM_CD WHERE 1=1
AND IM.MASTER_SN = #{masterSn}
AND IM.WH_CD = #{whCd}
AND IM.ITEM_TYPE = #{itemType}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR IM.STANDARD LIKE CONCAT('%', #{searchValue}, '%') )
ORDER BY CASE WHEN B.MOD_DT IS NULL THEN B.REG_DT ELSE B.MOD_DT END DESC

/* getBomOutputHistory */ SELECT BOH.BOM_SN, IM.ITEM_NO, IM.ITEM_NAME, IM.STANDARD, ITC.ITEM_CD_NM AS ITEM_TYPE_NM, WHC.ITEM_CD_NM AS WH_CD_NM, BOH.OUTPUT_CNT, BOH.RMK, EI.EMP_NAME_KR, DATE_FORMAT(BOH.REG_DT, '%Y-%m-%d') AS REG_DT FROM CAM_ITEM.DJ_BOM_OUTPUT_HISTORY BOH JOIN CAM_HR.DJ_EMP_INFO EI ON BOH.REG_EMP_SEQ = EI.EMP_SEQ JOIN CAM_ITEM.DJ_BOM B ON BOH.BOM_SN = B.BOM_SN JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = B.MASTER_SN JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND IM.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD ITC ON ITC.LG_CD = 'IT' AND IM.ITEM_TYPE = ITC.ITEM_CD WHERE 1=1
AND IM.WH_CD = #{whCd}
AND IM.ITEM_TYPE = #{itemType}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR IM.STANDARD LIKE CONCAT('%', #{searchValue}, '%') )
ORDER BY BOH.REG_DT DESC

