mapper.select:
/* getItemWhInfoList */ SELECT WI.*, (SELECT IFNULL(ITEM_NO, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS ITEM_NO, (SELECT IFNULL(ITEM_NAME, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS ITEM_NAME, (SELECT IFNULL(ITEM_UNIT_CD, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS ITEM_UNIT_CD, (SELECT IFNULL(ITEM_TYPE, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS ITEM_TYPE, (SELECT IFNULL(STANDARD, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS STANDARD, (SELECT IFNULL(WH_CD, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS WH_CD, (SELECT IFNULL(SAFETY_INVEN, '') FROM CAM_ITEM.DJ_ITEM_MASTER WHERE MASTER_SN = WI.MASTER_SN) AS SAFETY_INVEN, CI.CRM_NM, WHC.ITEM_CD_NM AS WH_CD_NM, WTC.ITEM_CD_NM AS WH_TYPE_NM FROM CAM_ITEM.DJ_ITEM_WH_INFO WI JOIN CAM_CRM.DJ_CRM_INFO CI ON WI.CRM_SN = CI.CRM_SN JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND WI.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD WTC ON WTC.LG_CD = 'WT' AND WI.WH_TYPE = WTC.ITEM_CD WHERE 1=1
AND WI.WH_TYPE = #{whType}
AND CI.CRM_SN = #{crmSn}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(WI.WH_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(WI.WH_DT, '%Y%m%d')
AND WI.WH_CD = #{whCd}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND WI.INSPECTION = #{inspection}
AND WI.REG_EMP_SEQ = #{regEmpSeq}
AND WI.CRM_SN = #{crmSn}
AND WI.MASTER_SN = #{masterSn}
ORDER BY WI.WH_DT DESC

/* getItemInvenValidation */
SELECT
    *
FROM
    CAM_ITEM.DJ_ITEM_INVENTORY
WHERE
    MASTER_SN = #{masterSn}

AND
    WH_CD = #{whCd}

/* getItemInvenList */ SELECT IM.*, (SELECT ITEM_CD_NM FROM CAM_ITEM.DJ_ITEM_CD WHERE LG_CD = 'WH' AND ITEM_CD = IM.WH_CD) AS WH_CD_NM, (SELECT IFNULL(SUM(IN_CNT), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS IN_CNT, (SELECT IFNULL(SUM(OUT_CNT), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS OUT_CNT, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS TOT_CNT, IM.SAFETY_INVEN AS SAFE_CNT, IFNULL(ITEM_REAL_CNT, 0) AS REAL_CNT, (SELECT IFNULL((SELECT UNIT_AMT FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND UNIT_AMT IS NOT NULL ORDER BY REG_DT DESC LIMIT 1) * (SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN) AS AMT, IFNULL((SELECT UNIT_AMT FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND UNIT_AMT IS NOT NULL ORDER BY REG_DT DESC LIMIT 1), 0) AS UNIT_AMT FROM CAM_ITEM.DJ_ITEM_MASTER IM WHERE 1=1
AND IM.WH_CD = #{whCd}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND IM.ACTIVE = 'Y' ORDER BY IM.REG_DT DESC

/* getItemInvenAdminList */ SELECT AA.*, BEF_TOT_CNT - TOT_CNT AS TOT_CNT_2, IF(REAL_CNT = 0, (BEF_TOT_CNT - TOT_CNT), REAL_CNT) AS REAL_CNT_2 FROM (
SELECT IM.*, (SELECT ITEM_CD_NM FROM CAM_ITEM.DJ_ITEM_CD WHERE LG_CD = 'WH' AND ITEM_CD = IM.WH_CD) AS WH_CD_NM, (SELECT IFNULL(SUM(IN_CNT), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{nowStrDt} AND #{nowEndDt}) AS IN_CNT, (SELECT IFNULL(SUM(OUT_CNT), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{nowStrDt} AND #{nowEndDt}) AS OUT_CNT, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{nowStrDt} AND #{nowEndDt}) AS TOT_CNT, IM.SAFETY_INVEN AS SAFE_CNT, IFNULL(ITEM_REAL_CNT, 0) AS REAL_CNT, (SELECT IFNULL((SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{befStrDt} AND #{befEndDt}) AS BEF_TOT_CNT, (SELECT IFNULL((SELECT UNIT_AMT FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND UNIT_AMT IS NOT NULL ORDER BY REG_DT DESC LIMIT 1) * (SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{befStrDt} AND #{befEndDt}) AS BEF_AMT, (SELECT IFNULL((SELECT UNIT_AMT FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND UNIT_AMT IS NOT NULL ORDER BY REG_DT DESC LIMIT 1) * (SUM(IN_CNT) - SUM(OUT_CNT)), 0) FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{nowStrDt} AND #{nowEndDt}) AS AMT, IFNULL((SELECT UNIT_AMT FROM CAM_ITEM.DJ_ITEM_HIST WHERE MASTER_SN = IM.MASTER_SN AND UNIT_AMT IS NOT NULL ORDER BY REG_DT DESC LIMIT 1), 0) AS UNIT_AMT FROM CAM_ITEM.DJ_ITEM_MASTER IM WHERE 1=1 AND IM.ACTIVE = 'Y' ORDER BY IM.REG_DT DESC
) AA WHERE 1=1
AND AA.WH_CD_NM LIKE CONCAT('%',#{searchValue},'%')
AND AA.ITEM_NAME LIKE CONCAT('%',#{searchValue},'%')
AND ( AA.WH_CD_NM LIKE CONCAT('%',#{searchValue},'%') OR AA.ITEM_NAME LIKE CONCAT('%',#{searchValue},'%') )

/* getInvenTransferHistoryList */ SELECT IT.*, IM.*, EI.EMP_NAME_KR, TFT.ITEM_CD_NM AS TRANSFER_TYPE_NM, FWH.ITEM_CD_NM AS FORWARDING_WH_CD_NM, RWH.ITEM_CD_NM AS RECEIVING_WH_CD_NM FROM CAM_ITEM.DJ_ITEM_INVENTORY_TRANSFER IT JOIN CAM_ITEM.DJ_ITEM_MASTER IM ON IM.MASTER_SN = IT.MASTER_SN JOIN CAM_HR.DJ_EMP_INFO EI ON IT.FORWARDER = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_CD TFT ON TFT.LG_CD = 'TFT' AND IT.TRANSFER_TYPE = TFT.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD FWH ON FWH.LG_CD = 'WH' AND IT.FORWARDING_WH_CD = FWH.ITEM_CD LEFT JOIN CAM_ITEM.DJ_ITEM_CD RWH ON RWH.LG_CD = 'WH' AND IT.RECEIVING_WH_CD = RWH.ITEM_CD WHERE 1=1
AND IT.TRANSFER_TYPE = #{transferType}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(IT.FORWARDING_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(IT.FORWARDING_DATE, '%Y%m%d')
AND IT.FORWARDING_WH_CD = #{forwardingWhCd}
AND IT.RECEIVING_WH_CD = #{receivingWhCd}
AND IM.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IM.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IM.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND IT.REG_EMP_SEQ = #{regEmpSeq}
ORDER BY IT.FORWARDING_DATE DESC

