mapper.select:
/* getCrmSalesConfirmList */ SELECT * FROM ( SELECT CI.CRM_SN, CI.CRM_NM, SR.OBTAIN_ORDER_SN, IFNULL(CSC.CRM_SALES_CONFIRM_SN, '') AS CRM_SALES_CONFIRM_SN, IFNULL(SR.SALES_MONTH, 0) AS SALES_MONTH, IFNULL(SR.DEPOSIT_AMT, 0) AS DEPOSIT_AMT, IFNULL(CSC.CONFIRM_AMT, 0) AS CONFIRM_AMT, IFNULL(CSC.CONFIRM_YN, 'N') AS CONFIRM_YN, EI.EMP_SEQ, EI.EMP_NAME_KR, (select IFNULL(sum(TOT_AMT),0) from cam_item.dj_item_hist where crm_sn = CI.CRM_SN and confirm_chk = 'Y' and date_format(MOD_DT, '%Y-%m') = #{yearMonth} and in_cnt = 0) AS DEADLINE_AMT, (select confirm_chk from cam_item.dj_item_hist where crm_sn = CI.CRM_SN and date_format(MOD_DT, '%Y-%m') = #{yearMonth} limit 1) AS CONFIRM_CHK FROM CAM_CRM.DJ_CRM_INFO CI LEFT JOIN CAM_ITEM.DJ_CRM_SALES_CONFIRM CSC ON CI.CRM_SN = CSC.CRM_SN AND DATE_FORMAT(CSC.CONFIRM_DATE, '%Y-%m') = #{yearMonth} LEFT JOIN CAM_HR.DJ_EMP_INFO EI ON CSC.CONFIRM_EMP_SEQ = EI.EMP_SEQ LEFT JOIN ( SELECT SR.OBTAIN_ORDER_SN, SR.CRM_SN, SUM(SRH.AMT) AS SALES_MONTH, SUM(DISTINCT SR.DEPOSIT_AMT) AS DEPOSIT_AMT FROM CAM_ITEM.DJ_ITEM_SM_RECORD_HIST SRH JOIN CAM_ITEM.DJ_ITEM_SM_RECORD SR ON SRH.SM_RECORD_SN = SR.SM_RECORD_SN WHERE SR.DEADLINE = 'Y' AND SR.UNPAID_TYPE != 'C' AND DATE_FORMAT(SRH.DELIVERY_DT, '%Y-%m') = #{yearMonth} GROUP BY SRH.CRM_SN )SR ON CI.CRM_SN = SR.CRM_SN WHERE 1=1 ) SUB WHERE 1=1
AND SUB.CRM_SN = #{crmSn}
AND SUB.OBTAIN_ORDER_SN IS NOT NULL
AND SUB.CONFIRM_YN = 'N'
AND SUB.CONFIRM_YN = 'Y'

