mapper.select:
/* getItemWhInfoList */ SELECT WI.*, CI.CRM_NM, WHC.ITEM_CD_NM AS WH_CD_NM, WTC.ITEM_CD_NM AS WH_TYPE_NM FROM CAM_ITEM.DJ_ITEM_WH_INFO WI JOIN CAM_CRM.DJ_CRM_INFO CI ON WI.CRM_SN = CI.CRM_SN JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND WI.WH_CD = WHC.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD WTC ON WTC.LG_CD = 'WT' AND WI.WH_TYPE = WTC.ITEM_CD WHERE 1=1
AND WI.WH_TYPE = #{whType}
AND WI.WH_CD = #{whCd}
AND WI.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( WI.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR WI.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
AND WI.INSPECTION = #{inspection}

/* getItemInvenList */ SELECT II.*, WHC.ITEM_CD_NM AS WH_CD_NM FROM CAM_ITEM.DJ_ITEM_INVENTORY II JOIN CAM_ITEM.DJ_ITEM_CD WHC ON WHC.LG_CD = 'WH' AND II.WH_CD = WHC.ITEM_CD
AND II.WH_CD = #{whCd}
AND II.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( II.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR II.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )

SELECT IT.*, EI.EMP_NAME_KR, TFT.ITEM_CD_NM AS TRANSFER_TYPE_NM, FWH.ITEM_CD_NM AS FORWARDING_WH_CD_NM, RWH.ITEM_CD_NM AS RECEIVING_WH_CD_NM FROM CAM_ITEM.DJ_ITEM_INVENTORY_TRANSFER IT JOIN CAM_HR.DJ_EMP_INFO EI ON IT.FORWARDER = EI.EMP_SEQ JOIN CAM_ITEM.DJ_ITEM_CD TFT ON TFT.LG_CD = 'TFT' AND IT.TRANSFER_TYPE = TFT.ITEM_CD JOIN CAM_ITEM.DJ_ITEM_CD FWH ON FWH.LG_CD = 'WH' AND IT.FORWARDING_WH_CD = FWH.ITEM_CD LEFT JOIN CAM_ITEM.DJ_ITEM_CD RWH ON RWH.LG_CD = 'WH' AND IT.RECEIVING_WH_CD = RWH.ITEM_CD WHERE 1=1
AND IT.TRANSFER_TYPE = #{transferType}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(IT.FORWARDING_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(IT.FORWARDING_DATE, '%Y%m%d')
AND IT.FORWARDING_WH_CD = #{forwardingWhCd}
AND IT.RECEIVING_WH_CD = #{receivingWhCd}
AND IT.${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( IT.ITEM_NO LIKE CONCAT('%', #{searchValue}, '%') OR IT.ITEM_NAME LIKE CONCAT('%', #{searchValue}, '%') )
