/*getEtaxListAll*/ SELECT A.*, B.*, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = (SELECT REG_EMP_SEQ FROM CAM_MNG.DJ_PAY_APP WHERE PAY_APP_SN = B.PAY_APP_SN)) AS PAY_EMP_SEQ, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = (SELECT EMP_SEQ FROM CAM_MNG.DJ_CLAIM_EXNP WHERE CE_GW_IDX = B.CE_GW_IDX AND PAY_APP_SN IS NULL LIMIT 1)) AS PURC_EMP_SEQ, (SELECT DATE_FORMAT(REG_DT, '%Y-%m-%d') FROM CAM_MNG.DJ_PAY_APP WHERE PAY_APP_SN = B.PAY_APP_SN) AS PAY_REG_DT, (SELECT DATE_FORMAT(REG_DATE, '%Y-%m-%d') FROM CAM_MNG.DJ_CLAIM_EXNP WHERE CE_GW_IDX = B.CE_GW_IDX AND PAY_APP_SN IS NULL LIMIT 1) AS CLAIM_EXNP_REG_DT, (SELECT DOC_STATUS FROM CAM_MNG.DJ_EXNP WHERE PAY_APP_SN = (SELECT PAY_APP_SN FROM CAM_MNG.DJ_PAY_APP WHERE PAY_APP_SN = B.PAY_APP_SN) LIMIT 1) AS PAY_EXNP_STAT FROM CAM_MNG.DJ_ETAX_INFO A LEFT JOIN (SELECT * FROM CAM_MNG.DJ_USE_ETAX_INFO GROUP BY ISS_NO) B ON A.ISS_NO = B.ISS_NO AND A.CO_CD = B.CO_CD AND A.TAX_TY = B.TAX_TY WHERE 1 = 1
AND TR_NM LIKE CONCAT('%', #{crmNm} , '%')
AND TRREG_NB LIKE CONCAT('%', #{crmNo} , '%')
AND ISS_DT BETWEEN #{strDt} AND #{endDt}
AND ISU_DT BETWEEN #{strDt} AND #{endDt}
AND ISS_YMD BETWEEN #{strDt} AND #{endDt}
AND A.TAX_TY = '2'
AND A.TAX_TY = '4'
AND A.TR_NM LIKE CONCAT('%', #{searchValue} , '%')
AND B.USE_ETAX_SN IS NULL
ORDER BY A.ISS_DT DESC

SELECT HEI.*, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_SEQ END AS DEPT_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME1, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_SEQ ELSE "" END AS TEAM_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_NAME ELSE "" END AS TEAM_NAME, DATE_FORMAT(HEI.JOIN_DAY,'%Y-%m-%d') AS JOIN_DAY2, IFNULL(CASE WHEN HEI.DUTY_NAME = "" THEN HEI.POSITION_NAME ELSE HEI.DUTY_NAME END, "") AS SPOT,
CASE WHEN HEI.DUTY_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 3 AND CM_CODE = HEI.DUTY_CODE) WHEN HEI.POSITION_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 4 AND CM_CODE = HEI.POSITION_CODE) ELSE 999 END SORT
FROM CAM_HR.DJ_EMP_INFO AS HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE HEI.ACTIVE = 'Y' AND HEI.ERP_EMP_SEQ != '' AND HEI.WORK_STATUS_CODE = 'Y'
AND HEI.GENDER_CODE = #{userGender}
AND ${userKind} like concat('%', #{kindContent} ,'%')
AND HEI.DEPT_SEQ = #{deptComp}
OR HEI.DEPT_SEQ IN ( SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = #{deptComp} OR PARENT_DEPT_SEQ = #{deptComp} )
AND HEI.DEPT_SEQ = #{deptTeam}
ORDER BY SORT

/*getBudgetDetailViewData*/ SELECT A.PJT_CD, A.PAY_APP_TYPE, A.REVERT_YN, B.PAY_APP_SN, B.PAY_APP_DET_SN AS EXNP_SN, B.BUDGET_SN, SUBSTRING(B.BUDGET_SN, 1, 1) AS JANG_SN, SUBSTRING(B.BUDGET_SN, 1, 3) AS GWAN_SN, B.BUDGET_NM, A.REQ_DE, B.TOT_COST, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO HR WHERE HR.EMP_SEQ = A.REG_EMP_SEQ) AS EMP_NAME, A.DOC_STATUS, 'N' AS RE_STAT, NULL AS INFO_CODE FROM CAM_MNG.DJ_PAY_APP A LEFT JOIN CAM_MNG.DJ_PAY_APP_DET B ON A.PAY_APP_SN = B.PAY_APP_SN LEFT JOIN CAM_MNG.DJ_EXNP C ON C.PAY_APP_SN = B.PAY_APP_SN WHERE A.PJT_CD = #{pjtCd} AND A.DOC_STATUS = 100 AND ((A.EVID_TYPE IN (4, 5, 6) AND C.DOC_STATUS != 100) OR (A.EVID_TYPE IN (1, 2, 3) AND C.RE_STAT != 'Y') OR A.EVID_TYPE IS NULL) AND B.BUDGET_SN IS NOT NULL AND B.BUDGET_SN LIKE CONCAT(#{bgtCd}, '%')
UNION ALL
SELECT A.PJT_CD, A.PAY_APP_TYPE, NULL AS REVERT_YN, A.PAY_APP_SN, A.EXNP_SN, B.BUDGET_SN, SUBSTRING(B.BUDGET_SN, 1, 1) AS JANG_SN, SUBSTRING(B.BUDGET_SN, 1, 3) AS GWAN_SN, B.BUDGET_NM, A.REQ_DE, B.TOT_COST, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO HR WHERE HR.EMP_SEQ = A.REG_EMP_SEQ) AS EMP_NAME, A.DOC_STATUS, A.RE_STAT, A.INFO_CODE FROM CAM_MNG.DJ_EXNP A LEFT JOIN CAM_MNG.DJ_EXNP_DET B ON A.EXNP_SN = B.EXNP_SN WHERE A.PJT_CD = #{pjtCd} AND A.DOC_STATUS IN (100, 101) AND A.RE_STAT = 'Y' AND B.BUDGET_SN IS NOT NULL AND B.BUDGET_SN LIKE CONCAT(#{bgtCd}, '%') ORDER BY REQ_DE DESC

