mapper.select:
/*useCardDetailInfo*/
SELECT
    *
FROM
    CAM_HR.ERP_HIST_CORPCD_USE
WHERE
    AUTH_NO = #{authNo} AND CARD_NO = #{cardNo}
AND
    AUTH_DD = #{authDate} AND AUTH_HH = #{authTime}
AND
    BUY_STS = #{buySts}

mapper.update:
/*delCardTo*/
UPDATE CAM_MNG.DJ_CARD_TO
SET
    DEL_YN = 'Y'
WHERE
    CARD_TO_SN = #{cardToSn}

mapper.insert:
INSERT INTO CAM_MNG.DJ_CARD_USER_GROUP
    (
         GROUP_NAME
        , REG_EMP_SEQ
        , REG_EMP_NAME
        , REG_DEPT_NAME
        , USE_YN
    )
VALUES
    (
        #{groupNm}
        , #{regEmpSeq}
        , #{regEmpName}
        , #{regDeptName}
        , #{useYn}
    )


    SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_MNG' AND TABLE_NAME = 'DJ_CARD_USER_GROUP'

mapper.select:
/*getCorpCardList*/ SELECT A.*, IFNULL(B.TR_CD, 'Y') as USE_YN, C.USE_EMP_SEQ, C.USE_EMP_NAME, C.CARD_TO_DE, C.CARD_FROM_DE, C.RT_YN, C.DEL_YN, C.REG_EMP_SEQ, D.MNG_SEQ, D.MNG_NAME, D.MNG_DEPT_SEQ, D.MNG_DEPT_NAME, REPLACE((SELECT PATH_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT DEPT_SEQ FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = D.MNG_SEQ)), '|', ' ') AS DEPT_NAME FROM CAM_MNG.DJ_CARD_INFO A LEFT JOIN CAM_MNG.DJ_CARD_PRIV B ON A.TR_CD = B.TR_CD AND A.CARD_BA_NB = B.CARD_BA_NB LEFT JOIN (SELECT * FROM( SELECT * FROM CAM_MNG.DJ_CARD_TO WHERE (TR_CD, REG_DT) IN ( SELECT TR_CD, MAX(REG_DT) AS DATE_TIME FROM CAM_MNG.DJ_CARD_TO GROUP BY TR_CD ) ORDER BY REG_DT DESC ) T GROUP BY T.TR_CD) C ON A.TR_CD = C.TR_CD AND A.CARD_BA_NB = C.CARD_BA_NB LEFT JOIN CAM_MNG.DJ_CARD_MANAGER D ON A.TR_CD = D.TR_CD AND A.CARD_BA_NB = D.CARD_BA_NB WHERE A.TR_NM NOT LIKE '%개인카드%'
AND (A.TR_NM LIKE CONCAT('%', #{searchValue}, '%') OR A.BA_NB LIKE CONCAT('%', #{searchValue}, '%') OR A.CARD_BA_NB LIKE CONCAT('%', #{searchValue}, '%'))
AND B.TR_CD IS NULL

/*getCardManagerData*/
SELECT
    *
FROM
    CAM_MNG.DJ_CARD_MANAGER
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
AND
    TR_CD = #{TR_CD}

mapper.update:
/*updCardManager*/
UPDATE CAM_MNG.DJ_CARD_MANAGER
SET
    MNG_SEQ = #{MNG_SEQ},
    MNG_NAME = #{MNG_NAME},
    MNG_DEPT_SEQ = #{MNG_DEPT_SEQ},
    MNG_DEPT_NAME = #{MNG_DEPT_NAME}
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
AND
    TR_CD = #{TR_CD}
