mapper.select:
/*useCardDetailInfo*/
SELECT
    *
FROM
    CAM_HR.ERP_HIST_CORPCD_USE
WHERE
    AUTH_NO = #{authNo} AND CARD_NO = #{cardNo}
AND
    AUTH_DD = #{authDate} AND AUTH_HH = #{authTime}
AND
    BUY_STS = #{buySts}

mapper.update:
/*delCardTo*/
UPDATE CAM_MNG.DJ_CARD_TO
SET
    DEL_YN = 'Y'
WHERE
    CARD_TO_SN = #{cardToSn}
