mapper.select:
/*useCardDetailInfo*/
SELECT
    A.*,
    B.TR_NM,
    C.FILE_NO
FROM
    CAM_HR.ERP_HIST_CORPCD_USE A
LEFT JOIN CAM_MNG.DJ_CARD_INFO B ON A.CARD_NO = REPLACE(B.CARD_BA_NB ,'-','')
LEFT JOIN CAM_INSIDE.DJ_HR_CARD_HIST C ON C.CARD_NO = A.CARD_NO AND C.AUTH_DD = A.AUTH_DD AND C.AUTH_NO = A.AUTH_NO AND C.AUTH_HH = C.AUTH_HH AND C.BUY_STS = A.BUY_STS
WHERE
    A.AUTH_NO = #{authNo} AND A.CARD_NO = #{cardNo}
AND
    A.AUTH_DD = #{authDate} AND A.AUTH_HH = #{authTime}
AND
    A.BUY_STS = #{buySts}

AND C.HR_BIZ_REQ_RESULT_ID = #{hrBizReqResultId}


    AND C.HR_BIZ_REQ_ID = #{hrBizReqId}

mapper.insert:
INSERT INTO CAM_MNG.DJ_CARD_USER_GROUP
    (
         GROUP_NAME
        , REG_EMP_SEQ
        , REG_EMP_NAME
        , REG_DEPT_NAME
        , USE_YN
    )
VALUES
    (
        #{groupNm}
        , #{regEmpSeq}
        , #{regEmpName}
        , #{regDeptName}
        , #{useYn}
    )


    SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_MNG' AND TABLE_NAME = 'DJ_CARD_USER_GROUP'

mapper.select:
/*getCardManagerData*/
SELECT
    *
FROM
    CAM_MNG.DJ_CARD_MANAGER
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
AND
    TR_CD = #{TR_CD}

mapper.update:
/*updCardManager*/
UPDATE CAM_MNG.DJ_CARD_MANAGER
SET
    MNG_SEQ = #{MNG_SEQ},
    MNG_NAME = #{MNG_NAME},
    MNG_DEPT_SEQ = #{MNG_DEPT_SEQ},
    MNG_DEPT_NAME = #{MNG_DEPT_NAME}
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
AND
    TR_CD = #{TR_CD}

mapper.select:
/*getCardHolderData*/
SELECT
    *
FROM
    CAM_MNG.DJ_CARD_MANAGER
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
  AND
    TR_CD = #{TR_CD}

mapper.update:
/*updCardHolder*/
UPDATE CAM_MNG.DJ_CARD_MANAGER
SET
    HOLDER_SEQ = #{HOLDER_SEQ},
    HOLDER_NAME = #{HOLDER_NAME},
    HOLDER_DEPT_SEQ = #{HOLDER_DEPT_SEQ},
    HOLDER_DEPT_NAME = #{HOLDER_DEPT_NAME}
WHERE
    CARD_BA_NB = #{CARD_BA_NB}
  AND
    TR_CD = #{TR_CD}

mapper.insert:
/*setCardAuthData*/
INSERT INTO CAM_MNG.DJ_CARD_AUTH_MNG
(
    TR_CD,
    CARD_NO,
    TR_NM
)
VALUES
(
    #{trCd},
    #{cardNo},
    #{trNm}
)

/*setCardAuthUserData*/
INSERT INTO CAM_MNG.DJ_CARD_AUTH_USER_MNG
(
    EMP_SEQ,
    EMP_NAME
)
VALUES
(
    #{empSeq},
    #{empName}
)
