mapper.select:
/*getCmmnCodeList*/ SELECT * FROM CAM_ERP.T_IFS_ERP_CMMN_CODE WHERE 1 = 1
AND CMMN_CODE_NM LIKE CONCAT('%', #{cmmnCodeNm}, '%')
ORDER BY CMMN_CODE

mapper.insert:
/*insCommCodeObject*/
INSERT INTO CAM_ERP.DJ_CMMN_CODE_OBJ
    (
        CNTC_SN, CMMN_CODE, CMMN_DETAIL_CODE, CMMN_OBJ_A, CMMN_OBJ_B, REG_EMP_SEQ
    )
VALUES
    (
        #{cntcSn}, #{cmmnCode}, #{cmmnDetailCode}, #{cmmnObjA}, #{cmmnObjB}, #{regEmpSeq}
    )

mapper.select:
/*getPayAppList*/ SELECT A.PAY_APP_DET_SN, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = B.REG_EMP_SEQ) AS EMP_NAME, C.DOC_NO, C.DOC_TITLE, B.PJT_SN, B.PJT_NM, B.PJT_CD, A.BUDGET_NM, A.EVID_TYPE, A.ISS_NO, A.TOT_COST, D.REQ_STAT_SN, E.PRUF_SE_CODE, E.PRUF_SE_NO, DATE_FORMAT(E.EXCUT_REQUST_DE, '%Y-%m-%d') AS EXCUT_REQUST_DE, E.EXCUT_SUM_AMOUNT, E.BCNC_CMPNY_NM, E.BCNC_LSFT_NO, E.TRANSFR_ACNUT_SE_CODE, E.BCNC_BANK_CODE, (SELECT CMMN_DETAIL_CODE_NM FROM CAM_ERP.T_IFS_ERP_CMMN_CODE_DETAIL WHERE E.BCNC_BANK_CODE = CMMN_DETAIL_CODE AND CMMN_CODE = 1070) AS BCNC_BANK_CODE_NM, E.SBSACNT_TRFRSN_CN, E.BCNC_ACNUT_NO, F.TRNSC_ID, F.RSP_MSG, F.RSP_CD, G.PROCESS_RESULT_CODE, G.PROCESS_RESULT_MSSAGE, -- 전자(세금계산서) 결과 start I.RSP_MSG AS ISS_RSP_MSG, I.RSP_CD AS ISS_RSP_CD, J.PROCESS_RESULT_CODE AS ISS_PROCESS_RESULT_CODE, J.PROCESS_RESULT_MSSAGE AS ISS_PROCESS_RESULT_MSSAGE, -- 전자(세금계산서) 결과 end K.LNK_PJT_SN, L.BG_SN, A.ENARA_MNG_STAT, -- 사업소득자, 기타소득자 원천징수 결과 start TAX.TAX_TRNSC_ID, TAX.TAX_RSP_MNG, TAX.TAX_RSP_CD, TAX.TAX_PROCESS_RESULT_CODE, TAX.TAX_PROCESS_RESULT_MSSAGE -- 사업소득자, 기타소득자 원천징수 결과 end FROM CAM_MNG.DJ_PAY_APP_DET A LEFT JOIN CAM_MNG.DJ_PAY_APP B ON A.PAY_APP_SN = B.PAY_APP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO C ON B.DOC_ID = C.DOC_ID LEFT JOIN CAM_ERP.DJ_ERP_REQ_STAT D ON D.PAY_APP_DET_SN = A.PAY_APP_DET_SN LEFT JOIN CAM_ERP.T_IFR_EXCUT_REQUST_ERP E ON E.TRNSC_ID = D.TRNSC_ID LEFT JOIN CAM_ERP.DJ_ERP_SEND F on E.TRNSC_ID = F.TRNSC_ID LEFT JOIN CAM_ERP.T_IFS_EXCUT_RESULT_ERP G ON E.EXCUT_CNTC_ID = G.EXCUT_CNTC_ID LEFT JOIN CAM_ERP.T_IFR_ETXBL_REQUST_ERP H ON H.ETXBL_CONFM_NO = A.ISS_NO LEFT JOIN CAM_ERP.DJ_ERP_SEND I ON I.TRNSC_ID = H.TRNSC_ID LEFT JOIN CAM_ERP.T_IFS_ETXBL_RESULT_ERP J ON J.ETXBL_CONFM_NO = H.ETXBL_CONFM_NO JOIN CAM_ERP.DJ_PROJECT_LINK K ON K.PJT_CD = B.PJT_CD LEFT JOIN CAM_ERP.DJ_BUDGET_CODE L ON A.BUDGET_SN = L.BUDGET_SN LEFT JOIN ( SELECT aa.*, aa.TRNSC_ID AS TAX_TRNSC_ID, bb.RSP_MSG AS TAX_RSP_MNG, bb.RSP_CD AS TAX_RSP_CD, cc.PROCESS_RESULT_CODE AS TAX_PROCESS_RESULT_CODE, cc.PROCESS_RESULT_MSSAGE AS TAX_PROCESS_RESULT_MSSAGE FROM CAM_ERP.DJ_ERP_TAX_REQUEST aa LEFT JOIN CAM_ERP.DJ_ERP_SEND bb ON aa.TRNSC_ID = bb.TRNSC_ID LEFT JOIN CAM_ERP.T_IFS_EXCUT_RESULT_ERP cc ON aa.EXCUT_CNTC_ID = cc.EXCUT_CNTC_ID ) TAX ON TAX.PAY_APP_DET_SN = A.PAY_APP_DET_SN WHERE B.DOC_STATUS = 100
AND DATE_FORMAT(B.REG_DT, '%Y-%m-%d') BETWEEN #{strDt} AND #{endDt}
AND LEFT(B.PJT_CD, 1) IN ('R', 'S', 'A') AND A.ENARA_SEND_EXCEPT = #{exceptYn}
AND F.TRNSC_ID IS NULL AND D.REQ_STAT_SN IS NULL AND A.ENARA_MNG_STAT = 'N'
AND ((F.RSP_CD = 'SUCC' AND G.PROCESS_RESULT_CODE IS NULL) OR (D.REQ_STAT_SN IS NOT NULL AND F.RSP_CD IS NULL))
AND ((F.RSP_CD = 'SUCC' AND G.PROCESS_RESULT_CODE = '000') OR A.ENARA_MNG_STAT = 'Y')
AND (F.RSP_CD LIKE '%ER%' OR (F.RSP_CD = 'SUCC' AND G.PROCESS_RESULT_CODE IS NOT NULL AND G.PROCESS_RESULT_CODE != '000')) AND A.ENARA_MNG_STAT = 'N'
AND B.REG_EMP_SEQ = #{empSeq}
ORDER BY A.PAY_APP_DET_SN DESC

mapper.update:
/*setEnaraSendExcept*/
UPDATE CAM_MNG.DJ_PAY_APP_DET
SET
    ENARA_SEND_EXCEPT = #{exceptYn}
WHERE
    PAY_APP_DET_SN IN (${payAppDetSn})

/*setEnaraMngStat*/
UPDATE CAM_MNG.DJ_PAY_APP_DET
SET
    ENARA_MNG_STAT = #{status}
WHERE
    PAY_APP_DET_SN IN (${payAppDetSn})

mapper.select:
/*getEnaraBankInfoByPayAppData*/ SELECT A.* FROM CAM_ERP.T_IFS_ERP_CMMN_CODE_DETAIL A LEFT JOIN CAM_ERP.DJ_CMMN_CODE_OBJ B ON A.CMMN_CODE = B.CMMN_CODE AND A.CMMN_DETAIL_CODE = B.CMMN_DETAIL_CODE WHERE A.CMMN_CODE = 1070
AND A.CMMN_DETAIL_CODE_NM = #{CRM_BNK_NM}
AND (A.CMMN_DETAIL_CODE_NM = #{CRM_BNK_NM} OR B.CMMN_OBJ_A = #{CRM_BNK_NM} OR B.CMMN_OBJ_B = #{CRM_BNK_NM})

mapper.update:
/*setRequestKeyPayAppDet*/
UPDATE CAM_MNG.DJ_PAY_APP_DET
SET
    REQUST_CNTC_SN = #{requstCntcSn}
WHERE
    PAY_APP_DET_SN = #{payAppDetSn}

