mapper.select:
/*getIntrfcFileList*/
SELECT
    INTRFC_ID, TRNSC_ID, FILE_ID, FILE_SN, CNTC_FILE_NM, CNTC_ORG_FILE_NM, CNTC_CREAT_DT
FROM
    CAM_ERP.T_IF_INTRFC_FILE
WHERE
    INTRFC_ID = #{INTRFC_ID}
AND
    TRNSC_ID = #{TRNSC_ID}
AND
    FILE_ID = #{FILE_ID}

/*getEtaxList*/
