mapper.select:
/*getCmmnCodeList*/ SELECT * FROM CAM_ERP.T_IFS_ERP_CMMN_CODE WHERE 1 = 1
AND CMMN_CODE_NM LIKE CONCAT('%', #{cmmnCodeNm}, '%')
ORDER BY CMMN_CODE

mapper.insert:
/*insCommCodeObject*/
INSERT INTO CAM_ERP.DJ_CMMN_CODE_OBJ
    (
        CNTC_SN, CMMN_OBJ_A, CMMN_OBJ_B, REG_EMP_SEQ
    )
VALUES
    (
        #{cntcSn}, #{cmmnObjA}, #{cmmnObjB}, #{regEmpSeq}
    )

mapper.select:
/*getPayAppList*/ SELECT A.PAY_APP_DET_SN, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = B.REG_EMP_SEQ) AS EMP_NAME, C.DOC_NO, C.DOC_TITLE, B.PJT_NM, B.PJT_CD, A.BUDGET_NM, A.EVID_TYPE, A.TOT_COST, E.PRUF_SE_CODE, E.PRUF_SE_NO, DATE_FORMAT(E.EXCUT_REQUST_DE, '%Y-%m-%d') AS EXCUT_REQUST_DE, E.EXCUT_SUM_AMOUNT, E.BCNC_CMPNY_NM, E.BCNC_LSFT_NO, E.TRANSFR_ACNUT_SE_CODE, E.SBSACNT_TRFRSN_CN, E.BCNC_ACNUT_NO FROM CAM_MNG.DJ_PAY_APP_DET A LEFT JOIN CAM_MNG.DJ_PAY_APP B ON A.PAY_APP_SN = B.PAY_APP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO C ON B.DOC_ID = C.DOC_ID LEFT JOIN CAM_ERP.T_IFR_EXCUT_REQUST_ERP E ON E.CNTC_SN = A.REQUST_CNTC_SN WHERE B.DOC_STATUS = 100
AND DATE_FORMAT(B.REG_DT, '%Y-%m-%d') BETWEEN #{strDt} AND #{endDt}
AND LEFT(B.PJT_CD, 1) IN ('R', 'S', 'A') AND A.ENARA_SEND_EXCEPT = #{exceptYn}

mapper.update:
/*setEnaraSendExcept*/
UPDATE CAM_MNG.DJ_PAY_APP_DET
SET
    ENARA_SEND_EXCEPT = #{exceptYn}
WHERE
    PAY_APP_DET_SN IN (${payAppDetSn})

/*setRequestKeyPayAppDet*/
UPDATE CAM_MNG.DJ_PAY_APP_DET
SET
    REQUST_CNTC_SN = #{requstCntcSn}
WHERE
    PAY_APP_DET_SN = #{payAppDetSn}

mapper.insert:
/*insExcutExpItmErp*/
INSERT INTO CAM_ERP.T_IFR_EXCUT_EXPITM_ERP
    (
        CNTC_JOB_PROCESS_CODE, INTRFC_ID, TRNSC_ID, FILE_ID, CNTC_CREAT_DT,
        CNTC_TRGET_SYS_CODE, EXCUT_CNTC_ID, ASSTN_TAXITM_CODE, PRDLST_NM,
        EXCUT_TAXITM_CNTC_ID, TAXITM_FNRSC_CNT
    )
VALUES
    (
        #{CNTC_JOB_PROCESS_CODE}, #{INTRFC_ID}, #{TRNSC_ID}, #{FILE_ID}, #{CNTC_CREAT_DT},
        #{CNTC_TRGET_SYS_CODE}, #{EXCUT_CNTC_ID}, #{ASSTN_TAXITM_CODE}, #{PRDLST_NM},
        #{EXCUT_TAXITM_CNTC_ID}, #{TAXITM_FNRSC_CNT}
    )

/*insExcutFnrscErp*/
INSERT INTO CAM_ERP.T_IFR_EXCUT_FNRSC_ERP
    (
        CNTC_JOB_PROCESS_CODE, INTRFC_ID, TRNSC_ID, FILE_ID, CNTC_CREAT_DT,
        CNTC_TRGET_SYS_CODE, EXCUT_CNTC_ID, EXCUT_TAXITM_CNTC_ID, FNRSC_SE_CODE,
        SPLPC, VAT, SUM_AMOUNT
    )
VALUES
    (
        #{CNTC_JOB_PROCESS_CODE}, #{INTRFC_ID}, #{TRNSC_ID}, #{FILE_ID}, #{CNTC_CREAT_DT},
        #{CNTC_TRGET_SYS_CODE}, #{EXCUT_CNTC_ID}, #{EXCUT_TAXITM_CNTC_ID}, #{FNRSC_SE_CODE},
        #{SPLPC}, #{VAT}, #{SUM_AMOUNT}
    )

/*insIntrfcFile*/
INSERT INTO CAM_ERP.T_IF_INTRFC_FILE
    (
        INTRFC_ID, TRNSC_ID, FILE_ID, FILE_SN, CNTC_FILE_NM, CNTC_ORG_FILE_NM, CNTC_CREAT_DT
    )
VALUES
    (
        #{INTRFC_ID}, #{TRNSC_ID}, #{FILE_ID}, #{FILE_SN}, #{CNTC_FILE_NM}, #{CNTC_ORG_FILE_NM}, #{CNTC_CREAT_DT}
    )

mapper.select:
/*getIntrfcFileList*/
SELECT
    INTRFC_ID, TRNSC_ID, FILE_ID, FILE_SN, CNTC_FILE_NM, CNTC_ORG_FILE_NM, CNTC_CREAT_DT
FROM
    CAM_ERP.T_IF_INTRFC_FILE
WHERE
    INTRFC_ID = #{INTRFC_ID}
AND
    TRNSC_ID = #{TRNSC_ID}
AND
    FILE_ID = #{FILE_ID}
