mapper.select:
/*getCmmnCodeList*/ SELECT * FROM CAM_ERP.T_IFS_ERP_CMMN_CODE WHERE 1 = 1
AND CMMN_CODE_NM LIKE CONCAT('%', #{cmmnCodeNm}, '%')
ORDER BY CMMN_CODE

