mapper.select:
/*getPayAppDetailData*/
SELECT PAD.*, FORMAT(TOT_COST , 0) AS TOT_COST_COMMA
FROM CAM_MNG.DJ_PAY_APP_DET PAD
WHERE PAD.PAY_APP_SN = #{payAppSn}

/*getPaymentList*/ SELECT AA.* FROM ( SELECT A.*, B.DOC_NO, C.TOT_COST, C.ITEM_COUNT, (SELECT REQ_END_DE FROM CAM_MNG.DJ_EXNP WHERE PAY_APP_SN = A.PAY_APP_SN ORDER BY EXNP_SN DESC LIMIT 1) AS REQ_END_DE, D.EXNP_STATUS, IFNULL(E.EXNP_DOC_STATUS, 0) AS EXNP_DOC_STATUS, (SELECT DOC_STATUS FROM CAM_MNG.DJ_EXNP WHERE PAY_APP_SN = A.PAY_APP_SN AND REQ_END_DE IS NOT NULL AND REQ_END_DE != "" ORDER BY EXNP_SN DESC LIMIT 1) AS EXNP_DOC_STATUS2, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO HR WHERE HR.EMP_SEQ = A.REG_EMP_SEQ) AS EMP_NAME FROM CAM_MNG.DJ_PAY_APP A LEFT JOIN DJ_CAMTIC.A_DOC_INFO B ON A.DOC_ID = B.DOC_ID LEFT JOIN (SELECT PAY_APP_SN, SUM(TOT_COST) AS TOT_COST, COUNT(*) AS ITEM_COUNT FROM CAM_MNG.DJ_PAY_APP_DET GROUP BY PAY_APP_SN) C ON A.PAY_APP_SN = C.PAY_APP_SN LEFT JOIN (SELECT PAY_APP_SN, COUNT(*) AS EXNP_STATUS FROM CAM_MNG.DJ_PAY_APP_DET WHERE EXNP_SAVE = 'Y' GROUP BY PAY_APP_SN ) D ON A.PAY_APP_SN = D.PAY_APP_SN LEFT JOIN (SELECT Y.PAY_APP_SN, COUNT(*) AS EXNP_DOC_STATUS FROM CAM_MNG.DJ_EXNP_DET X LEFT JOIN CAM_MNG.DJ_EXNP Y ON X.EXNP_SN = Y.EXNP_SN WHERE Y.REQ_END_DE IS NOT NULL AND Y.REQ_END_DE != "" GROUP BY Y.PAY_APP_SN ) E ON A.PAY_APP_SN = E.PAY_APP_SN WHERE
A.APP_DE > #{strDe} and A.APP_DE <= #{endDe}
AND PAY_APP_TYPE = 1
AND PAY_APP_TYPE = 2
AND PAY_APP_TYPE = 3
AND PAY_APP_TYPE = 4
AND A.DOC_STATUS = #{docStatus}
AND A.PJT_CD = #{pjtCd}
AND A.REG_EMP_SEQ = #{regEmpSeq}
) AA WHERE 1=1
AND ITEM_COUNT != IFNULL(EXNP_STATUS, 0)
AND AA.DOC_STATUS = #{searchDept}
ORDER BY AA.PAY_APP_SN DESC

