/*getExnpList*/ SELECT A.*, B.DOC_NO, C.PAY_EXNP_DE, D.TOT_COST, D.BUDGET_NM BUDGET_NM_EX, D.EVID_TYPE EVI_TYPE FROM CAM_MNG.DJ_EXNP A LEFT JOIN DJ_CAMTIC.A_DOC_INFO B ON A.DOC_ID = B.DOC_ID LEFT JOIN (SELECT PAY_APP_SN, PAY_EXNP_DE FROM CAM_MNG.DJ_PAY_APP GROUP BY PAY_APP_SN) C ON A.PAY_APP_SN = C.PAY_APP_SN LEFT JOIN (SELECT EXNP_SN, SUM(TOT_COST) TOT_COST, BUDGET_NM, EVID_TYPE FROM CAM_MNG.DJ_EXNP_DET GROUP BY EXNP_SN) D ON A.EXNP_SN = D.EXNP_SN WHERE
A.REQ_DE > #{strDe} AND A.REQ_DE <= #{endDe}
AND A.PAY_APP_TYPE = #{payAppType}
AND A.PJT_CD = #{pjtCd}
ORDER BY EXNP_SN DESC

/*getExnpReList*/ SELECT CASE WHEN ED.EVID_TYPE IN (1,2,3) THEN '반제(지출)' ELSE '지출' END AS TYPE, E.*, ED.CRM_NM, ED2.CRM_CNT, ED.EVID_TYPE, CASE WHEN ED.EVID_TYPE = 1 THEN '세금계산서' WHEN ED.EVID_TYPE = 2 THEN '계산서' WHEN ED.EVID_TYPE = 3 THEN '신용카드' WHEN ED.EVID_TYPE = 4 THEN '직원지급' WHEN ED.EVID_TYPE = 5 THEN '소득신고자' WHEN ED.EVID_TYPE = 6 THEN '기타' END AS EVID_TYPE_TEXT, CASE WHEN ED.EVID_TYPE IN (1,2,3) THEN E.DT1 ELSE E.DT3 END AS R_DT, ED.BUDGET_NM AS BUDGET_NM_EX, DI.DOC_NO AS DOC_NO, DI.APPRO_KEY AS APPRO_KEY, DI.DOC_MENU_CD AS DOC_MENU_CD, IFNULL(SUM(ED.TOT_COST), 0) AS TOT_COST, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = E.REG_EMP_SEQ) AS REG_EMP_NAME FROM CAM_MNG.DJ_EXNP_DET ED LEFT JOIN CAM_MNG.DJ_EXNP E ON ED.EXNP_SN = E.EXNP_SN LEFT JOIN (SELECT EXNP_SN, COUNT(*) AS CRM_CNT FROM CAM_MNG.DJ_EXNP_DET GROUP BY EXNP_SN) ED2 ON E.EXNP_SN = ED2.EXNP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON E.DOC_ID = DI.DOC_ID WHERE (ED.EVID_TYPE IN (1, 2, 3) or ED.EVID_TYPE NOT IN (1, 2, 3)) AND E.DOC_STATUS = '100' AND E.PAY_APP_TYPE = 1
AND E.PJT_CD = #{pjtCd}
AND E.REQ_DE BETWEEN #{startDt} AND #{endDt}
AND E.RE_STAT = #{searchStatus}
AND E.PJT_NM LIKE CONCAT('%',#{searchValue},'%')
AND ED.BUDGET_NM LIKE CONCAT('%',#{searchValue},'%')
AND ED.CRM_NM LIKE CONCAT('%', #{searchValue} ,'%')
AND E.EXNP_BRIEFS LIKE CONCAT('%',#{searchValue},'%')
AND ( E.PJT_NM LIKE CONCAT('%',#{searchValue},'%') OR ED.CRM_NM LIKE CONCAT('%', #{searchValue} ,'%') OR ED.BUDGET_NM LIKE CONCAT('%',#{searchValue},'%') OR E.EXNP_BRIEFS LIKE CONCAT('%',#{searchValue},'%') )
GROUP BY E.EXNP_SN ORDER BY E.EXNP_SN DESC

/*getExnpReListForExcelDown*/ SELECT E.EXNP_SN, CASE WHEN ED.EVID_TYPE IN (1,2,3) THEN '반제(지출)' ELSE '지출' END AS TYPE, E.PJT_NM, E.PJT_CD, E.EXNP_BRIEFS, E.ACC_NM, E.ACC_NO, E.BNK_NM, ED.CRM_ACC_HOLDER, ED.CRM_ACC_NO, ED.CRM_BNK_NM, ED.CRM_NM, CASE WHEN ED.EVID_TYPE = 1 THEN '세금계산서' WHEN ED.EVID_TYPE = 2 THEN '계산서' WHEN ED.EVID_TYPE = 3 THEN '신용카드' WHEN ED.EVID_TYPE = 4 THEN '직원지급' WHEN ED.EVID_TYPE = 5 THEN '사업소득자' WHEN ED.EVID_TYPE = 6 THEN '기타' WHEN ED.EVID_TYPE = 9 THEN '기타소득자' END AS EVID_TYPE_TEXT, CASE WHEN ED.EVID_TYPE IN (1,2,3) THEN DATE_FORMAT(E.DT1, '%Y-%m-%d') ELSE DATE_FORMAT(E.DT3, '%Y-%m-%d') END AS R_DT, E.REQ_EXNP_DE, ED.BUDGET_NM AS BUDGET_NM_EX, SUBSTRING_INDEX(ED.BUDGET_NM, ' / ', 1) AS BUDGET_NM_1, SUBSTRING_INDEX(SUBSTRING_INDEX(ED.BUDGET_NM, ' / ', -2), ' / ', 1) AS BUDGET_NM_2, SUBSTRING_INDEX(ED.BUDGET_NM, ' / ', -1) as BUDGET_NM_3, ED.TOT_COST, DI.DOC_NO AS DOC_NO, HEI.EMP_NAME_KR AS REG_EMP_NAME, HDI.DEPT_NAME AS REG_DEPT_NAME FROM CAM_MNG.DJ_EXNP_DET ED LEFT JOIN CAM_MNG.DJ_EXNP E ON ED.EXNP_SN = E.EXNP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON E.DOC_ID = DI.DOC_ID LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON E.REG_EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE (ED.EVID_TYPE IN (1, 2, 3) or ED.EVID_TYPE NOT IN (1, 2, 3)) AND E.DOC_STATUS = '100' AND E.PAY_APP_TYPE = 1
AND E.PJT_CD = #{pjtCd}
AND E.REQ_DE BETWEEN #{startDt} AND #{endDt}
AND E.RE_STAT = #{searchStatus}
AND E.PJT_NM LIKE CONCAT('%',#{searchValue},'%')
AND ED.BUDGET_NM LIKE CONCAT('%',#{searchValue},'%')
AND ED.CRM_NM LIKE CONCAT('%', #{searchValue} ,'%')
AND E.EXNP_BRIEFS LIKE CONCAT('%',#{searchValue},'%')
AND ( E.PJT_NM LIKE CONCAT('%',#{searchValue},'%') OR ED.CRM_NM LIKE CONCAT('%', #{searchValue} ,'%') OR ED.BUDGET_NM LIKE CONCAT('%',#{searchValue},'%') OR E.EXNP_BRIEFS LIKE CONCAT('%',#{searchValue},'%') )
ORDER BY E.EXNP_SN DESC

/* getIncpList */ SELECT A.*, IFNULL(D.EMP_NAME_KR, '') AS EMP_NAME, B.DOC_NO, C.TOT_COST FROM CAM_MNG.DJ_PAY_INCP A LEFT JOIN DJ_CAMTIC.A_DOC_INFO B ON A.DOC_ID = B.DOC_ID LEFT JOIN ( SELECT PAY_INCP_SN, SUM(TOT_COST) AS TOT_COST FROM CAM_MNG.DJ_PAY_INCP_DET GROUP BY PAY_INCP_SN ) C ON A.PAY_INCP_SN = C.PAY_INCP_SN LEFT JOIN CAM_HR.DJ_EMP_INFO D ON A.EXNP_EMP_SEQ = D.EMP_SEQ WHERE
A.APP_DE > #{startDt} AND A.APP_DE <= #{endDt}
ORDER BY A.PAY_INCP_SN DESC

/*getIncpReList*/ SELECT * FROM ( SELECT I.*, DI.APPRO_KEY AS APPRO_KEY, DI.DOC_MENU_CD AS DOC_MENU_CD, IFNULL(SUM(ID.TOT_COST), 0) AS TOT_COST, '' AS PAY_INCP_RE_SN, '' AS RE_APP_DE, HEI.EMP_NAME_KR AS EMP_NAME FROM CAM_MNG.DJ_PAY_INCP_DET ID LEFT JOIN CAM_MNG.DJ_PAY_INCP I ON ID.PAY_INCP_SN = I.PAY_INCP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON I.DOC_ID = DI.DOC_ID LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HEI.EMP_SEQ = I.EXNP_EMP_SEQ WHERE I.DOC_STATUS = '100' AND ID.EVID_TYPE NOT IN (1, 3, 5, 6) GROUP BY I.PAY_INCP_SN UNION ALL SELECT B.PAY_INCP_SN, A.PJT_NM, A.PJT_SN, A.PJT_CD, B.BUDGET_SN, B.BUDGET_NM, A.APP_DE, A.PAY_EXNP_DE, A.BUSN_CD, A.BUSN_EX_CD, A.EXNP_EMP_SEQ, A.G20_EMP_CD, A.G20_DEPT_CD, A.APP_CONT, A.BNK_SN, A.ACC_NM, A.ACC_NO, A.BNK_NM, A.PAY_APP_STAT, A.GUBUN, A.DOC_ID, A.DOC_STATUS, A.APPROVAL_EMP_SEQ, A.APPROVAL_DATE, A.PMR_NO, A.REG_DT, A.REG_EMP_SEQ, A.MOD_DT, A.MOD_EMP_SEQ, B.RE_STAT, A.INFO_CODE, DI.APPRO_KEY AS APPRO_KEY, DI.DOC_MENU_CD AS DOC_MENU_CD, IFNULL(B.TOT_COST, 0) AS TOT_COST, B.PAY_INCP_RE_SN, B.RE_APP_DE, HEI.EMP_NAME_KR AS EMP_NAME FROM CAM_MNG.DJ_PAY_INCP_RE B LEFT JOIN CAM_MNG.DJ_PAY_INCP A ON B.PAY_INCP_SN = A.PAY_INCP_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON A.DOC_ID = DI.DOC_ID LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HEI.EMP_SEQ = A.EXNP_EMP_SEQ) AB WHERE
AB.APP_DE > #{startDt} AND AB.APP_DE <= #{endDt}
ORDER BY AB.REG_DT DESC

