/* updatePurcFinalApprStat */
UPDATE CAM_MNG.DJ_MNG_PURC
SET
    STATUS = 'C',
    DOC_STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    PURC_SN = #{purcSn}

/* updatePurcListFinalApprStat */
UPDATE CAM_MNG.DJ_MNG_PURC_ITEM
