    STATUS = 'C'
WHERE
    PURC_SN = #{purcSn}

/* updateClaimApprStat */
UPDATE CAM_MNG.DJ_PURC_CLAIM
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    CLAIM_SN = #{claimSn}

/* updateClaimFinalApprStat */
UPDATE CAM_MNG.DJ_PURC_CLAIM
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    CLAIM_SN = #{claimSn}

/*updPurcItemStat*/
