mapper.select:
/*getPurcClaimData*/ SELECT A.*, EI.EMP_SEQ, EI.EMP_NAME_KR, EI.POSITION_NAME, EI.DEPT_SEQ, DI.DOC_NO, CAM_HR.FN_GetName('DEPT', EI.DEPT_SEQ, NULL) AS DEPT_NAME, DI.APPRO_KEY, DI.DOC_MENU_CD, (CASE WHEN A.ORDER_DT IS NOT NULL THEN 'Y' ELSE 'N' END) AS ORDER_CK FROM CAM_MNG.DJ_PURC_CLAIM A LEFT JOIN CAM_HR.DJ_EMP_INFO EI ON A.CLAIM_EMP_SEQ = EI.EMP_SEQ LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON A.DOC_ID = DI.DOC_ID WHERE 1=1
AND PURC_SN = NULL
AND CLAIM_SN = #{claimSn}
AND PURC_SN = #{purcSn}

mapper.update:
/*updPurcClaimData*/ UPDATE CAM_MNG.DJ_PURC_CLAIM SET
PURC_SN = #{purcSn},
PURC_SN = NULL,
CLAIM_EMP_SEQ = #{loginEmpSeq}, PURC_EMP_SEQ = #{purcEmpSeq}, PURC_TYPE = #{purcType}, EXP_TYPE = #{expType}, PJT_SN = #{pjtSn}, PJT_NM = #{pjtNm}, CLAIM_DE = #{claimDe}, EXP_DE = #{expDe}, CLAIM_TITLE = #{claimTitle}, PURC_REQ_PURPOSE = #{purcReqPurpose}, CRM_SN = #{crmSn}, CRM_NM = #{crmNm}, VAT = #{vat}, EST_AMT = #{estAmt}, VAT_AMT = #{vatAmt}, TOT_AMT = #{totAmt} WHERE CLAIM_SN = #{claimSn}

mapper.select:
/*getPurcProductList*/ SELECT TOT.*, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE ACTIVE = "Y" AND PRODUCT_GROUP_CODE_ID = 3 AND PARENT_CODE = TOT.PRODUCT_B AND PARENT_CODE_NM = TOT.PD_B AND PRODUCT_DT_CODE = TOT.PRODUCT_C) AS PD_C FROM ( SELECT A.*, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE PRODUCT_DT_CODE = A.PRODUCT_A AND PRODUCT_GROUP_CODE_ID = 1) AS PD_A, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE PARENT_CODE = A.PRODUCT_A AND PRODUCT_DT_CODE = A.PRODUCT_B AND PRODUCT_GROUP_CODE_ID = 2) AS PD_B, B.CRM_NM FROM CAM_MNG.DJ_MNG_PURC_ITEM A LEFT JOIN CAM_MNG.DJ_PURC_CLAIM B ON A.PURC_SN = B.PURC_SN WHERE B.PURC_SN IS NOT NULL AND B.STATUS = 100 ) TOT WHERE 1=1
AND ${searchKeyword} LIKE CONCAT('%',#{searchValue},'%')
AND (PURC_ITEM_NAME LIKE CONCAT('%',#{searchValue},'%') OR PURC_ITEM_UNIT LIKE CONCAT('%',#{searchValue},'%') OR PURC_ITEM_STD LIKE CONCAT('%',#{searchValue},'%') OR CRM_NM LIKE CONCAT('%',#{searchValue},'%'))
AND PRODUCT_A = #{productA}
AND PRODUCT_B = #{productB}
AND PRODUCT_C = #{productC}
AND PURC_ITEM_TYPE = #{purcItemType}

