mapper.select:
/*getPurcClaimData*/ SELECT A.*, B.EMAIL AS CRM_EMAIL, B.TEL_NUM AS CRM_TEL_NUM, B.FAX AS CRM_FAX_NUM, EI.EMP_SEQ, EI.EMP_NAME_KR, EI.POSITION_NAME, EI.DEPT_SEQ, EI.OFFICE_TEL_NUM, EI.MOBILE_TEL_NUM, EI.EMAIL_ADDR, EI2.EMP_SEQ AS CLAIM_EMP_SEQ, EI2.EMP_NAME_KR AS CLAIM_EMP_NAME, EI2.POSITION_NAME AS CLAIM_POSITION_NAME, EI2.OFFICE_TEL_NUM AS CLAIM_OFFICE_TEL_NUM, EI2.MOBILE_TEL_NUM AS CLAIM_MOBILE_TEL_NUM, EI2.EMAIL_ADDR AS CLAIM_EMAIL_ADDR, DI.DOC_NO, CONCAT((SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = EI.DEPT_SEQ)), ' ', (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = EI.DEPT_SEQ)) AS DEPT_NAME, CONCAT((SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = EI2.DEPT_SEQ)), ' ', (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = EI2.DEPT_SEQ)) AS CLAIM_DEPT_NAME, DI.APPRO_KEY, DI.DOC_MENU_CD, (CASE WHEN A.ORDER_DT IS NOT NULL THEN 'Y' ELSE 'N' END) AS ORDER_CK, (SELECT COUNT(*) FROM CAM_MNG.DJ_PURC_CLAIM_ITEM WHERE CLAIM_SN = A.CLAIM_SN) AS CLAIM_ITEM_CNT FROM CAM_MNG.DJ_PURC_CLAIM A LEFT JOIN CAM_HR.DJ_EMP_INFO EI ON A.PURC_EMP_SEQ = EI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO EI2 ON A.CLAIM_EMP_SEQ = EI2.EMP_SEQ LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON A.DOC_ID = DI.DOC_ID LEFT JOIN CAM_CRM.DJ_CRM_INFO B ON A.CRM_SN = B.CRM_SN WHERE 1=1
AND PURC_SN = NULL
AND CLAIM_SN = #{claimSn}
AND PURC_SN = #{purcSn}

/*getClaimExnpGroupIdx*/
SELECT * FROM CAM_MNG.DJ_CLAIM_EXNP
WHERE
    CLAIM_EXNP_SN = #{claimExnpSn}

mapper.update:
/*updPurcClaimData*/ UPDATE CAM_MNG.DJ_PURC_CLAIM SET
PURC_SN = #{purcSn},
PURC_SN = NULL,
CLAIM_EMP_SEQ = #{loginEmpSeq}, PURC_EMP_SEQ = #{purcEmpSeq}, PURC_TYPE = #{purcType}, EXP_TYPE = #{expType}, PJT_SN = #{pjtSn}, PJT_NM = #{pjtNm}, PAYMENT_METHOD = #{paymentMethod}, PURC_LINK = #{purcLink}, CLAIM_DE = #{claimDe}, EXP_DE = #{expDe}, CLAIM_TITLE = #{claimTitle}, PURC_REQ_PURPOSE = #{purcReqPurpose}, CRM_SN = #{crmSn}, CRM_NM = #{crmNm}, PRI_PAY = #{priPay}, CONT_YN = #{contYn}, DISCOUNT_AMT = #{discountAmt}, VAT = #{vat}, EST_AMT = #{estAmt}, VAT_AMT = #{vatAmt}, CHECK_PROFIT = #{checkProfit}, TOT_AMT = #{totAmt} WHERE CLAIM_SN = #{claimSn}

mapper.select:
/*getPurcProductList*/ SELECT TOT.*, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE ACTIVE = "Y" AND PRODUCT_GROUP_CODE_ID = 3 AND PARENT_CODE = TOT.PRODUCT_B AND PARENT_CODE_NM = TOT.PD_B AND PRODUCT_DT_CODE = TOT.PRODUCT_C) AS PD_C FROM ( SELECT A.*, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE PRODUCT_DT_CODE = A.PRODUCT_A AND PRODUCT_GROUP_CODE_ID = 1) AS PD_A, (SELECT PRODUCT_DT_CODE_NM FROM CAM_PJT_MNG.DJ_PRODUCT_CODE WHERE PARENT_CODE = A.PRODUCT_A AND PRODUCT_DT_CODE = A.PRODUCT_B AND PRODUCT_GROUP_CODE_ID = 2) AS PD_B, B.CRM_NM FROM CAM_MNG.DJ_MNG_PURC_ITEM A LEFT JOIN CAM_MNG.DJ_PURC_CLAIM B ON A.PURC_SN = B.PURC_SN WHERE B.PURC_SN IS NOT NULL AND B.STATUS = 100 ) TOT WHERE 1=1
AND ${searchKeyword} LIKE CONCAT('%',#{searchValue},'%')
AND (PURC_ITEM_NAME LIKE CONCAT('%',#{searchValue},'%') OR PURC_ITEM_UNIT LIKE CONCAT('%',#{searchValue},'%') OR PURC_ITEM_STD LIKE CONCAT('%',#{searchValue},'%') OR CRM_NM LIKE CONCAT('%',#{searchValue},'%'))
AND PRODUCT_A = #{productA}
AND PRODUCT_B = #{productB}
AND PRODUCT_C = #{productC}
AND PURC_ITEM_TYPE = #{purcItemType}

