mapper.select:
/*getClaimMngList*/ SELECT A.*, B.CLAIM_EXNP_SN, IFNULL((SELECT SUM(REQ_AMT) FROM CAM_MNG.DJ_CLAIM_EXNP WHERE CLAIM_SN = A.CLAIM_SN), 0) AS REQ_AMT, IFNULL((SELECT SUM(EXNP_AMT) FROM CAM_MNG.DJ_CLAIM_EXNP WHERE CLAIM_SN = A.CLAIM_SN), 0) AS EXNP_AMT, REPLACE((SELECT PATH_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT DEPT_SEQ FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = A.PURC_EMP_SEQ)), '|', ' ') AS DEPT_NAME, (SELECT DOC_NO FROM DJ_CAMTIC.A_DOC_INFO WHERE DOC_ID = A.DOC_ID) AS DOC_NO, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = A.PURC_EMP_SEQ) AS PURC_EMP_NAME FROM CAM_MNG.DJ_PURC_CLAIM A LEFT JOIN CAM_MNG.DJ_CLAIM_EXNP B ON A.CLAIM_SN = B.CLAIM_SN WHERE A.CLAIM_SN IN
#{item}
GROUP BY A.CLAIM_SN ORDER BY CLAIM_SN DESC

