mapper.select:
/* campus.getCodeOne */
SELECT
    *
FROM
    CAMPUS.DJ_CAMPUS_CODE CC
WHERE
    ACTIVE = 'Y'

        AND CAMPUS_CODE_ID = #{campusCodeId}


        AND CAMPUS_GROUP_CODE_ID = #{campusGroupCodeId} AND CAMPUS_DT_CODE = #{pk}

/* getRealEduTimeYear */
SELECT
    IFNULL(SUM(ER.REAL_EDU_TIME), 0) AS REAL_EDU_TIME,
    CAST(IFNULL(SUM(ER.REAL_EDU_TIME), 0) AS CHAR(10)) AS REAL_EDU_TIME2
FROM
    CAMPUS.DJ_EDU_INFO EI
LEFT JOIN
    CAMPUS.DJ_EDU_RESULT ER ON EI.EDU_INFO_ID = ER.EDU_INFO_ID
WHERE
    LEFT(EI.START_DT, 4) = #{eduYear}
AND EI.EDU_FORM_TYPE = #{eduFormType}
AND EI.REG_EMP_SEQ = #{empSeq}
AND ER.MNG_CHECK = 'Y'

/* getRealOjtTimeStudyToday */
SELECT
    IFNULL(SUM(REAL_OJT_TIME), 0) AS STUDY_TIME
FROM
    CAMPUS.DJ_OJT_USER
WHERE
    OJT_EMP_SEQ = #{empSeq}
AND OJT_RESULT_DT = #{applyDt}

/* getRealOjtOneTimeStudyToday */
SELECT
    IFNULL(SUM(REAL_OJT_TIME), 0) AS STUDY_ONE_TIME
FROM
    CAMPUS.DJ_OJT_USER
WHERE
    OJT_EMP_SEQ = #{empSeq}
  AND STUDY_INFO_SN = #{studyInfoSn}

/* getEduInfoList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, EI.EDU_INFO_ID, EI.REG_EMP_SEQ, EI.REG_EMP_NAME, EI.EDU_NAME, EI.START_DT, EI.END_DT, EI.DUTY_CLASS, CASE WHEN EI.CARE_LOCATION IS NULL THEN CARE_NAME ELSE EI.CARE_LOCATION END AS CARE_LOCATION, CONCAT('Level. ', EI.LEVEL_ID) AS LEVEL_ID, EI.EDU_FORM_TYPE, EI.STATUS, EI.TERM_TIME, IFNULL(ER.STATUS, 1) AS RES_STATUS, ER.MNG_CHECK, EI.OBJECT_FORUM_TYPE, IFNULL(EI.BOOK_PAGE_VAL, 0) AS BOOK_PAGE_VAL, EI.TREA_UNIT, EI.TREA_TYPE, EI.TREA_USER, EI.BOOK_UNIT, HEI.POSITION_NAME, IFNULL(ER.REAL_EDU_TIME, 0) AS REAL_EDU_TIME FROM CAMPUS.DJ_EDU_INFO EI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HEI.EMP_SEQ = EI.REG_EMP_SEQ LEFT JOIN CAMPUS.DJ_EDU_RESULT ER ON EI.EDU_INFO_ID = ER.EDU_INFO_ID, (SELECT @ROWNUM := 0) TMP WHERE 1=1 AND EI.ACTIVE='Y'
AND EI.REG_EMP_SEQ = #{empSeq}
AND EI.DEPT_SEQ = #{deptComp}
OR EI.DEPT_SEQ IN ( SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = #{deptComp} OR PARENT_DEPT_SEQ = #{deptComp} )
AND EI.DEPT_SEQ = #{deptTeam}
AND EDU_FORM_TYPE = #{studyClass}
AND EI.REG_EMP_NAME like concat('%', #{kindContent} ,'%')
AND EI.STATUS = #{status}
AND ER.MNG_CHECK = #{mngCheck}
AND HEI.POSITION_NAME = #{position}
AND HEI.POSITION_NAME = #{duty}
AND (DATE_FORMAT(START_DT, '%Y') = #{eduYear}) ORDER BY EI.EDU_INFO_ID ) RE
WHERE RE.RES_STATUS = #{resStatus}
ORDER BY RE.EDU_INFO_ID DESC

/* getEduCategoryList */ SELECT EC.EDU_CATEGORY_ID, EC.EDU_CATEGORY_NAME, EC.LARGE_CATEGORY_ID, EC.LARGE_CATEGORY_NAME FROM CAMPUS.DJ_EDU_CATEGORY EC WHERE EC.ACTIVE = 'Y'
AND EC.LARGE_CATEGORY_ID in (${largeCategoryId})
ORDER BY EC.LARGE_CATEGORY_ID, EC.ORDER

/* getEduCategoryDetailList */ SELECT ECD.EDU_CATEGORY_DETAIL_ID, ECD.EDU_CATEGORY_DETAIL_NAME, ECD.EDU_CATEGORY_ID, ECD.EDU_CATEGORY_NAME, ECD.LEVEL_ID, ECD.LEVEL_NAME FROM CAMPUS.DJ_EDU_CATEGORY_DETAIL ECD WHERE ECD.ACTIVE = 'Y'
AND ECD.LEVEL_ID = #{levelId}
AND ECD.EDU_CATEGORY_ID in (${eduCategoryId})
ORDER BY ECD.ORDER

/* getTargetCategoryList */ SELECT EC.EDU_CATEGORY_ID, EC.EDU_CATEGORY_NAME FROM CAMPUS.DJ_EDU_CATEGORY EC WHERE EC.EDU_CATEGORY_ID IN ( SELECT EDU_CATEGORY_ID FROM CAMPUS.DJ_EDU_TARGET ET LEFT JOIN CAMPUS.DJ_EDU_TARGET_DETAIL ETD ON ET.EDU_TARGET_ID = ETD.EDU_TARGET_ID LEFT JOIN CAMPUS.DJ_EDU_CATEGORY_DETAIL ECD ON ETD.EDU_CATEGORY_DETAIL_ID = ECD.EDU_CATEGORY_DETAIL_ID WHERE ET.TARGET_YEAR = #{targetYear} AND ET.REG_EMP_SEQ = #{empSeq}
AND ETD.DUTY_CLASS = #{dutyClass}
)

/* getEduPlanList */
SELECT
    EP.EDU_PLAN_ID,
    EP.EDU_CATEGORY_ID,
    EP.EDU_PLAN
FROM
    CAMPUS.DJ_EDU_PLAN EP
WHERE
    TARGET_YEAR = #{targetYear}
AND REG_EMP_SEQ = #{empSeq}
AND DUTY_CLASS = #{dutyClass}

/* getEduPlanOne */
SELECT
    EP.EDU_PLAN_ID,
    EP.EDU_PLAN
FROM
    CAMPUS.DJ_EDU_PLAN EP
WHERE
    EDU_CATEGORY_ID = #{eduCategoryId}
AND TARGET_YEAR = #{targetYear}
AND DUTY_CLASS = #{dutyClass}
AND REG_EMP_SEQ = #{empSeq}

/* getStudyInfoList */ SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, RE.* FROM ( SELECT CSI.*, IFNULL((SELECT SUM(JOURNAL_TIME) FROM CAMPUS.DJ_STUDY_JOURNAL SCSJ WHERE CSI.STUDY_INFO_SN = SCSJ.STUDY_INFO_SN AND FIND_IN_SET(#{empSeq}, SCSJ.STUDY_EMP_SEQ) AND CAPTAIN_APPOVAL_YN = 'Y' AND ASSISTANT_APPOVAL_YN = 'Y'), 0) AS EDU_TIME_TOTAL, (SELECT COUNT(*) FROM CAMPUS.DJ_OJT_RESULT WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN) as ST_CNT, (SELECT SUM(EDU_FULL_TIME) FROM CAMPUS.DJ_OJT_RESULT WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN) as ST_SUM, (SELECT STUDY_EMP_NAME FROM CAMPUS.DJ_STUDY_USER WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN AND STUDY_CLASS_SN = 4 LIMIT 1) as OJT_READER, (SELECT STUDY_EMP_NAME FROM CAMPUS.DJ_STUDY_USER WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN AND STUDY_CLASS_SN = 1 LIMIT 1) as STUDY_READER, (SELECT SUM(EDU_TIME) FROM CAMPUS.DJ_STUDY_PROPAG WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN) AS PROPAG_SUM, (SELECT COUNT(*) FROM CAMPUS.DJ_STUDY_USER WHERE STUDY_INFO_SN = CSI.STUDY_INFO_SN) - 1 AS COUNT_STUDY_MEMBER, IFNULL((SELECT SUM(STUDY_TIME) FROM CAMPUS.DJ_STUDY_RESULT_USER SRU LEFT JOIN CAMPUS.DJ_STUDY_JOURNAL SJ ON SJ.STUDY_JOURNAL_SN = SRU.STUDY_JOURNAL_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SJ.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SRU.STUDY_INFO_SN = CSI.STUDY_INFO_SN AND SJ.ACTIVE = 'Y' AND SSI.ADD_STATUS = 'S' AND SRU.STUDY_EMP_SEQ = #{empSeq} GROUP BY SRU.STUDY_EMP_SEQ ), 0) AS STUDY_TIME, IFNULL((SELECT SUM(REAL_EDU_TIME) FROM CAMPUS.DJ_PROPAG_USER SP LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SP.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SP.STUDY_INFO_SN = CSI.STUDY_INFO_SN AND SSI.ADD_STATUS = 'S' AND PROPAG_EMP_SEQ = #{empSeq} GROUP BY PROPAG_EMP_SEQ ), 0) AS PROPAG_TIME, IFNULL((SELECT SUM(REAL_OJT_TIME) FROM CAMPUS.DJ_OJT_USER SOU LEFT JOIN CAMPUS.DJ_OJT_RESULT SOR ON SOR.OJT_RESULT_SN = SOU.OJT_RESULT_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SOR.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SOU.STUDY_INFO_SN = CSI.STUDY_INFO_SN AND SOR.ACTIVE = 'Y' AND SSI.ADD_STATUS = 'S' AND OJT_EMP_SEQ = #{empSeq} GROUP BY OJT_EMP_SEQ ), 0) AS OJT_TIME FROM CAMPUS.DJ_STUDY_INFO CSI, (SELECT @ROWNUM := 0) TMP WHERE 1=1 AND ACTIVE = 'Y'
AND (DATE_FORMAT(START_DT, '%Y') = #{applyYear} OR DATE_FORMAT(END_DT, '%Y') = #{applyYear})
AND STUDY_CLASS_SN = #{studyClass}
) RE
WHERE RE.STUDY_INFO_SN IN ( SELECT STUDY_INFO_SN FROM CAMPUS.DJ_STUDY_USER WHERE STUDY_EMP_SEQ = #{empSeq} )
ORDER BY ROW_NUM DESC

/* getOjtOjtResultSnOne */
SELECT
    OJT_OJT_RESULT_SN
FROM CAMPUS.dj_ojt_ojt_result
WHERE STUDY_INFO_SN = #{pk}

/* getOjtResultInfoOne */
SELECT
    *
FROM CAMPUS.dj_ojt_ojt_result
WHERE STUDY_INFO_SN = #{pk}

/* getStudyUserList */
SELECT
    CSU.*
FROM
    CAMPUS.DJ_STUDY_USER CSU
WHERE STUDY_INFO_SN = #{pk}

    AND STUDY_CLASS_SN = #{studyClassSn}

ORDER BY STUDY_USER_SN

/* getStudyJournalList */
SELECT
    @ROWNUM := @ROWNUM + 1 AS ROW_NUM,
    CSJ.*
FROM
    CAMPUS.DJ_STUDY_JOURNAL CSJ,
    (SELECT @ROWNUM := 0) TMP
WHERE CSJ.STUDY_INFO_SN = #{studyInfoSn}
ORDER BY CSJ.JOURNAL_DT DESC

/* getStudyPropagUserInfo */
SELECT
    CPU.*
FROM
    CAMPUS.DJ_PROPAG_USER CPU
WHERE STUDY_PROPAG_SN = #{studyPropagSn}
  AND PROPAG_CLASS_SN = #{propagClassSn}
ORDER BY  PROPAG_USER_SN

/* getStudyOjtUserInfo */
SELECT
    COU.*
FROM
    CAMPUS.DJ_OJT_USER COU
WHERE OJT_RESULT_SN = #{ojtResultSn}
  AND OJT_CLASS_SN = #{ojtClassSn}
ORDER BY  OJT_USER_SN

/* getStudyPropagUserList */
SELECT
    PU.*
FROM
    CAMPUS.DJ_PROPAG_USER PU
WHERE PU.STUDY_PROPAG_SN = #{studyPropagSn}

/* getOjtPlanOne */
SELECT
    *
FROM
    CAMPUS.DJ_OJT_PLAN COP
WHERE COP.OJT_PLAN_SN = #{ojtPlanSn}

/* getOjtResultOne */
SELECT
    *
FROM
    CAMPUS.DJ_OJT_RESULT COR
WHERE COR.OJT_RESULT_SN = #{ojtPlanSn}

/* getOpenStudyInfoList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, COSI.*, IFNULL((SELECT SCOU.REG_EMP_NAME FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN limit 1), '') AS MEMBER, IFNULL((SELECT COUNT(SCOU.REG_EMP_NAME)-1 FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN), 0) AS MEMBER_COUNT, IFNULL((SELECT COUNT(SCOU.REG_EMP_NAME) FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN), 0) AS MEMBER_COUNT_TOTAL, IFNULL((SELECT SCOU.REAL_EDU_TIME FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN AND PART_YN = 'Y' AND REG_EMP_SEQ = #{empSeq}), '-') AS REAL_EDU_TIME FROM CAMPUS.DJ_OPENSTUDY_INFO COSI, (SELECT @ROWNUM := 0) TMP WHERE 1=1 AND ACTIVE = 'Y'
AND STEP = #{step}
AND DATE_FORMAT(COSI.open_study_dt, '%Y') = #{eduYear}
AND ( COSI.REG_EMP_SEQ = #{empSeq} or COSI.OPEN_STUDY_INFO_SN IN (SELECT OPEN_STUDY_INFO_SN FROM CAMPUS.DJ_OPENSTUDY_USER WHERE REG_EMP_SEQ = #{empSeq} AND PART_YN = 'Y') ) ORDER BY COSI.REG_DT ) RE ORDER BY RE.OPEN_STUDY_INFO_SN DESC

/* getOpenStudyInfoAdminList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, COSI.*, IFNULL((SELECT SCOU.REG_EMP_NAME FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN limit 1), '') AS MEMBER, IFNULL((SELECT COUNT(SCOU.REG_EMP_NAME)-1 FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN), 0) AS MEMBER_COUNT, IFNULL((SELECT COUNT(SCOU.REG_EMP_NAME) FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN), 0) AS MEMBER_COUNT_TOTAL, IFNULL((SELECT SCOU.REAL_EDU_TIME FROM CAMPUS.DJ_OPENSTUDY_USER SCOU WHERE COSI.OPEN_STUDY_INFO_SN = OPEN_STUDY_INFO_SN AND PART_YN = 'Y' AND REG_EMP_SEQ = #{empSeq}), '-') AS REAL_EDU_TIME FROM CAMPUS.DJ_OPENSTUDY_INFO COSI, (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND DATE_FORMAT(COSI.OPEN_STUDY_DT, '%Y%m') <= DATE_FORMAT(NOW(), '%Y%m') AND DATE_FORMAT(NOW(), '%Y%m') <= DATE_FORMAT(COSI.OPEN_STUDY_DT, '%Y%m')
AND STEP = #{step}
AND DATE_FORMAT(COSI.open_study_dt, '%Y') = #{eduYear}
ORDER BY COSI.REG_DT ) RE ORDER BY RE.OPEN_STUDY_INFO_SN DESC

/* getCommonEduList */ SELECT CE.*, (SELECT COUNT(*) FROM CAMPUS.DJ_COMMON_EDU_USER SCEU WHERE SCEU.COMMON_EDU_SN = CE.COMMON_EDU_SN AND PART_YN = 'Y' AND ACTIVE = 'Y') AS PART_COUNT, (SELECT COUNT(*) FROM CAMPUS.DJ_COMMON_EDU_USER SCEU WHERE SCEU.COMMON_EDU_SN = CE.COMMON_EDU_SN AND PART_YN = 'N' AND ACTIVE = 'Y') AS NO_PART_COUNT FROM CAMPUS.DJ_COMMON_EDU CE LEFT JOIN CAMPUS.DJ_COMMON_EDU_USER CEU ON CE.COMMON_EDU_SN = CEU.COMMON_EDU_SN WHERE 1=1
AND (DATE_FORMAT(CE.START_DT, '%Y') = #{eduYear} OR DATE_FORMAT(CE.END_DT, '%Y') = #{eduYear})
AND CEU.REG_EMP_SEQ = #{empSeq}
AND CEU.ACTIVE = 'Y' GROUP BY CE.COMMON_EDU_SN ORDER BY CE.COMMON_EDU_SN DESC

/* getCommonEduMngList */ SELECT CE.*, (SELECT COUNT(*) FROM CAMPUS.DJ_COMMON_EDU_USER SCEU WHERE SCEU.COMMON_EDU_SN = CE.COMMON_EDU_SN AND PART_YN = 'Y' AND ACTIVE = 'Y') AS PART_COUNT, (SELECT COUNT(*) FROM CAMPUS.DJ_COMMON_EDU_USER SCEU WHERE SCEU.COMMON_EDU_SN = CE.COMMON_EDU_SN AND PART_YN = 'N' AND ACTIVE = 'Y') AS NO_PART_COUNT FROM CAMPUS.DJ_COMMON_EDU CE WHERE 1=1
AND (DATE_FORMAT(CE.START_DT, '%Y') = #{eduYear} OR DATE_FORMAT(CE.END_DT, '%Y') = #{eduYear})
ORDER BY CE.COMMON_EDU_SN DESC

/* getEduAllStatList */
SELECT CASE WHEN HDI.DEPT_LEVEL = 2 THEN CONCAT((SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ), " ",(SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.DEPT_SEQ = DI.DEPT_SEQ)) ELSE HEI.DEPT_NAME END AS DEPT, CASE WHEN HEI.DUTY_NAME = "" THEN HEI.POSITION_NAME ELSE HEI.DUTY_NAME END AS POSITION, HEI.POSITION_NAME, HEI.DUTY_NAME , HEI.EMP_NAME_KR AS EMP_NAME, IFNULL(( SELECT SUM(REAL_EDU_TIME) FROM CAMPUS.DJ_EDU_RESULT SER WHERE SER.REG_EMP_SEQ = HEI.EMP_SEQ AND SER.ACTIVE = 'Y' AND SER.STATUS = 100 AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y%m%d') ), 0) AS PERSONAL_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_EDU_RESULT SER WHERE SER.REG_EMP_SEQ = HEI.EMP_SEQ AND SER.ACTIVE = 'Y' AND SER.STATUS = 100 AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y%m%d') ) AS PERSONAL_COUNT, IFNULL(( SELECT SUM(STUDY_TIME) FROM CAMPUS.DJ_STUDY_JOURNAL SJ LEFT JOIN CAMPUS.DJ_STUDY_RESULT_USER SRU ON SJ.STUDY_JOURNAL_SN = SRU.STUDY_JOURNAL_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SJ.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SRU.STUDY_EMP_SEQ = HEI.EMP_SEQ AND SJ.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SJ.JOURNAL_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SJ.JOURNAL_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ), 0) AS STUDY_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_STUDY_JOURNAL SJ LEFT JOIN CAMPUS.DJ_STUDY_RESULT_USER SRU ON SJ.STUDY_JOURNAL_SN = SRU.STUDY_JOURNAL_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SJ.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SRU.STUDY_EMP_SEQ = HEI.EMP_SEQ AND SJ.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SJ.JOURNAL_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SJ.JOURNAL_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ) AS STUDY_COUNT, IFNULL(( SELECT SUM(REAL_EDU_TIME) FROM CAMPUS.DJ_STUDY_PROPAG SP LEFT JOIN CAMPUS.DJ_PROPAG_USER SPU ON SP.STUDY_PROPAG_SN = SPU.STUDY_PROPAG_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SP.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SPU.PROPAG_EMP_SEQ = HEI.EMP_SEQ AND SP.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SP.PROPAG_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SP.PROPAG_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ), 0) AS PROPAG_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_STUDY_PROPAG SP LEFT JOIN CAMPUS.DJ_PROPAG_USER SPU ON SP.STUDY_PROPAG_SN = SPU.STUDY_PROPAG_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SP.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SPU.PROPAG_EMP_SEQ = HEI.EMP_SEQ AND SP.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SP.PROPAG_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SP.PROPAG_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ) AS PROPAG_COUNT, IFNULL(( SELECT SUM(CASE WHEN OJT_CLASS_SN = 4 THEN SOR.EDU_TIME ELSE SOR.EDU_TIME_HALF END) FROM CAMPUS.DJ_OJT_USER SOU LEFT JOIN CAMPUS.DJ_OJT_RESULT SOR ON SOR.OJT_RESULT_SN = SOU.OJT_RESULT_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SOR.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SOU.OJT_EMP_SEQ = HEI.EMP_SEQ AND SOR.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SOR.OJT_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SOR.OJT_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ), 0) AS OJT_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_OJT_USER SOU LEFT JOIN CAMPUS.DJ_OJT_RESULT SOR ON SOR.OJT_RESULT_SN = SOU.OJT_RESULT_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SOR.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SOU.OJT_EMP_SEQ = HEI.EMP_SEQ AND SOR.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SOR.OJT_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SOR.OJT_DT, '%Y%m%d') AND SSI.ADD_STATUS = 'S' ) AS OJT_COUNT, IFNULL(( SELECT SUM(EDU_TIME) FROM CAMPUS.DJ_OPENSTUDY_INFO COI LEFT JOIN CAMPUS.DJ_OPENSTUDY_USER COU ON COI.OPEN_STUDY_INFO_SN = COU.OPEN_STUDY_INFO_SN WHERE COU.REG_EMP_SEQ = HEI.EMP_SEQ AND COI.STATUS = 100 AND COU.PART_YN = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y%m%d') ), 0) AS OPEN_STUDY_TIME, ( SELECT COUNT(EDU_TIME) FROM CAMPUS.DJ_OPENSTUDY_INFO COI LEFT JOIN CAMPUS.DJ_OPENSTUDY_USER COU ON COI.OPEN_STUDY_INFO_SN = COU.OPEN_STUDY_INFO_SN WHERE COU.REG_EMP_SEQ = HEI.EMP_SEQ AND COI.STATUS = 100 AND COU.PART_YN = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y%m%d') ) AS OPEN_STUDY_COUNT, IFNULL(( SELECT SUM(CCEU.REAR_EDU_TIME) FROM CAMPUS.DJ_COMMON_EDU CCE LEFT JOIN CAMPUS.DJ_COMMON_EDU_USER CCEU ON CCE.COMMON_EDU_SN = CCEU.COMMON_EDU_SN WHERE CCEU.REG_EMP_SEQ = HEI.EMP_SEQ AND CCE.STATUS = 100 AND CCEU.PART_YN = 'Y' AND CCEU.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(CCE.START_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(CCE.START_DT, '%Y%m%d') ), 0) AS COMMON_EDU_TIME, ( SELECT COUNT(CCEU.REAR_EDU_TIME) FROM CAMPUS.DJ_COMMON_EDU CCE LEFT JOIN CAMPUS.DJ_COMMON_EDU_USER CCEU ON CCE.COMMON_EDU_SN = CCEU.COMMON_EDU_SN WHERE CCEU.REG_EMP_SEQ = HEI.EMP_SEQ AND CCE.STATUS = 100 AND CCEU.PART_YN = 'Y' AND CCEU.ACTIVE = 'Y' AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(CCE.START_DT, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(CCE.START_DT, '%Y%m%d') ) AS COMMON_EDU_COUNT FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE 1=1
AND (DIVISION = 0 OR (DIVISION = 4 AND DIVISION_SUB != 3)) AND WORK_STATUS_CODE = 'Y'
AND HEI.EMP_SEQ = #{empSeq}
AND HEI.EMP_NAME_KR LIKE CONCAT('%', #{sEmpName}, '%')
ORDER BY EMP_NAME_KR

/* getEduMyStatList */ SELECT CASE WHEN HDI.DEPT_LEVEL = 2 THEN CONCAT((SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ), " ",HEI.DEPT_NAME) ELSE HEI.DEPT_NAME END AS DEPT, CASE WHEN HEI.DUTY_NAME = "" THEN HEI.POSITION_NAME ELSE HEI.DUTY_NAME END AS POSITION, HEI.EMP_NAME_KR AS EMP_NAME, IFNULL(( SELECT SUM(REAL_EDU_TIME) FROM CAMPUS.DJ_EDU_RESULT SER WHERE SER.REG_EMP_SEQ = HEI.EMP_SEQ AND SER.ACTIVE = 'Y' AND SER.STATUS = 100 AND DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y') = #{eduYear} ), 0) AS PERSONAL_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_EDU_RESULT SER WHERE SER.REG_EMP_SEQ = HEI.EMP_SEQ AND SER.ACTIVE = 'Y' AND SER.STATUS = 100 AND DATE_FORMAT(SER.APPROVAL_SEND_DATE, '%Y') = #{eduYear} ) AS PERSONAL_COUNT, IFNULL(( SELECT SUM(STUDY_TIME) FROM CAMPUS.DJ_STUDY_JOURNAL SJ LEFT JOIN CAMPUS.DJ_STUDY_RESULT_USER SRU ON SJ.STUDY_JOURNAL_SN = SRU.STUDY_JOURNAL_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SJ.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SRU.STUDY_EMP_SEQ = HEI.EMP_SEQ AND SJ.ACTIVE = 'Y' AND DATE_FORMAT(SJ.JOURNAL_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ), 0) AS STUDY_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_STUDY_JOURNAL SJ LEFT JOIN CAMPUS.DJ_STUDY_RESULT_USER SRU ON SJ.STUDY_JOURNAL_SN = SRU.STUDY_JOURNAL_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SJ.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SRU.STUDY_EMP_SEQ = HEI.EMP_SEQ AND SJ.ACTIVE = 'Y' AND DATE_FORMAT(SJ.JOURNAL_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ) AS STUDY_COUNT, IFNULL(( SELECT SUM(REAL_EDU_TIME) FROM CAMPUS.DJ_STUDY_PROPAG SP LEFT JOIN CAMPUS.DJ_PROPAG_USER SPU ON SP.STUDY_PROPAG_SN = SPU.STUDY_PROPAG_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SP.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SPU.PROPAG_EMP_SEQ = HEI.EMP_SEQ AND SP.ACTIVE = 'Y' AND DATE_FORMAT(SP.PROPAG_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ), 0) AS PROPAG_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_STUDY_PROPAG SP LEFT JOIN CAMPUS.DJ_PROPAG_USER SPU ON SP.STUDY_PROPAG_SN = SPU.STUDY_PROPAG_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SP.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SPU.PROPAG_EMP_SEQ = HEI.EMP_SEQ AND SP.ACTIVE = 'Y' AND DATE_FORMAT(SP.PROPAG_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ) AS PROPAG_COUNT, IFNULL(( SELECT SUM(CASE WHEN OJT_CLASS_SN = 4 THEN SOR.EDU_TIME ELSE SOR.EDU_TIME_HALF END) FROM CAMPUS.DJ_OJT_USER SOU LEFT JOIN CAMPUS.DJ_OJT_RESULT SOR ON SOR.OJT_RESULT_SN = SOU.OJT_RESULT_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SOR.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SOU.OJT_EMP_SEQ = HEI.EMP_SEQ AND SOR.ACTIVE = 'Y' AND DATE_FORMAT(SOR.OJT_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ), 0) AS OJT_TIME, ( SELECT COUNT(-1) FROM CAMPUS.DJ_OJT_USER SOU LEFT JOIN CAMPUS.DJ_OJT_RESULT SOR ON SOR.OJT_RESULT_SN = SOU.OJT_RESULT_SN LEFT JOIN CAMPUS.DJ_STUDY_INFO SSI ON SOR.STUDY_INFO_SN = SSI.STUDY_INFO_SN WHERE SOU.OJT_EMP_SEQ = HEI.EMP_SEQ AND SOR.ACTIVE = 'Y' AND DATE_FORMAT(SOR.OJT_DT, '%Y') = #{eduYear} AND SSI.ADD_STATUS = 'S' ) AS OJT_COUNT, IFNULL((SELECT SUM(EDU_TIME) FROM CAMPUS.DJ_OPENSTUDY_INFO COI LEFT JOIN CAMPUS.DJ_OPENSTUDY_USER COU ON COI.OPEN_STUDY_INFO_SN = COU.OPEN_STUDY_INFO_SN WHERE COU.REG_EMP_SEQ = HEI.EMP_SEQ AND COI.STATUS = 100 AND COU.PART_YN = 'Y' AND DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y') = #{eduYear}), 0) AS OPEN_STUDY_TIME, (SELECT COUNT(EDU_TIME) FROM CAMPUS.DJ_OPENSTUDY_INFO COI LEFT JOIN CAMPUS.DJ_OPENSTUDY_USER COU ON COI.OPEN_STUDY_INFO_SN = COU.OPEN_STUDY_INFO_SN WHERE COU.REG_EMP_SEQ = HEI.EMP_SEQ AND COI.STATUS = 100 AND COU.PART_YN = 'Y' AND DATE_FORMAT(COI.OPEN_STUDY_DT, '%Y') = #{eduYear}) AS OPEN_STUDY_COUNT, IFNULL((SELECT SUM(CCEU.REAR_EDU_TIME) FROM CAMPUS.DJ_COMMON_EDU CCE LEFT JOIN CAMPUS.DJ_COMMON_EDU_USER CCEU ON CCE.COMMON_EDU_SN = CCEU.COMMON_EDU_SN WHERE CCEU.REG_EMP_SEQ = HEI.EMP_SEQ AND CCE.STATUS = 100 AND CCEU.PART_YN = 'Y' AND CCEU.ACTIVE = 'Y' AND DATE_FORMAT(CCE.START_DT, '%Y') = #{eduYear}), 0) AS COMMON_EDU_TIME, (SELECT COUNT(CCEU.REAR_EDU_TIME) FROM CAMPUS.DJ_COMMON_EDU CCE LEFT JOIN CAMPUS.DJ_COMMON_EDU_USER CCEU ON CCE.COMMON_EDU_SN = CCEU.COMMON_EDU_SN WHERE CCEU.REG_EMP_SEQ = HEI.EMP_SEQ AND CCE.STATUS = 100 AND CCEU.PART_YN = 'Y' AND CCEU.ACTIVE = 'Y' AND DATE_FORMAT(CCE.START_DT, '%Y') = #{eduYear}) AS COMMON_EDU_COUNT FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE (DIVISION = 0 OR (DIVISION = 4 AND DIVISION_SUB != 3))
AND HEI.EMP_SEQ = #{empSeq}
AND HEI.EMP_NAME_KR LIKE CONCAT('%', #{sEmpName}, '%')
AND WORK_STATUS_CODE = 'Y' ORDER BY EMP_NAME_KR

/* getDutyInfoList */ SELECT CDI.*, DATE_FORMAT(CONCAT(CDI.DUTY_MONTH, '-01'), '%Y년 %m월') AS DUTY_MONTH_TEXT FROM CAMPUS.DJ_DUTY_INFO CDI WHERE 1=1
AND CDI.REG_EMP_SEQ = #{regEmpSeq}
ORDER BY CDI.DUTY_MONTH DESC, CDI.DUTY_INFO_SN DESC

