mapper.select:
/* getRecentBoardArticleList */ SELECT BA.BOARD_ARTICLE_ID, CASE WHEN BC.BOARD_CATEGORY_NAME IS NULL THEN BA.BOARD_ARTICLE_TITLE ELSE CONCAT('[', BC.BOARD_CATEGORY_NAME,'] ', BA.BOARD_ARTICLE_TITLE) END AS BOARD_ARTICLE_TITLE, CASE WHEN B.ANONYMOUS_ACTIVE = 'N' THEN REGEXP_REPLACE(BA.REG_EMP_NAME, '[가-힣]', '*') ELSE BA.REG_EMP_NAME END AS REG_EMP_NAME, DATE_FORMAT(BA.REG_DATE, '%Y-%m-%d') AS REG_DATE, BA.BOARD_ARTICLE_VIEW_COUNT, BA.BOARD_ARTICLE_GROUP, BA.BOARD_ARTICLE_LEVEL, EI.EMP_SEQ, EI.EMP_NAME_KR, BA.PUBLIC_YN, BA.PRIVATE_PASS_WORD, (SELECT IF(COUNT(*) > 0, 'Y', 'N') FROM CAM_BOARD.DJ_BOARD_ARTICLE_FILE WHERE BOARD_ARTICLE_ID = BA.BOARD_ARTICLE_ID) AS FILE_YN FROM CAM_BOARD.DJ_BOARD_ARTICLE BA JOIN CAM_BOARD.DJ_BOARD B ON BA.BOARD_ID = B.BOARD_ID LEFT JOIN CAM_BOARD.DJ_BOARD_CATEGORY BC ON BA.BOARD_CATEGORY_ID = BC.BOARD_CATEGORY_ID LEFT JOIN DJ_CAMTIC.V_EMP_INFO EI ON BA.BOARD_ARTICLE_MNG_SEQ = EI.EMP_SEQ WHERE BA.ACTIVE = 'Y'
AND BA.BOARD_CATEGORY_ID = #{searchCategory}
AND BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%')
AND BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%')
AND ( BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%') OR BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%') )
AND BA.REG_DATE > date_add(NOW(), INTERVAL -7 DAY)
ORDER BY BA.REG_DATE DESC, BA.BOARD_ARTICLE_GROUP DESC, BA.BOARD_ARTICLE_ID ASC LIMIT #{pagination.limitStart}, #{recordSize}

/* getBoardArticleList */ SELECT (SELECT BOARD_NAME FROM CAM_BOARD.DJ_BOARD WHERE BOARD_ID = BA.BOARD_ID) AS BOARD_NAME, BA.BOARD_ARTICLE_ID, CASE WHEN BC.BOARD_CATEGORY_NAME IS NULL THEN BA.BOARD_ARTICLE_TITLE ELSE CONCAT('[', BC.BOARD_CATEGORY_NAME,'] ', BA.BOARD_ARTICLE_TITLE) END AS BOARD_ARTICLE_TITLE,
REGEXP_REPLACE(BA.REG_EMP_NAME, '[가-힣]', '*') AS REG_EMP_NAME,
BA.REG_EMP_NAME,
DATE_FORMAT(BA.REG_DATE, '%Y-%m-%d') AS REG_DATE, BA.BOARD_ARTICLE_VIEW_COUNT, BA.BOARD_ARTICLE_GROUP, BA.BOARD_ARTICLE_LEVEL, EI.EMP_SEQ, EI.EMP_NAME_KR, BA.PUBLIC_YN, BA.PRIVATE_PASS_WORD, (SELECT IF(COUNT(*) > 0, 'Y', 'N') FROM CAM_BOARD.DJ_BOARD_ARTICLE_FILE WHERE BOARD_ARTICLE_ID = BA.BOARD_ARTICLE_ID AND FILE_CD = #{boardId}) AS FILE_YN, (SELECT COUNT(*) FROM CAM_BOARD.V_BOARD_ARTICLE_REPLY WHERE BOARD_ARTICLE_ID = BA.BOARD_ARTICLE_ID) AS REPLY_CNT FROM CAM_BOARD.DJ_BOARD_ARTICLE BA LEFT JOIN CAM_BOARD.DJ_BOARD_CATEGORY BC ON BA.BOARD_CATEGORY_ID = BC.BOARD_CATEGORY_ID LEFT JOIN DJ_CAMTIC.V_EMP_INFO EI ON BA.BOARD_ARTICLE_MNG_SEQ = EI.EMP_SEQ WHERE 1=1
AND BA.BOARD_ID in(40,41,42,43)
AND BA.BOARD_ID = #{boardId}
AND BA.ACTIVE = 'Y'
AND BA.BOARD_CATEGORY_ID = #{searchCategory}
AND BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%')
AND BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%')
AND ( BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%') OR BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%') )
ORDER BY BA.REG_DATE DESC, BA.BOARD_ARTICLE_GROUP DESC, BA.BOARD_ARTICLE_ID ASC LIMIT #{pagination.limitStart}, #{recordSize}

/* getBoardArticleListCnt */ SELECT count(*) FROM CAM_BOARD.DJ_BOARD_ARTICLE BA LEFT JOIN CAM_BOARD.DJ_BOARD_CATEGORY BC ON BA.BOARD_CATEGORY_ID = BC.BOARD_CATEGORY_ID LEFT JOIN DJ_CAMTIC.V_EMP_INFO EI ON BA.BOARD_ARTICLE_MNG_SEQ = EI.EMP_SEQ WHERE 1=1
AND BA.BOARD_ID in(40,41,42,43)
AND BA.BOARD_ID = #{boardId}
AND BA.ACTIVE = 'Y'
AND BA.BOARD_CATEGORY_ID = #{searchCategory}
AND BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%')
AND BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%')
AND ( BA.BOARD_ARTICLE_TITLE LIKE CONCAT('%',#{searchContent},'%') OR BA.BOARD_ARTICLE_CONTENT LIKE CONCAT('%',#{searchContent},'%') )
ORDER BY BA.BOARD_ARTICLE_GROUP DESC, BA.BOARD_ARTICLE_ID ASC

/* getArticleReplyList */ SELECT BAR.ARTICLE_REPLY_ID, BAR.BOARD_ARTICLE_ID, BAR.ARTICLE_REPLY_CONTENT, BAR.ARTICLE_REPLY_GROUP, BAR.ARTICLE_REPLY_ORIGIN_ID, BAR.ARTICLE_REPLY_LEVEL, EI.EMP_SEQ, EI.EMP_NAME_KR, BAR.ARTICLE_REPLY_PUBLIC_YN, BAR.PRIVATE_PASS_WORD, BAR.REG_EMP_SEQ, BAR.REG_EMP_NAME, DATE_FORMAT(BAR.REG_DATE, '%Y-%m-%d %H:%i:%s') AS REG_DATE FROM CAM_BOARD.V_BOARD_ARTICLE_REPLY BAR LEFT JOIN CAM_HR.DJ_EMP_INFO EI ON BAR.REG_EMP_SEQ = EI.EMP_SEQ WHERE 1=1
AND BAR.BOARD_ARTICLE_ID = #{requestBoardId}
AND BAR.BOARD_ARTICLE_ID = #{boardArticleId}
AND BAR.ACTIVE = 'Y' ORDER BY BAR.ARTICLE_REPLY_GROUP ASC, BAR.ARTICLE_REPLY_STEP ASC

/* getArticleReplyMaxLevel */
SELECT
    IFNULL(MAX(BAR.ARTICLE_REPLY_LEVEL), 0) + 1
FROM
    CAM_BOARD.V_BOARD_ARTICLE_REPLY BAR
WHERE
    ARTICLE_REPLY_GROUP = #{articleReplyGroup}
AND
    ARTICLE_REPLY_ID = #{articleReplyOriginId}
