mapper.select:
/* getSuggestionSystemList */ SELECT SB.*, CASE WHEN SB.SUGGESTION_TYPE = 'CP04' THEN '경영혁신' WHEN SB.SUGGESTION_TYPE = 'CP05' THEN '업무/관리효율' WHEN SB.SUGGESTION_TYPE = 'CP06' THEN '제품개선' WHEN SB.SUGGESTION_TYPE = 'CP07' THEN '공정개선' WHEN SB.SUGGESTION_TYPE = 'CP08' THEN '환경/청결' WHEN SB.SUGGESTION_TYPE = 'CP09' THEN '직원단합' WHEN SB.SUGGESTION_TYPE = 'CP10' THEN '사기진작' WHEN SB.SUGGESTION_TYPE = 'CP11' THEN '사업아이템 제안' WHEN SB.SUGGESTION_TYPE = 'CP11' THEN '단순건의' END AS SUGGESTION_TYPE_TXT,
EI.EMP_SEQ AS REG_EMP_SEQ, EI.EMP_NAME_KR AS REG_EMP_NAME, EI2.EMP_SEQ AS EXECUTE_EMP_SEQ, EI2.EMP_NAME_KR AS EXECUTE_EMP_NAME FROM CAM_BOARD.DJ_SUGGESTION_BOARD SB JOIN CAM_HR.DJ_EMP_INFO EI ON SB.REG_EMP_SEQ = EI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO EI2 ON SB.EXECUTE = EI2.EMP_SEQ WHERE 1=1 AND SB.ACTIVE = 'Y'
AND SB.SUGGESTION_TYPE = #{suggestionType}
AND SB.STATUS = #{status}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SB.SUGGESTION_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SB.SUGGESTION_DATE, '%Y%m%d')
AND SB.SUGGESTION_TITLE LIKE CONCAT('%',#{searchContent},'%')
AND ( SB.SUGGESTION_CONTENT1 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT2 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT3 LIKE CONCAT('%',#{searchContent},'%') )
AND EI.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%')
AND EI2.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%')
AND ( SB.SUGGESTION_TITLE LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT1 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT2 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT3 LIKE CONCAT('%',#{searchContent},'%') OR EI.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%') OR EI2.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%') )
ORDER BY SB.REG_DATE DESC LIMIT #{pagination.limitStart}, #{recordSize}

/* getSuggestionSystemListCnt */ SELECT COUNT(*) FROM CAM_BOARD.DJ_SUGGESTION_BOARD SB JOIN CAM_HR.DJ_EMP_INFO EI ON SB.REG_EMP_SEQ = EI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO EI2 ON SB.EXECUTE = EI2.EMP_SEQ WHERE 1=1 AND SB.ACTIVE = 'Y'
AND SB.SUGGESTION_TYPE = #{suggestionType}
AND SB.STATUS = #{status}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(SB.SUGGESTION_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(SB.SUGGESTION_DATE, '%Y%m%d')
AND SB.SUGGESTION_TITLE LIKE CONCAT('%',#{searchContent},'%')
AND ( SB.SUGGESTION_CONTENT1 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT2 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT3 LIKE CONCAT('%',#{searchContent},'%') )
AND EI.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%')
AND EI2.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%')
AND ( SB.SUGGESTION_TITLE LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT1 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT2 LIKE CONCAT('%',#{searchContent},'%') OR SB.SUGGESTION_CONTENT3 LIKE CONCAT('%',#{searchContent},'%') OR EI.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%') OR EI2.EMP_NAME_KR LIKE CONCAT('%',#{searchContent},'%') )

/* getScheduleList */ SELECT SC.SCHEDULE_TITLE AS title, CONCAT(DATE_FORMAT(SC.START_DT, '%Y-%m-%d'), ' ', SC.START_TIME) AS start, CONCAT(DATE_FORMAT(SC.END_DT, '%Y-%m-%d'), ' ', SC.END_TIME) AS end, DATE_FORMAT(SC.START_DT, '%Y-%m-%d') AS startDate, DATE_FORMAT(SC.END_DT, '%Y-%m-%d') AS endDate, SC.* FROM CAM_BOARD.DJ_SCHEDULE_BOARD SC WHERE 1=1
SC.START_DT >= #{startDt} AND SC.END_DT <= #{endDt}
AND PUBLIC_CLASS = #{publicClass}
AND ACTIVE = 'Y'

/* getTodayScheduleList */ SELECT SC.SCHEDULE_TITLE AS title, CONCAT(DATE_FORMAT(SC.START_DT, '%Y-%m-%d'), ' ', SC.START_TIME) AS start, CONCAT(DATE_FORMAT(SC.END_DT, '%Y-%m-%d'), ' ', SC.END_TIME) AS end, DATE_FORMAT(SC.START_DT, '%Y-%m-%d') AS startDate, DATE_FORMAT(SC.END_DT, '%Y-%m-%d') AS endDate, SC.* FROM CAM_BOARD.DJ_SCHEDULE_BOARD SC WHERE 1=1
AND #{selectedDate} BETWEEN SC.START_DT AND SC.END_DT
AND PUBLIC_CLASS = #{publicClass}
AND ACTIVE = 'Y'

/* getCustomSchedule */ SELECT *, (SELECT SCHEDULE_BOARD_ID FROM CAM_BOARD.DJ_SCHEDULE_BOARD WHERE SCHEDULE_BOARD_ID < #{scheduleBoardId} AND #{selectedDate} BETWEEN START_DT AND END_DT and PUBLIC_CLASS = 'CS' ORDER BY SCHEDULE_BOARD_ID DESC LIMIT 1) as 'beforeKey', (SELECT SCHEDULE_BOARD_ID FROM CAM_BOARD.DJ_SCHEDULE_BOARD WHERE SCHEDULE_BOARD_ID > #{scheduleBoardId} AND #{selectedDate} BETWEEN START_DT AND END_DT and PUBLIC_CLASS = 'CS' ORDER BY SCHEDULE_BOARD_ID ASC LIMIT 1) as 'afterKey' FROM CAM_BOARD.DJ_SCHEDULE_BOARD WHERE 1=1
AND PUBLIC_CLASS = #{publicClass}
AND #{selectedDate} BETWEEN START_DT AND END_DT
AND SCHEDULE_BOARD_ID = #{scheduleBoardId}

/* getRequestBoardList */ SELECT RB.REQUEST_BOARD_ID, RB.REQUEST_TYPE, RB.REQUEST_TITLE, RB.REQUEST_CONTENT, CASE WHEN RB.STATUS = '1' THEN '요청중' WHEN RB.STATUS = '2' THEN '접수완료' WHEN RB.STATUS = '3' THEN '처리완료' WHEN RB.STATUS = '-1' THEN '취소' END AS STATUS, DATE_FORMAT(RB.REG_DATE, '%Y-%m-%d') AS REG_DATE, EI.EMP_SEQ AS REG_EMP_SEQ, EI.EMP_NAME_KR AS REG_EMP_NAME FROM CAM_BOARD.DJ_REQUEST_BOARD RB JOIN CAM_HR.DJ_EMP_INFO EI ON RB.REG_EMP_SEQ = EI.EMP_SEQ WHERE RB.ACTIVE = 'Y' AND RB.REQUEST_TYPE = #{requestType}
AND RB.STATUS = #{status}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(RB.REG_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(RB.REG_DATE, '%Y%m%d')
AND RB.${searchColumn} LIKE CONCAT('%', #{searchContent}, '%')
AND ( RB.REQUEST_TITLE LIKE CONCAT('%',#{searchContent},'%') OR RB.REQUEST_CONTENT LIKE CONCAT('%',#{searchContent},'%') )
ORDER BY RB.REG_DATE DESC LIMIT #{pagination.limitStart}, #{recordSize}

/* getRequestBoardListCnt */ SELECT COUNT(*) FROM CAM_BOARD.DJ_REQUEST_BOARD RB JOIN CAM_HR.DJ_EMP_INFO EI ON RB.REG_EMP_SEQ = EI.EMP_SEQ WHERE RB.ACTIVE = 'Y' AND RB.REQUEST_TYPE = #{requestType}
AND RB.STATUS = #{status}
AND DATE_FORMAT(#{startDt}, '%Y%m%d') <= DATE_FORMAT(RB.REG_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDt}, '%Y%m%d') >= DATE_FORMAT(RB.REG_DATE, '%Y%m%d')
AND RB.${searchColumn} LIKE CONCAT('%', #{searchContent}, '%')
AND ( RB.REQUEST_TITLE LIKE CONCAT('%',#{searchContent},'%') OR RB.REQUEST_CONTENT LIKE CONCAT('%',#{searchContent},'%') )

/* getMainScheduleList */ SELECT SC.SCHEDULE_TITLE AS title, CONCAT(DATE_FORMAT(SC.START_DT, '%Y-%m-%d'), ' ', SC.START_TIME) AS start, CONCAT(DATE_FORMAT(SC.END_DT, '%Y-%m-%d'), ' ', SC.END_TIME) AS end, DATE_FORMAT(SC.START_DT, '%Y-%m-%d') AS startDate, DATE_FORMAT(SC.END_DT, '%Y-%m-%d') AS endDate, SC.* FROM CAM_BOARD.DJ_SCHEDULE_BOARD SC WHERE 1=1
AND PUBLIC_CLASS = #{publicClass}
AND #{selectedDate} BETWEEN SC.START_DT AND SC.END_DT
AND SCHEDULE_TITLE LIKE CONCAT('%', #{searchInput}, '%')
AND ACTIVE = 'Y' ORDER BY REG_DATE DESC LIMIT #{pagination.limitStart}, #{recordSize}

/*getMainScheduleListCnt*/ SELECT count(*) FROM CAM_BOARD.DJ_SCHEDULE_BOARD SC WHERE 1=1
AND PUBLIC_CLASS = #{publicClass}
AND #{selectedDate} BETWEEN SC.START_DT AND SC.END_DT
AND SCHEDULE_TITLE LIKE CONCAT('%', #{searchInput}, '%')
AND ACTIVE = 'Y'
