mapper.select:
/* getUserList */ SELECT VEI.EMP_SEQ, VEI.EMP_NAME_KR, VEI.LOGIN_ID, VEI.DEPT_SEQ, (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = VEI.DEPT_SEQ)) AS PARENT_DEPT_NAME, (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE DEPT_SEQ = VEI.DEPT_SEQ) AS DEPT_NAME, scp.dec_b64('KEY1', VEI.MOBILE_TEL_NUM) AS MOBILE_TEL_NUM, VEI.OFFICE_TEL_NUM, VEI.POSITION_CODE, IFNULL(VEI.POSITION_NAME, '') AS POSITION_NAME, VEI.DUTY_CODE, IFNULL(VEI.DUTY_NAME, '') AS DUTY_NAME, VEI.JOB_DETAIL, CASE WHEN VEI.DUTY_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 3 AND CM_CODE = VEI.DUTY_CODE) WHEN VEI.POSITION_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 4 AND CM_CODE = VEI.POSITION_CODE) ELSE 999 END SORT FROM CAM_HR.DJ_EMP_INFO VEI WHERE 1 = 1 AND VEI.ACTIVE = 'Y'
AND (DIVISION = 0 OR (DIVISION = 4 AND DIVISION_SUB != 3)) AND WORK_STATUS_CODE = 'Y'
AND ((DIVISION = 0 OR (DIVISION = 4 AND DIVISION_SUB != 3)) OR (DIVISION IN(1) AND DIVISION_SUB IN(6)) OR (DIVISION IN(1) AND DIVISION_SUB IN(6)) OR (DIVISION IN(4) AND DIVISION_SUB IN(3)) ) AND WORK_STATUS_CODE = 'Y'
AND TEMP_DIVISION = #{tempType}
AND VEI.DIVISION != #{notDivision}
AND VEI.DEPT_SEQ = #{DEPT_SEQ}
AND VEI.DEPT_NAME LIKE CONCAT('%', #{deptName}, '%')
AND VEI.EMP_NAME_KR LIKE CONCAT('%', #{sEmpName}, '%')
ORDER BY DUTY_CODE DESC, EMP_NAME_KR ASC /*ORDER BY SORT*/

/* getUserListTotal */ SELECT COUNT(1) FROM CAM_HR.DJ_EMP_INFO VEI WHERE 1 = 1 AND VEI.ACTIVE = 'Y'
AND VEI.DEPT_SEQ = #{DEPT_SEQ}
AND VEI.DEPT_NAME LIKE CONCAT('%', #{deptName}, '%')

mapper.insert:
/* insOneFileInfo */ INSERT INTO CAM_COMMON.DJ_FILE_INFO ( FILE_CD, FILE_UUID, FILE_ORG_NAME, FILE_PATH, FILE_SIZE, FILE_EXT, EMP_SEQ, REG_EMP_SEQ
, CONTENT_ID
) VALUES ( #{fileCd}, #{fileUUID}, #{fileOrgName}, #{filePath}, #{fileSize}, #{fileExt}, #{empSeq}, #{empSeq}
, #{contentId}
)
SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_COMMON' AND TABLE_NAME = 'DJ_FILE_INFO'

