mapper.select:
/* getUserList */ SELECT * FROM DJ_CAMTIC.V_EMP_INFO VEI WHERE 1 = 1 AND VEI.ACTIVE = 'Y'
AND VEI.DEPT_SEQ = #{DEPT_SEQ}
AND VEI.DEPT_NAME LIKE CONCAT('%', #{deptName}, '%')

/* getUserListTotal */ SELECT COUNT(1) FROM DJ_CAMTIC.V_EMP_INFO VEI WHERE 1 = 1 AND VEI.ACTIVE = 'Y'
AND VEI.DEPT_SEQ = #{DEPT_SEQ}
AND VEI.DEPT_NAME LIKE CONCAT('%', #{deptName}, '%')
