mapper.select:
/* getCmCodeList */ SELECT * FROM CAM_COMMON.DJ_COM_CODE CC WHERE 1=1
AND CC.CM_GROUP_CODE_ID IN
#{item}
AND CC.CM_GROUP_CODE_ID = #{cmGroupCodeId}
ORDER BY CC.CM_CODE_ID

/* getCmCodeListReward */ SELECT *, SUBSTRING(CC.CM_CODE_NM, 8) AS CM_CODE_NMM FROM CAM_COMMON.DJ_COM_CODE CC WHERE 1=1
AND CC.CM_GROUP_CODE_ID IN
#{item}
AND CC.CM_GROUP_CODE_ID = #{cmGroupCodeId}
AND CC.CM_CODE_NM like CONCAT('%', #{specificValue}, '%')
ORDER BY CC.CM_CODE_ID

/* getCmCodeInfo */
SELECT CC.* FROM CAM_COMMON.DJ_COM_CODE CC WHERE 1=1
AND CC.CM_CODE_ID = #{cmCodeId}
AND CC.CM_CODE = #{cmCode}
AND CC.CM_CODE_NM = '임시저장' AND CC.CM_CODE_NM = '상신' AND CC.CM_CODE_NM = '결재' AND CC.CM_CODE_NM = '반려' AND CC.CM_CODE_NM = '재상신' AND CC.CM_CODE_NM = '회수' AND CC.CM_CODE_NM = '전결' AND CC.CM_CODE_NM = '결재안함' AND CC.CM_CODE_NM = '최종결재'

mapper.insert:
/* setCmCodeSave */

INSERT INTO CAM_COMMON.DJ_COM_GROUP_CODE
(
    CM_GROUP_CODE, CM_GROUP_CODE_NM, CM_GROUP_CODE_DESC, SORT, ACTIVE, REG_EMP_SEQ
)
VALUES
(
    #{cmGroupCode}, #{cmGroupCodeNm}, #{cmGroupCodeDesc}, #{orderCmGCR}, #{active}, #{empSeq}
)

/* setCmCodeSave */

INSERT INTO CAM_COMMON.DJ_COM_CODE
(
    CM_CODE, CM_GROUP_CODE_ID, CM_CODE_NM, CM_CODE_DESC, SORT, ACTIVE, REG_EMP_SEQ
)
VALUES
(
    #{cmCode}, #{cmGroupCodeId}, #{cmCodeNm}, #{cmCodeDesc}, #{orderCmCR}, #{cmCodeActive}, #{empSeq}
)
