mapper.update:
UPDATE
    CAM_CRM.DJ_CRM_INFO
SET
    CRM_FILE = #{crmFile}
WHERE
    CRM_SN = #{crmSn}

UPDATE
    CAM_CRM.DJ_CRM_INFO
SET
    CRM_LICS = #{crmLics}
WHERE
    CRM_SN = #{crmSn}

UPDATE
    CAM_CRM.DJ_CRM_INFO
SET
    BN_CP = #{bnCp}
WHERE
    CRM_SN = #{crmSn}

mapper.select:
/*getCrmHistList ==> CRM 관리 > 고객 이력관리 데이터 조회 */ SELECT A.*, DATE_FORMAT(A.CRM_REL_STR_DT, '%Y-%m-%d %H:%i') AS START_DATETIME, CI.*, CM.*, B.EMP_NAME_KR, C.DEPT_NAME FROM CAM_CRM.DJ_CRM_HIST A LEFT JOIN CAM_CRM.DJ_CRM_INFO CI ON A.CRM_SN = CI.CRM_SN LEFT JOIN CAM_CRM.DJ_CRM_MEM CM ON A.CRM_MEM_SN = CM.CRM_MEM_SN LEFT JOIN CAM_HR.DJ_EMP_INFO B ON A.REG_EMP_SEQ = B.EMP_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO C ON B.DEPT_SEQ = C.DEPT_SEQ WHERE 1 = 1
AND C.PARENT_DEPT_SEQ = #{deptSeq} AND C.DEPT_SEQ = #{teamSeq}
AND B.DEPT_SEQ = #{teamSeq}
AND ( B.DEPT_SEQ = #{deptSeq} OR C.PARENT_DEPT_SEQ = #{deptSeq} )
AND DATE_FORMAT(A.CRM_REL_STR_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{strDate}, '%Y-%m-%d') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d')
AND CI.CRM_NM LIKE CONCAT('%', #{crmNm}, '%')
AND CI.CRM_SN = #{crmSn}
ORDER BY A.REG_DT DESC

/* getMfOverviewList */ SELECT CRM_MF_SN, MF_AREA, IF(ACTIVE = 'Y', '정상', '') AS ACTIVE, MF_NAME, MF_NO, CEO_NAME, IF(CEO_GENDER = '1', '남', '여') AS CEO_GENDER, ADDR, DATE_FORMAT(EST_DATE, '%Y-%m-%d') AS EST_DATE, HISTORY, LOCATION, INDUSTRY, INDUSTRY2, MAIN_PRODUCT, IF(AM_PART = '1', 'O', 'X') AS AM_PART, REPLACE(REPLACE(REPLACE(AM_PART_TYPE, '1', '전기차'), '2', '내연차'), '3', '수소차') AS AM_PART_TYPE, TEL_NUM, FAX_NUM, HOME_PAGE, EMAIL, CAPITAL, SALES, SALES_AMT, SALES_RATIO_PROV, SALES_RATIO_OT_PROV, EMP_CNT, IF(EMP_FOREIGN = '1', 'O', 'X') AS EMP_FOREIGN, FOREIGN_CNT, IF(EXPORT_YN = '1', 'Y', 'N') AS EXPORT_YN, IF(LABORATORY_YN = '1', 'Y', 'N') AS LABORATORY_YN, IF(CARBON_YN = '1', 'Y', 'N') AS CARBON_YN, IF(RPR_YN = '1', 'Y', 'N') AS RPR_YN, IF(NEW_PRODUCT_YN = '1', 'Y', 'N') AS NEW_PRODUCT_YN, IF(FACILITY_INVEST_YN = '1', 'Y', 'N') AS FACILITY_INVEST_YN, HIGHLY_SAT_FIELD, NEED_FIELD, IF(AGREE_YN = '1', 'Y', 'N') AS AGREE_YN, IF(AGREE2_YN = '1', 'Y', 'N') AS AGREE2_YN, CEO_TEL_NUM, CHARGE_NAME, CHARGE_TEL_NUM FROM CAM_CRM.DJ_CRM_MF_OVERVIEW WHERE 1=1
AND ${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( MF_AREA LIKE CONCAT('%', #{searchValue}, '%') OR MF_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MF_NO LIKE CONCAT('%', #{searchValue}, '%') OR CEO_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MAIN_PRODUCT LIKE CONCAT('%', #{searchValue}, '%') )
LIMIT ${skip} , ${pageSize}

/* getMfOverviewListCnt */ SELECT COUNT(*) FROM CAM_CRM.DJ_CRM_MF_OVERVIEW WHERE 1=1
AND ${searchKeyword} LIKE CONCAT('%', #{searchValue}, '%')
AND ( MF_AREA LIKE CONCAT('%', #{searchValue}, '%') OR MF_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MF_NO LIKE CONCAT('%', #{searchValue}, '%') OR CEO_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MAIN_PRODUCT LIKE CONCAT('%', #{searchValue}, '%') )

mapper.insert:
/* insGroupCode */
INSERT INTO CAM_CRM.DJ_CRM_CD
(
    GRP_SN,
    GRP_NM
)
VALUES
    (
        #{grpSn},
        #{grpNm}
    )

mapper.select:
/* selLgCode */ SELECT * FROM CAM_CRM.DJ_CRM_CD WHERE GRP_SN = #{grpSn}
AND LG_CD in(${lgCdArr})
AND LG_CD is not null
AND CRM_CD is null

/* selSmCode */ SELECT * FROM CAM_CRM.DJ_CRM_CD WHERE GRP_SN = #{grpSn} AND LG_CD is not null
AND LG_CD in(${lgCdArr})
AND LG_CD = #{lgCd}
AND CRM_CD is not null

mapper.update:
/* setCrmAccountingFileUpd */
UPDATE
    CAM_CRM.DJ_CRM_ACCOUNTING
SET
    ${column} = #{value},
    MOD_EMP_SEQ = #{empSeq}
WHERE
    CRM_SN = #{crmSn}

AND
    CRM_ACCOUNTING_SN = #{crmAccountingSn}

/* setCrmInterestsUpd */
UPDATE
    CAM_CRM.DJ_CRM_INTERESTS
SET
    MAIN_DEPTH = #{mainDepth},
    SUB_DEPTH = #{subDepth},
    MOD_EMP_SEQ = #{regEmpSeq}
WHERE
    CRM_INTERESTS_SN = #{crmInterestsSn}
AND
    CRM_SN = #{crmSn}
