mapper.update:
/* updateCardLossApprStat */
UPDATE CAM_INSIDE.DJ_TP_CARD_LOSS
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    TP_CL_SN = #{tpClSn}

/* updateCardLossFinalApprStat */
UPDATE CAM_INSIDE.DJ_TP_CARD_LOSS
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    TP_CL_SN = #{tpClSn}

