mapper.select:
/* getSignInfoList */ SELECT CSI.SIGN_SEQ AS SIGN_SEQ, CSI.FORM_COMP_NAME AS FORM_COMP_NAME, CSI.SI_NAME AS SI_NAME, CSI.CI_KIND_VAL AS CI_KIND_VAL, CSI.ACTIVE AS ACTIVE, CSI.USE_FORM_VAL AS USE_FORM_VAL, CSI.USE_FORM_NAME AS USE_FORM_NAME, CSI.REMARK_VAL AS REMARK_VAL, (SELECT CONCAT(CFI.FILE_PATH, CFI.FILE_UUID) FROM CAM_COMMON.DJ_FILE_INFO CFI WHERE CONCAT('sign_', CSI.SIGN_SEQ) = CFI.CONTENT_ID ORDER BY CFI.REG_DATE DESC LIMIT 1) AS FILE_NO FROM CAM_COMMON.DJ_SIGN_INFO CSI WHERE 1=1
AND CSI.SI_NAME LIKE CONCAT('%', #{siName}, '%')
AND CSI.CI_KIND_VAL = #{ciKindVal}
AND CSI.ACTIVE = #{active}
ORDER BY CSI.SIGN_SEQ DESC
