mapper.select:
/* getFormFolderList */ SELECT FORM_FOLDER_ID, FORM_COMP_SEQ, FORM_COMP_NAME, FORM_FOLDER_NAME, SORT, REMARK, VISIBLE, ACTIVE FROM DJ_CAMTIC.A_FORM_FOLDER WHERE 1=1
AND FORM_COMP_SEQ = #{searchCompSeq}
AND FORM_FOLDER_NAME LIKE CONCAT('%', #{searchKeyWord}, '%')
AND ACTIVE = #{searchActive}
ORDER BY SORT ASC

/* getFormList */ SELECT FI.FORM_ID, FI.FORM_COMP_NAME, FF.FORM_FOLDER_ID, FF.FORM_FOLDER_NAME, FI.FORM_NAME, FI.ACTIVE, FI.VISIBLE, FI.EMAIL_ADDRESS, FI.APPROVER_MARK, FI.SORT, FI.HEADER_CAMPAIGN, FI.FOOTER_CAMPAIGN, FI.FORM_CONTENT, FI.LINKAGE_TYPE, FI.LINKAGE_PROCESS_ID, FI.LINKAGE_POP_ACTIVE, FI.LINKAGE_POP_WIDTH, FI.LINKAGE_POP_HEIGHT, FRO.FORM_REQ_OPT_ID, FRO.PRESERVE_PERIOD, FRO.PUBLIC_TYPE, FRO.URGENT_TYPE, FRO.SECURITY_TYPE, FRO.DOC_GBN FROM DJ_CAMTIC.A_FORM FI JOIN DJ_CAMTIC.A_FORM_FOLDER FF ON FI.FORM_FOLDER_ID = FF.FORM_FOLDER_ID LEFT JOIN DJ_CAMTIC.A_FORM_REQ_OPT FRO ON FI.FORM_ID = FRO.FORM_ID WHERE 1=1
AND FI.FORM_COMP_SEQ = #{searchCompSeq}
AND FI.FORM_NAME LIKE CONCAT('%', #{searchKeyWord}, '%')
AND FI.ACTIVE = #{searchActive}
ORDER BY FF.SORT ASC, FI.SORT ASC

/* getTemplateFormFile */
SELECT
    *
FROM
    DJ_CAMTIC.A_FORM_FILE
WHERE
    FORM_ID = #{formId}
  AND
    FILE_HOST_ADDRESS = #{fileHostAddress}

mapper.update:
/* setFormReqOptUpd */
UPDATE
    DJ_CAMTIC.A_FORM_REQ_OPT
SET
    PRESERVE_PERIOD = #{preservePeriod},
    SECURITY_TYPE = #{securityType},
    DOC_GBN = #{docGbn},
    MOD_EMP_SEQ = #{empSeq}
WHERE
    FORM_ID = #{formId}
  AND
    FORM_REQ_OPT_ID = #{formReqOptId}

mapper.delete:
/* setFormReaderDel */
DELETE
FROM DJ_CAMTIC.A_FORM_READER
WHERE FORM_ID = #{formId}

/* setFormReceiverDel */
DELETE
FROM DJ_CAMTIC.A_FORM_RECEIVER
WHERE FORM_ID = #{formId}

/* setFormCustomFieldDel */
DELETE
FROM DJ_CAMTIC.A_FORM_CUSTOM_FIELD
WHERE FORM_ID = #{formId}

mapper.select:
/* getLinkageProcessList */ SELECT LINKAGE_PROCESS_ID, LINKAGE_PROCESS_COMP_SEQ, LINKAGE_PROCESS_COMP_NAME, LINKAGE_PROCESS_CODE, LINKAGE_PROCESS_NAME, TEMP_PROCESS_URL, DRAFT_PROCESS_URL, DRAFT_CANCEL_PROCESS_URL, RETRIEVE_PROCESS_URL, APPROVE_PROCESS_URL, FINAL_APPROVAL_PROCESS_URL, RETURN_PROCESS_URL, DELETE_PROCESS_URL, ACTIVE FROM DJ_CAMTIC.A_LINKAGE_PROCESS WHERE 1=1
AND LINKAGE_PROCESS_COMP_SEQ = #{searchCompSeq}
AND LINKAGE_PROCESS_NAME LIKE CONCAT('%', #{searchKeyWord}, '%')
ORDER BY REG_DATE ASC

mapper.update:
/* setApprovalMngUpd */
UPDATE
    CAM_APPROVAL.DJ_APPROVAL_MNG
SET
    FORM_ID = #{formId},
    APPROVAL_TYPE = #{approvalType},
    COPPER_EMP_SEQ = #{otherEmpSeq}
WHERE
    APPROVAL_MNG_SN = #{approvalMngSn}

mapper.delete:
/* setApprovalMngDtDel */
DELETE FROM
    CAM_APPROVAL.DJ_APPROVAL_MNG_DT
WHERE FORM_ID = #{formId}
