mapper.update:
UPDATE CAM_COMMON.DJ_BOARD_ARTICLE
SET
    BOARD_ARTICLE_TITLE = #{noticeTitle},
    BOARD_ARTICLE_CONTENT = #{content}
WHERE
    BOARD_ARTICLE_ID = #{boardArticleId}
AND
    BOARD_ID = #{boardId}
AND
    BOARD_CATEGORY_ID = #{category}

UPDATE CAM_COMMON.DJ_BOARD_ARTICLE
SET
    ACTIVE = 'N'
WHERE
    BOARD_ARTICLE_ID = #{boardArticleId}
  AND
    BOARD_ID = #{boardId}
  AND
    BOARD_CATEGORY_ID = #{category}
