mapper.select:
/* getVacCodeList2 */
SELECT
*
FROM
CAM_INSIDE.DJ_SUBHOLIDAY_CODE
WHERE
ACTIVE = "Y"
AND
SUBHOLIDAY_MC_CODE = #{mcCode}

    AND SUBHOLIDAY_MD_CODE = #{mdCode}


    AND SUBHOLIDAY_DT_CODE = #{dtCode}


    AND SUBHOLIDAY_CODE_ID != '9'

    AND SUBHOLIDAY_CODE_ID = '11'

/* getPersonHolidayStatus */ WITH RECURSIVE REQUEST AS ( SELECT #{startDt} AS START_DATE, #{endDt} AS END_DATE, #{empSeq} AS EMP_SEQ ) SELECT /** 1연차 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 1 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS ANNUAL,
/** 3오전반차 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 3 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS MORNING,
/** 4오후반차 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 4 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS AFTERNOON,
/** 5병가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 5 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS SICK,
/** 6공가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 6 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS PUBLICHOLI,
/** 7경조휴가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 7 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS CONDOLENCES,
/** 8출산휴가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 8 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS MATERNITY,
/** 9대체휴가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 9 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS ALTERNATIVE,
/** 10근속포상휴가 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 10 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS LONGAWARD,
/** 11휴일근로 */ (SELECT IFNULL(SUM(SUBHOLIDAY_USE_DAY), 0) FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST A WHERE SUBHOLIDAY_CODE_ID = 11 AND REG_EMP_SEQ = R.EMP_SEQ AND APPR_STAT = 'Y') AS HOLIDAYWORK FROM REQUEST R

SELECT *, ROW_NUMBER() OVER (ORDER BY sm.SUBHOLIDAY_CODE_ID) AS ROW_NUM, DI.APPRO_KEY, (SELECT APPRO_KEY FROM DJ_CAMTIC.A_DOC_INFO WHERE DOC_ID = sm.ADMIN_DOC_ID) AS ADMIN_APPRO_KEY, (SELECT DOC_MENU_CD FROM DJ_CAMTIC.A_DOC_INFO WHERE DOC_ID = sm.ADMIN_DOC_ID) AS ADMIN_DOC_MENU_CD, DI.DOC_MENU_CD, DATE_FORMAT(sm.APPLY_DATE, '%Y-%m-%d') as APPLY_DAY FROM cam_inside.dj_subholiday_use_hist sm JOIN cam_inside.dj_subholiday_code sc ON sm.SUBHOLIDAY_CODE_ID = sc.SUBHOLIDAY_CODE_ID left join DJ_CAMTIC.A_DOC_INFO DI on sm.DOC_ID = DI.DOC_ID WHERE 1=1 AND sm.ACTIVE = 'Y' AND sm.SUBHOLIDAY_CODE_ID = 11
AND sm.APPR_STAT = #{status}
AND DATE_FORMAT(#{workDay}, '%Y-%m-%d') = DATE_FORMAT(sm.APPLY_DATE, '%Y-%m-%d')
AND sm.APPLY_SEQ = #{empSeq}
AND sm.APPR_STAT = 'Y'
ORDER BY ROW_NUM DESC
