mapper.select:
/* getBustripList */ SELECT IHBR.*, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_INSIDE.DJ_HR_BIZ_REQ IHBR LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON IHBR.DOC_ID = DI.DOC_ID WHERE
IHBR.TRIP_DAY_FR >= #{startDate} AND IHBR.TRIP_DAY_TO <= #{endDate}
AND PROJECT_CD = #{projectCd}
AND BUSN_NAME like concat('%', #{busnName}, '%')
ORDER BY REG_DT DESC

/* getBustripReqCheck */ SELECT A.*, B.HR_BIZ_REQ_RESULT_ID, B.STATUS AS RES_STATUS, B.EXP_STAT, B.SAVE_YN, B.DOC_ID AS RES_DOC_ID, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_INSIDE.DJ_HR_BIZ_REQ A LEFT JOIN CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT B ON A.HR_BIZ_REQ_ID = B.HR_BIZ_REQ_ID LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON B.DOC_ID = DI.DOC_ID WHERE A.STATUS IN ('100') AND
TRIP_DAY_FR >= #{startDate} AND TRIP_DAY_TO <= #{endDate}
AND A.PROJECT_CD = #{projectCd}
AND A.BUSN_NAME like concat('%', #{busnName}, '%')
ORDER BY A.REG_DT DESC

mapper.update:
/* bustrip updateApprStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

/* bustrip updateFinalApprStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

/* bustrip updateResApprStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

/* bustrip updateResFinalApprStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

UPDATE CAM_INSIDE.DJ_HR_BIZ_REQ
SET
    DRIVER_EMP_SEQ = #{driverEmpSeq}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

mapper.select:
/* getBustripMaxDayCost */ SELECT IFNULL(MAX(DAY_COST), 0) AS DAY_COST FROM CAM_INSIDE.DJ_HR_BIZ_EXNP IHBE LEFT JOIN CAM_INSIDE.DJ_HR_BIZ_REQ IHBR ON IHBE.HR_BIZ_REQ_ID = IHBR.HR_BIZ_REQ_ID WHERE IHBE.EMP_SEQ = #{empSeq}
AND DATE_FORMAT(IHBR.TRIP_DAY_FR, '%Y%M%D') <= DATE_FORMAT( (SELECT TRIP_DAY_FR FROM CAM_INSIDE.DJ_HR_BIZ_REQ SIHBR WHERE HR_BIZ_REQ_ID = #{hrBizReqId}) , '%Y%M%D') AND DATE_FORMAT(IHBR.TRIP_DAY_TO, '%Y%M%D') >= DATE_FORMAT( (SELECT TRIP_DAY_TO FROM CAM_INSIDE.DJ_HR_BIZ_REQ SIHBR WHERE HR_BIZ_REQ_ID = #{hrBizReqId}) , '%Y%M%D')
AND IHBE.HR_BIZ_REQ_ID != #{hrBizReqId}

/* getBustripCostList */ SELECT IHCI.* FROM CAM_INSIDE.DJ_HR_COST_INFO IHCI WHERE 1=1
AND DATE_FORMAT(IHCI.START_DT, '%Y%M%D') <= DATE_FORMAT( (SELECT SIHBR.TRIP_DAY_FR FROM CAM_INSIDE.DJ_HR_BIZ_REQ SIHBR WHERE SIHBR.HR_BIZ_REQ_ID = #{hrBizReqId}) , '%Y%M%D') AND DATE_FORMAT(IHCI.END_DT, '%Y%M%D') >= DATE_FORMAT( (SELECT SIHBR.TRIP_DAY_TO FROM CAM_INSIDE.DJ_HR_BIZ_REQ SIHBR WHERE SIHBR.HR_BIZ_REQ_ID = #{hrBizReqId}) , '%Y%M%D') AND IHCI.TRIP_CODE = (SELECT SIHBR.TRIP_CODE FROM CAM_INSIDE.DJ_HR_BIZ_REQ SIHBR WHERE SIHBR.HR_BIZ_REQ_ID = #{hrBizReqId})
ORDER BY IHCI.REG_DT DESC
