mapper.select:
SELECT IHBR.*, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_INSIDE.DJ_HR_BIZ_REQ IHBR LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON IHBR.DOC_ID = DI.DOC_ID WHERE
IHBR.TRIP_DAY_FR >= #{startDate} AND IHBR.TRIP_DAY_TO <= #{endDate}
AND PROJECT_CD = #{projectCd}
AND BUSN_NAME like concat('%', #{busnName}, '%')

/* getBustripReqCheck */ SELECT A.*, B.STATUS AS RES_STATUS, B.EXP_STAT FROM CAM_INSIDE.DJ_HR_BIZ_REQ A LEFT JOIN CAM_INSIDE.DJ_HR_BIZ_REQ_RESULT B ON A.HR_BIZ_REQ_ID = B.HR_BIZ_REQ_ID WHERE A.STATUS IN ('100') AND
TRIP_DAY_FR >= #{startDate} AND TRIP_DAY_TO <= #{endDate}
AND A.PROJECT_CD = #{projectCd}
AND A.BUSN_NAME like concat('%', #{busnName}, '%')

mapper.update:
/* bustrip updateApprStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}

/* bustrip updateApprFinalStat */
UPDATE
    CAM_INSIDE.DJ_HR_BIZ_REQ
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    HR_BIZ_REQ_ID = #{hrBizReqId}
