mapper.select:
/* getCertificateList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, IUP.USER_PROOF_SN, DATE_FORMAT(IUP.REG_DE, '%Y-%m-%d') AS REG_DE, IUP.PROOF_TYPE, IUP.REG_DEPT_NAME, IUP.REGTR_NAME, DATE_FORMAT(IUP.SUBMISSION_DE, '%Y-%m-%d') AS SUBMISSION_DE, IUP.USAGE_NAME, IUP.STATUS, DATE_FORMAT(IUP.APPROVAL_RESULT_DATE, '%Y-%m-%d') AS APPROVAL_RESULT_DATE, HEI.EMP_NAME_KR AS APPROVAL_EMP_NAME, IUP.NUMBER, IUP.DOCU_YEAR_DE, IUP.REG_DUTY_NAME, IUP.ISSUE, CONCAT((SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = DI.DEPT_SEQ)), ' ', (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = DI.DEPT_SEQ)) AS DEPT_FULL_NAME FROM CAM_INSIDE.DJ_USER_PROOF IUP LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON IUP.APPROVAL_EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO HEIR ON IUP.CRTR_SN = HEIR.EMP_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO DI ON HEIR.DEPT_SEQ = DI.DEPT_SEQ, (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND IUP.CRTR_SN = #{empSeq}
AND DATE_FORMAT(CREATE_DT, '%Y') = #{docuYearDe}
AND PROOF_TYPE = #{proofType}
AND USAGE_NAME = #{purpose}
AND STATUS = #{status}
AND STATUS IN (10, 30, 100, 110)
AND (REGTR_NAME like CONCAT('%', COALESCE(#{searchText}, ''), '%') OR REG_DUTY_NAME like CONCAT('%', COALESCE(#{searchText}, ''), '%'))
AND REGTR_NAME like CONCAT('%', #{searchText}, '%')
AND REG_DUTY_NAME like CONCAT('%', #{searchText}, '%')
AND IUP.ACTIVE = 'Y' ORDER BY IUP.REG_DE ) RE ORDER BY RE.ROW_NUM DESC

mapper.update:
/* updateCertifiApprStat */
UPDATE
    CAM_INSIDE.DJ_USER_PROOF
SET

        DOC_ID = #{docId},

    STATUS = ${status},
    APPROVAL_SEND_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    USER_PROOF_SN = #{userProofSn}

/* certificate.setReqCert */
UPDATE
CAM_INSIDE.DJ_USER_PROOF
SET

    APPROVAL_EMP_SEQ = #{empSeq},
    APPROVAL_RESULT_DATE = now(),

STATUS = ${status}
WHERE
USER_PROOF_SN = #{userProofSn}
