mapper.select:
/* getCarRequestList */ SELECT IHCR.CAR_REQ_SN, CASE WHEN IHCR.CAR_CLASS_TEXT = '기타' THEN CONCAT('[',IHCR.CAR_CLASS_TEXT,'(',IHCR.CAR_CLASS_RMK,')]',IHCR.START_TIME,'~',IHCR.END_TIME) ELSE CONCAT('[',IHCR.CAR_CLASS_TEXT,'] ',IHCR.START_TIME,'~',IHCR.END_TIME) END AS title, DATE_FORMAT(IHCR.START_DT, '%Y-%m-%d') AS startDate, DATE_FORMAT(IHCR.END_DT, '%Y-%m-%d') AS endDate, CONCAT(IHCR.START_DT, ' ',IHCR.START_TIME) AS start, CONCAT(IHCR.END_DT, ' ',IHCR.END_TIME) AS end FROM CAM_INSIDE.DJ_HR_CAR_REQ IHCR where 1=1
AND DATE_FORMAT(IHCR.START_DT, '%Y-%m') = #{carReqDt}
AND IHCR.CAR_CLASS_SN = #{carClass}
AND IHCR.CAR_TYPE_SN = #{carType}
AND IHCR.VISIT_NAME like CONCAT('%', #{enterSearch}, '%')
AND IHCR.WAY_POINT_NAME like CONCAT('%', #{enterSearch}, '%')
AND IHCR.EMP_NAME like CONCAT('%', #{enterSearch}, '%')
AND ( IHCR.VISIT_NAME like CONCAT('%', #{enterSearch}, '%') OR IHCR.WAY_POINT_NAME like CONCAT('%', #{enterSearch}, '%') OR IHCR.EMP_NAME like CONCAT('%', #{enterSearch}, '%') )

/* searchDuplicateCar */
SELECT IHCR.EMP_NAME, IHCR.EMP_SEQ FROM CAM_INSIDE.DJ_HR_CAR_REQ IHCR WHERE CONCAT(REPLACE(IHCR.START_DT, '-', ''), REPLACE(IHCR.START_TIME, ':', '')) < CONCAT(REPLACE(#{endDt}, '-', ''), REPLACE(#{endTime}, ':', '')) AND CONCAT(REPLACE(IHCR.END_DT, '-', ''), REPLACE(IHCR.END_TIME, ':', '')) > CONCAT(REPLACE(#{startDt}, '-', ''), REPLACE(#{startTime}, ':', '')) AND CAR_CLASS_SN = #{carClassSn}
AND CAR_REQ_SN = #{carReqSn}

/* getRoomRequestList */ SELECT IRR.ROOM_REQ_SN, CONCAT('[',IRR.ROOM_CLASS_TEXT,']') AS schTitle, CONCAT(IRR.START_TIME, ' ~ ', IRR.END_TIME) AS USE_TIME, CONCAT(IRR.START_DT, ' ',IRR.START_TIME) AS START_DATE, CONCAT(IRR.END_DT, ' ',IRR.END_TIME) AS END_DATE, IRR.* FROM CAM_INSIDE.DJ_ROOM_REQ IRR where 1=1
AND DATE_FORMAT(IRR.START_DT, '%Y-%m') = #{datePicker}
AND IRR.ROOM_CLASS_SN = #{meetingRoomDivision}
AND IRR.USE_PURPOSE_TEXT = #{usePurpose}
AND IRR.RENTAL_FEE_TEXT = #{rentalFee}
AND IRR.MANAGER_NAME like CONCAT('%', #{name}, '%')
AND IRR.REG_EMP_NAME like CONCAT('%', #{name}, '%')
AND IRR.REMARK_CN like CONCAT('%', #{name}, '%')
AND ( IRR.MANAGER_NAME like CONCAT('%', #{name}, '%') OR IRR.REG_EMP_NAME like CONCAT('%', #{name}, '%') OR IRR.REMARK_CN like CONCAT('%', #{name}, '%') )

/* searchDuplicateRoom */

    SELECT
        IRR.MANAGER_NAME AS EMP_NAME
    FROM
        CAM_INSIDE.DJ_ROOM_REQ IRR
    WHERE
        CONCAT(REPLACE(IRR.START_DT, '-', ''), REPLACE(IRR.START_TIME, ':', '')) < CONCAT(REPLACE(#{endDt}, '-', ''), REPLACE(#{endTime}, ':', ''))
    AND CONCAT(REPLACE(IRR.END_DT, '-', ''), REPLACE(IRR.END_TIME, ':', '')) > CONCAT(REPLACE(#{startDt}, '-', ''), REPLACE(#{startTime}, ':', ''))
    AND ROOM_CLASS_SN = #{roomClassSn}


    AND !ROOM_REQ_SN = #{roomReqSn}

mapper.delete:
/* setCarRequestDelete */
DELETE FROM
    CAM_INSIDE.DJ_HR_CAR_REQ
WHERE
    CAR_REQ_SN = #{carReqSn}

mapper.update:
/* setCarCodeUpdate */ UPDATE CAM_INSIDE.DJ_HR_CAR_CODE SET ACTIVE = #{active}, CAR_CLASS_NAME = #{carClassName}, CAR_NUM_NAME = #{carNum}, CAR_USE_DEPT_ACTIVE = #{useDeptType},
CAR_USE_DEPT_SN = #{carUseDeptSn}, CAR_USE_DEPT_NAME = #{carUseDeptName},
MANAGER_SN = #{empSeq}, MANAGER_NAME = #{empName}, REG_DT = #{regDt}, REMARK_CN = #{remarkCn} WHERE CAR_CODE_SN = #{carCodeSn}

/* setRoomRequestUpdate */ UPDATE CAM_INSIDE.DJ_ROOM_REQ SET START_DT = #{startDt}, END_DT = #{endDt}, START_TIME = #{startTime}, END_TIME = #{endTime}, ROOM_CLASS_SN = #{roomClassSn}, ROOM_CLASS_TEXT = (SELECT ROOM_NAME FROM CAM_INSIDE.DJ_ROOM_CODE WHERE ROOM_SN = #{roomClassSn}), USE_PURPOSE_SN = #{usePurposeSn}, USE_PURPOSE_TEXT = #{usePurposeText},
ETC = #{etc},
RENTAL_FEE_SN = #{rentalFeeSn}, RENTAL_FEE_TEXT = #{rentalFeeText},
PAY = #{pay},
MANAGER_SN = #{empSeq}, MANAGER_NAME = #{empName}, REMARK_CN = #{remarkCn} WHERE ROOM_REQ_SN = #{roomReqSn}

/* updateCarApprStat */
UPDATE CAM_INSIDE.DJ_HR_CAR_REQ
SET
    STATUS = #{approveStatCode},
    DOC_ID = #{docId}
WHERE
    CAR_REQ_SN = #{carReqSn}

/* updateCarFinalApprStat */
UPDATE CAM_INSIDE.DJ_HR_CAR_REQ
SET
    STATUS = #{approveStatCode},
    APPROVAL_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq}
WHERE
    CAR_REQ_SN = #{carReqSn}

mapper.select:
SELECT COUNT(*) AS cnt FROM CAM_INSIDE.DJ_HR_CAR_REQ WHERE CAR_CLASS_SN = #{carClassSn}
AND ((START_DT = #{startDt} AND #{startTime} BETWEEN START_TIME AND END_TIME) OR (END_DT = #{endDt} AND #{endTime} BETWEEN START_TIME AND END_TIME))
AND #{startDt} BETWEEN START_DT AND END_DT
