mapper.select:
/* getDocumentList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, CCD.*, DATE_FORMAT(CCD.EFFECTIVE_DT, '%Y-%m-%d') AS EFFECTIVE_DATE, DATE_FORMAT(CCD.SHIPMENT_DT, '%Y-%m-%d') AS SHIPMENT_DATE FROM CAM_COMMON.DJ_CAMTIC_DOCUMENT CCD, (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND CCD.DOCU_TYPE = #{docuType}
AND CCD.DOCUMENT_PART_TYPE = #{documentPart}
AND CCD.DEPT_PART_TYPE = #{documentPart}
AND CCD.DOCUMENT_TITLE_NAME like CONCAT('%', #{searchText}, '%')
AND CONCAT(CCD.DOCUMENT_FIRST_NUMBER,'-',CCD.DOCUMENT_SECOND_NUMBER) like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(CCD.EFFECTIVE_DT, '%Y-%m-%d') like CONCAT('%', #{searchText}, '%')
AND CCD.RECEIVE_NAME like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(CCD.SHIPMENT_DT, '%Y-%m-%d') like CONCAT('%', #{searchText}, '%')
AND CCD.MANAGER_NAME like CONCAT('%', #{searchText}, '%')
AND CCD.REMARK_CN like CONCAT('%', #{searchText}, '%')
ORDER BY CCD.REG_DT ) RE ORDER BY RE.ROW_NUM DESC

/* getDocuOrderList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, IDO.* FROM CAM_INSIDE.DJ_DOCUMENT_ORDER IDO, (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND IDO.CLASS_SN = #{classType}
AND CONCAT(IDO.DOCU_YEAR_SN,'-',IDO.DOCU_NO) like CONCAT('%', #{searchText}, '%')
AND IDO.PROJECT_NAME like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(IDO.CO_NAME, '%Y-%m-%d') like CONCAT('%', #{searchText}, '%')
ORDER BY IDO.REG_DT ) RE ORDER BY RE.ROW_NUM DESC

/* getDocuContractList */ SELECT RE.*, DFI.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, IDC.* FROM CAM_INSIDE.DJ_DOCUMENT_CONTRACT IDC, (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND IDC.CLASS_SN = #{classType}
AND CONCAT(IDC.DOCU_YEAR_SN,'-',IDC.DOCU_NO) like CONCAT('%', #{searchText}, '%')
AND IDC.PROJECT_NAME like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(IDC.CO_NAME, '%Y-%m-%d') like CONCAT('%', #{searchText}, '%')
ORDER BY IDC.REG_DT ) RE LEFT JOIN CAM_COMMON.DJ_FILE_INFO DFI ON RE.FILE_SN = DFI.FILE_NO ORDER BY RE.ROW_NUM DESC

/* getSnackList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, ISI.*, CFI.file_org_name, CFI.file_ext, CFI.file_size, IFNULL(CFI.file_no, 0) AS file_no, CFI.file_path, CFI.file_uuid FROM CAM_INSIDE.DJ_SNACK_INFO ISI LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON ISI.SNACK_INFO_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'snack', (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND ISI.REG_EMP_SEQ = #{empSeq}
AND DATE_FORMAT(USE_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{startDt}, '%Y-%m-%d') AND DATE_FORMAT(#{endDt}, '%Y-%m-%d') ORDER BY ISI.REG_DT ) RE ORDER BY RE.ROW_NUM DESC

/* getSnackExcelList */ SELECT ISI.USE_DT, ISI.SNACK_TYPE, ISI.SNACK_TYPE_TEXT, IFNULL(CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END, "") AS REG_DEPT_NAME, IFNULL(CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_NAME ELSE "" END, "") AS REG_TEAM_NAME, IFNULL(HEI.EMP_NAME_KR, "") AS EMP_NAME, ISI.AMOUNT_SN FROM CAM_INSIDE.DJ_SNACK_INFO ISI LEFT JOIN CAM_INSIDE.DJ_SNACK_COMPANION ISC ON ISI.SNACK_INFO_SN = ISC.SNACK_INFO_SN LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON ISC.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE 1=1 AND DATE_FORMAT(USE_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{startDt}, '%Y-%m-%d') AND DATE_FORMAT(#{endDt}, '%Y-%m-%d')
AND SNACK_TYPE in (${searchTypeText})
ORDER BY REG_DEPT_NAME, REG_TEAM_NAME, SNACK_TYPE, USE_DT

