/* setDocuContractInsert */ INSERT INTO CAM_INSIDE.DJ_DOCUMENT_CONTRACT ( MAIN_CLASS_SN, MAIN_CLASS_NAME, CLASS_SN, CLASS_NAME, DOCU_YEAR_SN, DOCU_NO, DOCU_DE, PROJECT_NAME, PROJECT_MONEY, START_DE, END_DE, CO_SN, CO_NAME, REMARK_CN, REG_EMP_SEQ, REG_EMP_NAME, REG_DT,
PROJECT_NUMBER, REPRESENTATIVE, BUSINESS_NUMBER, ZIP_CODE, ADDR, ADDR_DETAIL
) VALUES ( #{mainClassSn}, #{mainClassName}, #{classSn}, #{className}, CONCAT(#{docuName}, RIGHT(DATE_FORMAT(NOW(), '%Y'), 2)), (SELECT IFNULL(MAX(SIDC.DOCU_NO), 0)+1 FROM CAM_INSIDE.DJ_DOCUMENT_CONTRACT SIDC WHERE DOCU_YEAR_SN = CONCAT(#{docuName}, RIGHT(DATE_FORMAT(NOW(), '%Y'), 2))), #{docuDe}, #{projectName}, #{projectMoney}, #{startDe}, #{endDe}, #{coSn}, #{coName}, #{remarkCn}, #{regEmpSeq}, #{regEmpName}, NOW(), #{projectNumber}, #{representative}, #{businessNumber}, #{zipCode}, #{addr}, #{addrDetail} )
SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_INSIDE' AND TABLE_NAME = 'DJ_DOCUMENT_CONTRACT'

