mapper.select:
/* getEmploymentContList */ SELECT EC.SALARY_CONTRACT_ID, EC.EMP_SEQ, EC.EMP_NAME, EC.DEPT_SEQ, EC.DEPT_NAME, EC.POSITION_NAME, EC.SALARY_CONTRACT_REQ_DT, EC.SEND_YN, EC.FLAG, (CASE WHEN EC.dept_seq = 1000 then EC.dept_name when DI.parent_dept_seq = 1000 then DI.dept_name ELSE DI2.dept_name end) AS parentDeptName FROM CAM_INSIDE.DJ_EMPLOYMENT_CONTRACT EC JOIN CAM_HR.DJ_EMP_INFO EI ON EI.EMP_SEQ = EC.EMP_SEQ JOIN CAM_HR.DJ_DEPT_INFO DI ON EC.DEPT_SEQ = DI.DEPT_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO DI2 ON DI.parent_dept_seq = DI2.DEPT_SEQ WHERE EC.ACTIVE = 'Y'
AND DATE_FORMAT(#{startDate}, '%Y%m%d') <= DATE_FORMAT(EC.SALARY_CONTRACT_REQ_DT, '%Y%m%d') AND DATE_FORMAT(#{endDate}, '%Y%m%d') >= DATE_FORMAT(EC.SALARY_CONTRACT_REQ_DT, '%Y%m%d')
AND (DI.PARENT_DEPT_SEQ = #{deptSeq} OR EC.DEPT_SEQ = #{deptSeq})
AND EC.DEPT_SEQ = #{team}
AND EC.FLAG = #{status}
AND EC.EMP_NAME LIKE CONCAT('%', #{empName}, '%')
AND EC.SEND_YN = #{sendYn}
AND EC.EMP_SEQ = #{empSeq}
AND EC.EMP_SEQ = 0
ORDER BY EC.SALARY_CONTRACT_REQ_DT DESC

