mapper.select:
/*getBusinessParticipationList*/ SELECT C.PJT_SN, C.PJT_NM, C.SBJ_CLASS, DATE_FORMAT(C.STR_DT, '%Y-%m-%d') AS PJT_STR_DE, DATE_FORMAT(C.END_DT, '%Y-%m-%d') AS PJT_END_DE, CASE WHEN C.BUSN_CLASS = 'R' THEN DATE_FORMAT(IFNULL((SELECT NOW_STR_DE FROM CAM_PJT_MNG.DJ_PJT_RND WHERE PJT_SN = C.PJT_SN AND C.YEAR_CLASS = 'M'), C.STR_DT), '%Y-%m-%d') WHEN C.BUSN_CLASS = 'S' THEN DATE_FORMAT(IFNULL((SELECT NOW_STR_DE FROM CAM_PJT_MNG.DJ_PJT_UNRND WHERE PJT_SN = C.PJT_SN AND C.YEAR_CLASS = 'M'), C.STR_DT), '%Y-%m-%d') ELSE DATE_FORMAT(C.STR_DT, '%Y-%m-%d') END AS PJT_STR_DE2, CASE WHEN C.BUSN_CLASS = 'R' THEN DATE_FORMAT(IFNULL((SELECT NOW_END_DE FROM CAM_PJT_MNG.DJ_PJT_RND WHERE PJT_SN = C.PJT_SN AND C.YEAR_CLASS = 'M'), C.END_DT), '%Y-%m-%d') WHEN C.BUSN_CLASS = 'S' THEN DATE_FORMAT(IFNULL((SELECT NOW_END_DE FROM CAM_PJT_MNG.DJ_PJT_UNRND WHERE PJT_SN = C.PJT_SN AND C.YEAR_CLASS = 'M'), C.END_DT), '%Y-%m-%d') ELSE DATE_FORMAT(C.END_DT, '%Y-%m-%d') END AS PJT_END_DE2, C.PM_EMP_SEQ, A.PART_EMP_SEQ, A.PART_EMP_NM, REPLACE((SELECT PATH_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT DEPT_SEQ FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = A.PART_EMP_SEQ)), '|', ' ') AS DEPT_NAME, A.PART_DET_STR_DT, A.PART_DET_END_DT, A.EMP_SAL, A.CHNG_SAL, A.MON_SAL, (SELECT LG_CD_NM FROM CAM_PJT_MNG.DJ_PJT_CD WHERE LG_CD = C.SBJ_DEP AND GRP_SN = 'SUP_DEP' LIMIT 1) AS SBJ_DEP_NM, DATE_FORMAT(C.END_DT, '%Y-%m-%d') AS PJT_END_DT, C.SBJ_STAT_YN, C.PJT_STEP, A.TOT_RATE, B.MNG_STAT, A.TOT_PAY_BUDG, A.TOT_ITEM_BUDG FROM CAM_PJT_MNG.DJ_PART_RATE_DET A LEFT JOIN CAM_PJT_MNG.DJ_PJT_PART_RATE_VER B ON A.PART_RATE_VER_SN = B.PART_RATE_VER_SN LEFT JOIN CAM_PJT_MNG.DJ_PROJECT C ON B.PJT_SN = C.PJT_SN WHERE (C.PJT_SN, B.PART_RATE_VER) IN (SELECT PJT_SN, MAX(PART_RATE_VER) AS PART_RATE_VER FROM CAM_PJT_MNG.DJ_PJT_PART_RATE_VER WHERE MNG_STAT != 'R' GROUP BY PJT_SN) AND C.PJT_STOP != 'Y'
AND C.BUSN_CLASS = #{busnClass}
AND C.PJT_NM LIKE CONCAT('%', #{pjtNm}, '%')
AND C.PJT_STEP NOT IN ('S3', 'R3', 'E6')
AND C.PJT_STEP IN ('S3', 'R3', 'E6')

/*getUserPartRateList*/ SELECT AA.EMP_SEQ, AA.EMP_NAME_KR, AA.POSITION_NAME, DATE_FORMAT(AA.JOIN_DAY, '%Y-%m-%d') AS JOIN_DAY, BB.* FROM CAM_HR.DJ_EMP_INFO AA LEFT JOIN (SELECT C.PJT_SN, A.PART_EMP_SEQ, A.CHNG_SAL, A.MON_SAL, A.EMP_SAL, A.PART_DET_STR_DT, A.PART_DET_END_DT, (SELECT LG_CD_NM FROM CAM_PJT_MNG.DJ_PJT_CD WHERE LG_CD = C.SBJ_DEP AND GRP_SN = 'SUP_DEP' LIMIT 1) AS SBJ_DEP_NM, B.MNG_STAT, C.PJT_NM, DATE_FORMAT(C.END_DT, '%Y-%m-%d') AS PJT_END_DT, C.PM_EMP_SEQ, C.SBJ_STAT_YN, C.PJT_STEP, C.PJT_STOP, A.TOT_RATE, A.PAY_TOTAL, REPLACE((SELECT PATH_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = (SELECT DEPT_SEQ FROM CAM_HR.DJ_EMP_INFO WHERE EMP_SEQ = A.PART_EMP_SEQ)), '|', ' ') AS DEPT_NAME FROM CAM_PJT_MNG.DJ_PART_RATE_DET A LEFT JOIN CAM_PJT_MNG.DJ_PJT_PART_RATE_VER B ON A.PART_RATE_VER_SN = B.PART_RATE_VER_SN LEFT JOIN CAM_PJT_MNG.DJ_PROJECT C ON B.PJT_SN = C.PJT_SN WHERE 1 = 1 AND #{bsYear} BETWEEN DATE_FORMAT(A.PART_DET_STR_DT, '%Y') AND DATE_FORMAT(A.PART_DET_END_DT, '%Y') AND (C.PJT_SN, B.PART_RATE_VER) IN ( SELECT PJT_SN, MAX(PART_RATE_VER) AS PART_RATE_VER FROM CAM_PJT_MNG.DJ_PJT_PART_RATE_VER WHERE MNG_STAT != 'R' GROUP BY PJT_SN )
AND B.JOIN_MEM_NM LIKE CONCAT('%', #{kindContent}, '%')
) BB ON AA.EMP_SEQ = BB.PART_EMP_SEQ WHERE BB.PJT_SN IS NOT NULL AND BB.PJT_STOP != 'Y'
AND AA.WORK_STATUS_CODE = #{status}
AND AA.DIVISION = '0'
AND AA.DIVISION = '4' AND AA.DIVISION_SUB = '1'
AND AA.DIVISION = '4' AND AA.DIVISION_SUB = '2'
AND ${userKind} LIKE CONCAT('%', #{kindContent}, '%')
AND AA.DEPT_SEQ = #{deptComp}
OR AA.DEPT_SEQ IN ( SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = #{deptComp} OR PARENT_DEPT_SEQ = #{deptComp} )
AND AA.DEPT_SEQ = #{deptTeam}
ORDER BY AA.EMP_NAME_KR

