mapper.select:
select DATE_FORMAT(deic.card_interview_date,'%Y-%m-%d') as card_interview_date, deic.EMP_SEQ, deic.card_interviewer, deic.card_number, deic.card_superior_person, deic.card_superior_person2, deic.card_status, car.dept_name, car.dept_team_name, car.emp_name_kr, car.dept_seq, car.team_seq from ( select CASE WHEN ddi.DEPT_LEVEL = 2 THEN (SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO DI WHERE ddi.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE a.DEPT_SEQ END AS dept_seq, CASE WHEN ddi.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE ddi.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE a.DEPT_NAME END AS dept_name, CASE WHEN ddi.DEPT_LEVEL = 2 THEN a.DEPT_SEQ ELSE "" END AS team_seq, -- CASE WHEN ddi.DEPT_LEVEL = 2 THEN a.DEPT_NAME ELSE "" END AS dept_team_name, a.emp_seq, a.dept_team_name, a.emp_name_kr from cam_hr.dj_emp_info as a join cam_hr.dj_dept_info ddi on a.DEPT_SEQ = ddi.dept_seq )as car join cam_inside.dj_employee_interview_card deic on car.emp_seq = deic.EMP_SEQ where 1=1
AND YEAR(deic.card_interview_date) = YEAR(STR_TO_DATE(#{searchDate}, '%Y'))
AND car.dept_seq = #{dept}
AND car.team_seq = #{team}
AND ( car.dept_name LIKE CONCAT('%', #{searchText}, '%') OR car.dept_team_name LIKE CONCAT('%', #{searchText}, '%') OR car.emp_name_kr LIKE CONCAT('%', #{searchText}, '%') OR deic.card_interviewer LIKE CONCAT('%', #{searchText}, '%') OR deic.card_number = #{searchText} )
