mapper.select:
/* getEvalItemMainList */ SELECT EIM.EVAL_ITEM_MAIN_ID, EIM.EVAL_MANAGE_TITLE, EIM.EVAL_ITEM_CNT, DATE_FORMAT(EIM.REG_DATE, '%Y-%m-%d') AS REG_DATE FROM CAM_INSIDE.DJ_EVAL_ITEM_MAIN EIM WHERE EIM.ACTIVE = 'Y'
AND EIM.EVAL_MANAGE_TITLE LIKE CONCAT('%', #{evalManageTitle}, '%')
ORDER BY EIM.REG_DATE DESC

/* getRecruitEvalSelSheet */
SELECT
    RECRUIT_EVAL_SHEET_ID,
    RECRUIT_INFO_SN,
    INTERVIEW_EVAL_SHEET,
    RECRUIT_AREA_INFO_SN
FROM
    CAM_INSIDE.DJ_RECRUIT_EVAL_SHEET
WHERE 1=1

    AND RECRUIT_EVAL_SHEET_ID = #{recruitEvalSheetId}


    AND RECRUIT_INFO_SN = #{recruitInfoSn}


    AND RECRUIT_AREA_INFO_SN =#{recruitAreaInfoSn}

mapper.insert:
/* setRecruitEvalSelSheet */
INSERT INTO CAM_INSIDE.DJ_RECRUIT_EVAL_SHEET
    (
        RECRUIT_INFO_SN,
        INTERVIEW_EVAL_SHEET,
        REG_EMP_SEQ,
        RECRUIT_AREA_INFO_SN
    )
VALUES
    (
        #{recruitInfoSn},
        #{interviewEvalSheet},
        #{empSeq}

        ,#{recruitAreaInfoSn}

    )


    SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_INSIDE' AND TABLE_NAME = 'DJ_RECRUIT_EVAL_SHEET'

mapper.update:
/* setRecruitEvalSelSheetUpd */
UPDATE
    CAM_INSIDE.DJ_RECRUIT_EVAL_SHEET
SET
    INTERVIEW_EVAL_SHEET = #{interviewEvalSheet},
    MOD_EMP_SEQ = #{empSeq}

    , RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}

WHERE
    RECRUIT_EVAL_SHEET_ID = #{recruitEvalSheetId}

mapper.select:
/* evalLoginChk */
SELECT
    *
FROM
    CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN
WHERE 1=1


        AND EMP_SEQ = #{uniqId}


        AND EMP_SEQ = #{evalEmpSeq}


AND
    RECRUIT_INFO_SN = #{recruitInfoSn}

AND
    RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}


AND
    EVAL_TYPE = #{evalType}


    GROUP BY EMP_SEQ

/* getApplicationScreenViewList */ SELECT AM.APPLICATION_ID, IT.IN_TIME, AM.USER_EMAIL, AM.RECRUIT_INFO_SN, AM.RECRUIT_AREA_INFO_SN, AM.CAREER_TYPE, AM.USER_NAME, ROUND((TO_DAYS(NOW()) - TO_DAYS(AM.BDAY)) / 365, 0) AS AGE, CASE WHEN AM.GENDER = 'M' THEN '남자' ELSE '여자' END AS GENDER, DAS.SCHOOL_NAME, GROUP_CONCAT(AC.WORK_ST_DT, '~', AC.WORK_EN_DT SEPARATOR '|') AS WORK_DATE, REPLACE(SUBSTRING_INDEX(REPLACE(AM.ADDR, ' ', '_'), '_', 2), '_', ' ') AS ADDR, GROUP_CONCAT(LA.LANG_NAME SEPARATOR ', ') AS LANG_NAME, AI.JOB, CASE WHEN INSTR(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM') > 0 THEN REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM', '오후') ELSE REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'AM', '오전') END AS SAVE_DATE, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN WHERE SAVE_TYPE = 'S' AND AM.RECRUIT_INFO_SN != RECRUIT_INFO_SN AND AM.APPLICATION_ID != APPLICATION_ID AND AM.USER_EMAIL = USER_EMAIL) AS DUPLICATION_CNT, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 1) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'otherRmk' AND EVAL_SCREEN_TYPE = 'doc' GROUP BY APPLICATION_ID ) AS DOC_SCREEN_AVERAGE, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 1) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A LEFT JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'opinion' AND EVAL_SCREEN_TYPE = 'in' GROUP BY APPLICATION_ID ) AS IN_SCREEN_AVERAGE, CASE WHEN AM.IN_AVOID = 'Y' THEN '불참' ELSE '' END IN_AVOID, AM.PRELIMINARY_PASS FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM JOIN ( SELECT DAS.APPLICATION_ID, MAX(SCHOOL_TYPE) AS SCHOOL_TYPE, CONCAT( SCHOOL_NAME, ' ', DEPT, '(', CASE WHEN MAX(SCHOOL_TYPE) = '1' THEN '고등학교' WHEN MAX(SCHOOL_TYPE) = '2' THEN '전문대학' WHEN MAX(SCHOOL_TYPE) = '3' OR MAX(SCHOOL_TYPE) = '4' THEN '대학교' WHEN MAX(SCHOOL_TYPE) = '5' THEN '석사' WHEN MAX(SCHOOL_TYPE) = '6' THEN '박사' END, CASE WHEN GRADUATE_TYPE = '1' THEN '' WHEN GRADUATE_TYPE = '2' THEN ' 졸업예정' WHEN GRADUATE_TYPE = '2' THEN ' 수료' END, ')' ) AS SCHOOL_NAME FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL DAS GROUP BY DAS.APPLICATION_ID )DAS ON AM.APPLICATION_ID = DAS.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_LANG_ABILITY LA ON AM.APPLICATION_ID = LA.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_CAREER AC ON AM.APPLICATION_ID = AC.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_IN_TIME IT ON AM.APPLICATION_ID = IT.APPLICATION_ID JOIN CAM_INSIDE.DJ_RECRUIT_AREA_INFO AI ON AM.RECRUIT_AREA_INFO_SN = AI.RECRUIT_AREA_INFO_SN WHERE AM.RECRUIT_INFO_SN = #{recruitInfoSn} AND AM.SAVE_TYPE = 'S'
AND AM.APPLICATION_STAT = #{searchType}
AND AM.APPLICATION_STAT != #{notSearchType}
AND AM.APPLICATION_STAT NOT IN (${notSearchTypeArr})
AND AM.RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn} GROUP BY AM.APPLICATION_ID ORDER BY AM.USER_NAME

mapper.delete:
/*delLoginChk*/
DELETE
FROM
    CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN
WHERE
    1 = 1
AND
    RECRUIT_INFO_SN = #{recruitInfoSn}
AND
    EVAL_TYPE = #{evalType}
